CREATE TABLE statistic
(
  id INT NOT NULL AUTO_INCREMENT,
  number INT NOT NULL,
  date DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP,
  PRIMARY KEY (id)
);
