CREATE TABLE PARTY
(
  id INT NOT NULL AUTO_INCREMENT,
  name varchar(200) NOT NULL,
  PRIMARY KEY (id)
);

CREATE UNIQUE INDEX PARTY_NAME_INDEX ON PARTY (name);

CREATE TABLE PARTY_SONGS
(
  song_id INT NOT NULL,
  party_id INT NOT NULL,
  position INT NOT NULL,
  CONSTRAINT PARTY_SONGS_SONG_ID_FK FOREIGN KEY (song_id) REFERENCES SONG (ID) ON DELETE CASCADE,
  CONSTRAINT PARTY_SONGS_GROUP_ID_FK FOREIGN KEY (party_id) REFERENCES PARTY (ID) ON DELETE CASCADE
);
