INSERT INTO `SONG` (`ID`, `DESCRIPTION`, `TEXT`) VALUES
  (1, 'Вся молодежь Казахстана', 'Вся молодежь Казахстана, Вставай со Xристом в пролом.\r\nВыше держи свое знамя, Смелее  вперед мы пойдем!\r\nСреди бесконечных просторов Лети наша песнь о Творце.\r\nВ больших городах и селах Услышат пусть все о Христе!\r\n\r\nПрипев:\r\nЛишь только со Христом Достигним цели этой,\r\nЧтоб слышали о Боге Жители планеты.\r\nЛишь только для Христа Служить сегодня будем,\r\nЧтоб к светлым небесам Стремились люди!\r\n\r\nБез Бога мы просто земляне По жизни идем бесцельно,\r\nИ на душе нашей камень, Закованы ноги в цепи.\r\nНо лишь со Христом мы свободны, И дышится легче теперь.\r\nГосподь! Благодатью наполни! Взывают все дети к Тебе.\r\n\r\nВ единстве и в мудрости Божьей, сокрыта великая сила.\r\nОна нам идти поможет, Когда на пути обессилем.\r\nВставай молодежь Казахстана, Нас дело великое ждет!\r\nХристова любовь наше знамя, Она нас вперед поведет!\r\n'),
  (2, 'Иисус, Ты очень нужен мне', 'Иисус, Ты очень нужен мне, Нет Тебя-и исчезает радость.\r\n//Я согласен крова не иметь, Только б Ты был постоянно рядом.//\r\n\r\nИисус, Ты мне необходим, Извлекаю из скорбей уроки.\r\n//Без Тебя немыслимо ходить По не ровным жизненным дорогам.//\r\n\r\nВ мире одиноко и темно, Терпит бедствие земная дружба.\r\n//Оставайся, Иисус, со мной, Ты мне нужен, очень-очень нужен.//\r\n'),
  (3, 'Мы семья, мы одна семья ', 'Мы семья, мы одна семья, Иисус наш Господь.\r\nИ я так рад, что Он собрал нас вместе,\r\nИ я так рад, что Отец наш Вечный Бог!\r\n\r\nТы и я, мы одна семья,\r\nТы и я, брат с сестрой,\r\nИ я так рад, что наш Отец Небесный\r\nДарует нам и радость, и покой.\r\n\r\nМы с тобой Кровию одной\r\nОт греха спасены.\r\nИ я так рад, что со Христом все вместе\r\nВ одной семье теперь едины мы.\r\n\r\nМы семья. мы одна семья,\r\nИисус наш Господь.\r\nИ я так рад, что Он собрал нас вместе,\r\nИ я так рад, что Отец наш Вечный Бог!\r\n'),
  (4, 'Благослови меня Господь', 'Благослови меня Господь,прошу благослови.\r\nДушою нищей я иду,к Тебе искать любви.\r\nСклоняю низко пред Тобой повинную главу,\r\nМолю оставь меня с Собой,наполни жизнь мою.\r\n\r\nПрипев:\r\n//Без Бога не можем мы жить,\r\nБез Бога не можем любить,\r\nБез Бога мертва вся земля,\r\nБез Бога мертвы ты и я...// 2 р.\r\n\r\nТы знаешь Сам,как горд я был,как многим я владел,\r\nКак о Тебе я позабыл и как я оскудел,\r\nТеперь прошу лишь об одном : \" Возьми меня слугой,\r\nХочу я в доме быть Твоём ,где радость и покой \"\r\n'),
  (5, 'Господь я Тебе благодарен', 'Господь я Тебе благодарен\r\nЗа вечный Божественный свет,\r\nДо смерти я Тебя буду славить,\r\nПрими же теперь мой обет.\r\n\r\nПрипев:\r\nПусть я бессилен и устал, Пусть на пути не устоял,\r\nНо ведь души моей мечты Знаешь лишь Ты\r\n\r\nГлаза мои полны печали,\r\nИ сердце так сильно болит,\r\nЛишь Ты мои слышишь желанья,\r\nИ радость лишь Ты мне даришь.\r\n\r\nМолитвы к Тебе посылаю,\r\nИ верю: Ты слышишь Меня,\r\nЯ сердце к Тебе направляю,\r\nО, Боже, прости же меня\r\n'),
  (6, 'Твёрдо я верю: мой Иисус!', 'Твёрдо я верю: мой Иисус!\r\nИм я утешен и Им веселюсь;\r\nНеба наследье хочет Он дать,\r\nКак же приятно Им обладать!\r\n\r\nПрипев:\r\nВечно я буду петь с торжеством\r\nОб Иисусе дивном моём.\r\nВечно я буду петь с торжеством\r\nОб Иисусе дивном моём.\r\n\r\nТвёрдо я верю: с часа того,\r\nКак я отдался, дитя я Его.\r\nМир наполняет сердце моё,\r\nВ Нем нахожу я хлеб и питьё.\r\n\r\nТвердо я верю: сильной рукой\r\nОн простирает Свой кров надо мной.\r\nЧтоб ни случилось, радостен дух:\r\nВечно со мною Пастырь и Друг.\r\n\r\nЧудный и полный мир и покой\r\nДух мой находит в союзе с Тобой;\r\nДай, чтоб Тебе я сердце отдал;\r\nЯ б умалялся, Ты б возрастал.\r\n'),
  (7, 'Манит сердце мечтою', 'Манит сердце мечтою Высь небес голубая,\r\nНашей общей мечтою Стала вера живая.\r\n\r\nПрипев:\r\nОн нас сделал родными,\r\nОн нас сделал друзьями,\r\nДал нам новое имя:\r\nНас зовут христиане.\r\n\r\nИисус стал нам Другом, Мы на деле узнали,\r\nОн врачует недуги, Удаляет печали.\r\n\r\nОн дарует прощенье, Мир и радость без меры\r\nИ второе рожденье, Только искренне веруй.\r\n'),
  (8, 'Я у Господа в долгу поныне', 'Я у Господа в долгу поныне:\r\nКаждый день живу Его даяньем,\r\nЖизнь моя в единородном Сыне,\r\nЗа меня Он перенес страданья.\r\n\r\nПрипев:\r\nВелики любовь Его и милость,\r\nСколько благ дает Он ежедневно!\r\nБлагодать Его не прекратилась,\r\nОн - даятель каждое мгновенье.\r\nКак не быть мне благодарным Богу?!\r\nЕго имя славно и чудесно.\r\nЯ возьму с собой в мою дорогу\r\nЭту нескончаемую песню.\r\n\r\nСколько дней протянется погода,\r\nНам никто не вычислит, не скажет,\r\nА пока нам Бог дает свободу\r\nИ победу над коварством вражьим.\r\n\r\nХорошо, что Бог спасать умеет,\r\nИ таких, каким я был - калека,\r\nОн Отец, Он любит и жалеет\r\nКаждого живаго человека.\r\n\r\nЕсть у нас у каждого потери,\r\nПусть они нам жизнь не омрачают,\r\nПосмотрите люди, даже звери\r\nНа любовь любовью отвечают.\r\n'),
  (9, 'Если пришла беда кто нам', 'Если пришла беда кто нам, скажите, поможет\r\nНо через все года видел я руку Божью.\r\n\r\nПрипев:\r\nМир не поймёт тебя сердце больнее ранит.\r\n//Только Господь, любя Руки к тебе протянет.//\r\n\r\nВылей всю душу Ему а горести и страданьях\r\nДаже кромешную тьму Он превратит в сиянье.\r\n\r\nВстань на колени, друг если скорбей так много\r\nИ ты познаешь суть счастья в общеньи с Богом.\r\n'),
  (10, 'Бога я вижу в озерах синих', 'Бога я вижу в озерах синих,\r\nБога я вижу в порывах ветра,\r\nОн зажигает души светильник,\r\nПроникновенно лучистым светом.\r\n\r\nПрипев:\r\nОн очевиден в улыбке солнца,\r\nОн слышен в шуме дождя и снега,\r\nЯ как сотрудник Его посольства\r\nПровозглашаю программу неба.\r\n\r\nВолю благую, Господню знаю,\r\nОн дал мне царственное гражданство\r\nПровоглашаю любовь, как знамя\r\nБлагословенного государства.\r\n\r\nЗдесь на земле для меня лишь область,\r\nЛишь только область родной отчизны\r\nЯ верю в Бога и в Божий образ,\r\nПреображаюсь в духовной жизни.\r\n\r\nЯ верю в Бога, и в Боге дивном\r\nВновь становлюсь возрастая в вере.\r\nЯ вижу Бога на небе синем,\r\nЯ слышу Бога в поющем ветре.\r\n'),
  (11, 'Господь, Ты мой свет', 'Припев:\r\nГосподь, Ты мой свет и спасенье мое.\r\nКого же мне бояться,\r\nКого же мне страшиться?\r\n\r\nДаже если все злые люди на земле\r\nвстанут против меня, то и тогда, то и тогда\r\nя буду надеяться.\r\n\r\nБоже, я прошу, только одного ищу,\r\nжить в доме Твоем, каждый свой день,\r\nкаждый свой час жить в доме Твоем.\r\n\r\nНаучи, Господь, чтобы каждый новый день\r\nбыл спасеньем моим, в светлые дни,\r\nв смутные дни я буду надеяться.\r\n'),
  (12, 'Господь, я так люблю Тебя', 'Господь, я так люблю Тебя, Иисус\r\nТы спас меня от дьявольских уз.\r\nИзбрал меня из тьмы в Свой чудный свет\r\nИ заключил завет.\r\n\r\nДивный наш Бог, драгоценною Кровию\r\nАгнца омой нас от наших грехов\r\nСердце наполни Твоею любовию\r\nДай наслаждаться Тобой вновь и вновь.\r\n\r\nГосподь, я знаю Ты велешь меня\r\nТуда, где вечно буду петь я.\r\nПрошу Тебя, не оставляй меня,\r\nВедь я люблю Тебя.\r\nДивный наш Бог, Ты даруешь прощение.\r\nЯ упаду-Ты поднимешь любя.\r\nСердце наполнишь Своею любовию,\r\nБлагословишь и поддержишь меня.\r\n'),
  (13, 'Золотой Иерусали', 'Золотой Иерусалим, золотой Иерусалим\r\nЗолотой Иерусалим обещал нам Бог.\r\nЕсли Бога не забыть, если ближнего любить\r\nЗолотой Иерусалим обещал нам Бог.\r\n\r\nЗолотой Иерусалим, Золотой Иерусалим\r\nТам, как голуби просты, мы станем вдруг.\r\nИ мы сядем в Отчий круг, и раздастся песен звук\r\nИ не будет больше мук и не будет мук.\r\n\r\nЗолотой Иерусалим, Золотой Иерусалим\r\nЗолотой Иерусалим обещал нам Бог.\r\nВесь украшен и святой, песню там поет немой\r\nЗолотой Иерусалим обещал нам Бог.\r\n'),
  (14, 'Боже, я Тебе желаю верить', 'Боже, я Тебе желаю верить, даже если все вокруг устанут,\r\nДаже если все врагами станут, даже если боли не измерить.\r\n\r\nБоже, я с Тобой хочу встречаться, даже если нет гостиных пышных,\r\nДаже если нету денег лишних и с постели не могу подняться.\r\n\r\nПрипев:\r\nТы мой Друг, моя надежда.\r\nИ один лишь Ты как прежде боль души моей увидишь,\r\nНе изменишь, не покинешь. Не изменишь, не покинешь.\r\n\r\nТы всегда поймешь, всегда поддержишь, мой осветишь путь среди тумана.\r\nЛюбишь Ты как мать, как доктор лечишь, и прощаешь, как это ни странно.\r\n\r\nБоже я с Тобой желаю встречи в небесах сияющих лазурных.\r\nИ везде среди обычных будней, Если рядом Ты-мнелегче\r\n\r\nПрипев:\r\nТы мой Друг, моя надежда.\r\nИ один лишь Ты как прежде боль души моей увидишь,\r\nНе изменишь, не покинешь. Не изменишь, не покинешь.\r\nТы-мой Друг, моя дорога,\r\nИ Ты встретишь у порога, У порога в дом небесный,\r\nВ мир мечты моей чудесной. В мир мечты моей чудесной.\r\n'),
  (15, 'Все для тебя Высшим', 'Все для тебя Высшим Разумом создано,\r\nЭта вселенная, дом твой, земля.\r\n//Будущий рай, настоящее, прошлое,\r\nВсе для тебя, все для тебя.//\r\n\r\nЭти поля, эти яркие звездочки,\r\nСолнечный луч и журчанье ручья.\r\n//Тучки весенние, ливни осенние,\r\nВсе для тебя, все для тебя.//\r\n\r\nПесни венчальные, звуки прощальные,\r\nНочи бесцветные и красочность дня,\r\n//В мире заброшенном все, что хорошее,\r\nВсе для тебя, все для тебя.//\r\n\r\nБожья любовь для тебя, друг, проявлена\r\nВ жертвенной крови Иисуса Христа.\r\n//Радость спасения, с Богом общение -\r\nВсе для тебя, все для тебя.//\r\n'),
  (16, 'Ясли и Крест - чудная весть!', 'Ясли и Крест - чудная весть!\r\nВсе унесли года.\r\nТихая ночь, дивная ночь\r\nНочь Рождества пришла!\r\n\r\nПрипев:\r\nПусть же всегда в памяти моей\r\nНочь Рождества светит сильней!\r\nВечно живой Иисус в ту ночь\r\nК людям пришел, чтобы помочь\r\n\r\nНам молодым дорог Иисус\r\nВедь спас Он нас от уз.\r\nОн был рожден, чтоб я и ты\r\nВечную жизнь нашли.\r\n\r\nЛюди земли! Помните о том,\r\nЧто Иисус нас спас\r\nСчастье дарит, даром дарит\r\nЯрко звезда горит!\r\n'),
  (17, 'День воскресный, чудный гость', 'День воскресный, чудный гость небесный,\r\nРадостно приветствуем тебя.\r\nТы даруешь нам свой мир чудесный,\r\nСладкий отдых после дней труда.\r\nНо еще великий день мы ждем,\r\nКогда в вечный мы покой войдем.\r\n\r\nТот войдет туда, кто здесь всецело\r\nЖизнь свою для Бога посвятил,\r\nДо конца кто устоял и смело\r\nШел вперед и в битве победил.\r\nТот небесный отдых обретет,\r\nВ бесконечный он покой войдет.\r\n\r\nО небесная страна святая!\r\nНет в тебе земных скорбей и слез,\r\nБесконечный мир, блаженство рая\r\nИ, как солнце, там всегда Христос.\r\nО Господь, веди и нас туда,\r\nГде наш вечный отдых от труда.\r\n'),
  (18, 'Жертвой Иисуса Христа', 'Жертвой Иисуса Христа я спасён от Огня\r\nРанами Иисуса Христа я исцелён, нет сомнения\r\nКровью Иисуса Христа я омыт от греха\r\nЧерез смерть Иисуса Христа мне дана жизнь вечная\r\n\r\nБлагослови душа моя Господа(Господа)\r\nИ вся внутренность моя святое имя Его\r\nБлагослови душа моя Господа(Господа )\r\nИ не забывай всех благодеяний Его\r\n\r\nЯ верю в голгофскую жертву, кровь Святую Твою,\r\nЖизнь вечную В То, что имя моё записанно на небесах,\r\nНа небесах, на небесах, на небесах!\r\n'),
  (19, 'Душа томится в клетке', 'Душа томится в клетке день ото дня, Господь прости и помилуй меня.\r\nНе знаю, что там ждет за той горой и как быть, как дальше идти.\r\nСердце открыто ищет Твои пути, прошу, утешь, обнови мои дни.\r\nЯ пред Тобою стою такой как есть и все мысли известны Тебе\r\n\r\nПрипев:\r\nЭммануил, Эммануил - Ты есть скала моего спасенья,\r\nмоя любовь и мое воскресенье, Эммануил, Эммануил.\r\nОмой меня Ты живою водою, и поддержи Своею рукою,\r\nЭммануил, Эммануил.\r\n\r\nТы даёшь силу жизни в Духе Святом,  я на коленях в Доме Твоём\r\nИ в тишине и в смиренье я в молитве с Тобой обо всём говорю.\r\nНадежду, радость я в Тебе нахожу Ты есть Мессия - Тебя я жду,\r\nИмя Твоё говорит, что с нами Бог и навек мы будем с Тобой.\r\n'),
  (20, 'Иисус превозносим', 'Иисус превозносим,\r\nТвоё имя Ты Царь,\r\nПребывающий среди нас,\r\nИ наполняющий сердца!\r\n\r\nПрипев:\r\nМы поклоняемся Тебе Господь!\r\nТы – Бог на небе и на земле,\r\nТы – Бог на небе и на земле,\r\nТы – Царь мира во веки веков!\r\n\r\nИисус, Ты достоин\r\nВечной славы от нас!\r\nВозвещать буду всякий день\r\nВеликолепие Твоё!\r\n'),
  (21, 'Иисус превозносим, Твоё', 'Иисус превозносим, Твоё имя Ты Царь,\r\nПребывающий среди нас, И наполняющий сердца!\r\n\r\nПрипев:\r\nМы поклоняемся Тебе Господь!\r\nТы – Бог на небе и на земле,\r\nТы – Бог на небе и на земле,\r\nТы – Царь мира во веки веков!\r\n\r\nИисус, Ты достоин Вечной славы от нас!\r\nВозвещать буду всякий день Великолепие Твоё!\r\n'),
  (22, 'И вот промчался еще день', 'И вот промчался еще день, такой же, как и все,\r\nВсе те же будни, та же грусть, когда Христа в нем нет...\r\nИ лишь с Тобой всегда тепло: и в дождь, и в град, и в снег,\r\nМне дышится всегда легко, когда Ты рядом есть.\r\n\r\nПрипев:\r\nИисус, о Иисус! Тебя увижу я тогда...\r\nИисус, о Иисус! С Тобою быть хочу всегда.\r\n\r\nМне не забыть Твоей любви, Твоих пронзенных рук,\r\nЧто подарили мне они спасение от мук.\r\nТой встречи день настанет вновь и там увижу я\r\nТебя, Христос, моя любовь, что вел меня, храня...\r\n\r\nПришел к Тебе когда-то я. и грусти не скрывал,\r\nСказал: \"Христос, прости меня\", и Ты мне руку дал.\r\nИ лишь с Тобой всегда тепло и в дождь, и в град, и в снег,\r\nИ на душе всегда легко, когда Ты рядом есть\r\n'),
  (23, 'Ты не один идёшь по жизни', 'Ты не один идёшь по жизни.\r\nВ мире людей никто не лишний,\r\nВсе мы в одной семье у Бога,\r\nВсе мы с тобой, и нас так много.\r\n\r\nПрипев:\r\nТы не один, всегда Бог рядом!\r\nТы не один, грустить не надо!\r\nТы не один, пойдем со мною\r\nВ страну чудес, в страну святую!\r\n\r\nЕсли порою одиноко,\r\nТучи скорбей—и их так много,\r\nЗнай, что всегда за облаками\r\nСолнце любви горит над нами.\r\n\r\nБожья любовь нас согревает,\r\nВ трудные дни Бог утешает.\r\nВ Его семье никто не лишний!\r\nС Богом иди всегда по жизни.\r\n'),
  (24, 'Иисус - спасенье наше', 'Иисус - спасенье наше, мир, свобода,  радость, свет.\r\nИ Его светлей и краше никого на свете нет.\r\n\r\nПрипев:\r\n//Иисус-Любовь святая, отчего идем к Нему!\r\nНа земле и в царстве рая нет подобного Ему.//\r\n\r\nЭто имя- дорогое, солнце милости для всех,\r\nНаше знамя боевое, побеждающее грех!\r\n\r\nЭто имя- жизнь без меры в светлых милости лучах.\r\nЭто имя- сила веры, побеждающая страх.\r\n'),
  (25, 'Иисус, Ты Царь царей', 'Иисус, Ты Царь царей и Господь наш, Ты царишь над всей землей;\r\nПобедил врагов Своих на Голгофе и воссел на троне Ты.\r\n\r\nПрипев:\r\nДа, Ты царь наш, Царь царей, Спаситель мира,\r\nЦарь наш! Мы славим Твоё Имя, Иисус!\r\n\r\nБудь прославлен Ты в веках, Боже сильный, Твой престол на небесах!\r\nТвое царство обладает вселенной, держишь все в Своих руках!\r\n\r\nНет числа Твоим делам, Боже мудрый, нет числа и чудесам.\r\nПред величием Твоим прекланяюсь, жизнь кладеи к Твоим ногам.\r\n'),
  (26, 'Как земля без дождя в знойное лето', 'Как земля без дождя в знойное лето\r\nКак весна без цветов иль рассвет без россы\r\nБез Тебя, мой Господь, нет жизни и света\r\nЯ нуждаюсь в Тебе, о Иисус приходи.\r\n\r\nЯ жду Тебя, рассвет предваряя\r\nЯ жду Тебя, склоняясь в тиши\r\nЯ жду Тебя, все сердцем взывая\r\nЯ жду Тебя, о Иисус, приходи.\r\n\r\nНа кресте Он страдал, кровь за нас проливая,\r\nЧтоб простить и омыть все людские грехи.\r\nО, придите к Христу, Бог вас призывает\r\nУ подножья креста есть прощенье для всех.\r\n\r\nЯ жду Тебя, рассвет предваряя\r\nЯ жду Тебя, склоняясь в тиши\r\nЯ жду Тебя, все сердцем взывая\r\nЯ жду Тебя, о Иисус, приходи.\r\n\r\nМы ждем Тебя, рассвет предваряя\r\nМы ждем Тебя, склоняясь в тиши\r\nМы ждем Тебя, всем сердцем взывая\r\nМы ждем Тебя, о Иисус, приходи.\r\n'),
  (27, 'Как иссохшая земля жаждет дождя', 'Как иссохшая земля жаждет дождя,\r\nТак душа моя, мой Бог\r\nКак олень не может жить без воды,\r\nТак я без Тебя!\r\n\r\nЯ не могу без Твоих сил на каждый день!\r\nЯ не могу без Твоей милости и благодати!\r\nЯ не могу без Твоих слов на каждый день,\r\nЯ не могу без Твоей любви и благодати!\r\n'),
  (28, 'Так бывает порою: на хороших людей', 'Так бывает порою: на хороших людей\r\nкто-то скажет плохое и разлучит друзей.\r\n//Гибнут нежные чувства, остывает любовь\r\nот чьих-то коварных и завистливых слов.//\r\n\r\nКрикнуть хочется людям, чтоб услышал весь свет:\r\n\"осторожно с любовью, ведь возврата к ней нет!\".\r\n//В мире горя довольно, берегите любовь\r\nот жестоких ударов и завистливых слов.//\r\n\r\nВсем надо учиться счастье в Боге беречь,\r\nчтобы было на свете больше радостных встреч,\r\n//чтобы горечь разлуки не грозило сердцам.\r\nНе давайте же, люди, волю скверным словам.//\r\n'),
  (29, 'Как олень жаждет к водам тихим', 'Как олень жаждет к водам тихим,\r\nТак душа моя к Тебе.\r\nТы один желанье сердца\r\nЖизни свет моей судьбе!\r\n\r\nПрипев:\r\nКрепость Ты моя и щит!\r\nЛишь к Тебе мой дух спешит,\r\nТы один желанье сердца.\r\nЯ склоняюсь пред Тобой!\r\n\r\nНе нужны все богатства мира.\r\nТолько Ты даешь покой,\r\nТы один даруешь радость,\r\nТолько Ты, Спаситель, мой!\r\n\r\nТы есть Царь, правишь Ты вселенной этой.\r\nТы мой Друг и Брат\r\nЯ люблю Тебя больше всех на свете\r\nБыть Твоим я рад!\r\n'),
  (30, 'Буду жить под защитой Твоих крыл', 'Буду жить под защитой Твоих крыл,\r\nБуду жить в мире Божией любви.\r\nНочь настанет, и я не убоюсь,\r\nКогда я под защитой Твоих крыл.\r\nПод защитой, под защитой,\r\nПод защитой Твоих крыл (2р.)\r\n\r\nБуду жить под защитой Твоих крыл,\r\nМой покой под охраною Твоей.\r\nПусть летят стрелы - я не устрашусь,\r\nКогда я под защитой Твоих крыл.\r\nПод защитой, под защитой,\r\nПод защитой Твоих крыл (2р.)\r\n\r\nХочешь жить под защитой Его крыл?\r\nХочешь жить под охраною Его?\r\nВстань скорее и к Господу иди,-\r\nБудешь ты под защитой Его крыл.\r\nПод защитой, под защитой,\r\nПод защитой Его крыл (2р.)\r\n\r\nЖизнь пройдёт-перейду я в Отчий дом.\r\nБуду славить я Божию любовь.\r\nАллилуя! – спою я вновь и вновь.\r\nСлава Агнцу за пролитую кровь.\r\nПод защитой, под защитой,\r\nПод защитой Твоих крыл (2р.)\r\n'),
  (31, 'Как прекрасны вверху небеса', 'Как прекрасны вверху небеса\r\nСреди звезд ходит плавно луна\r\nИ всегда мой смотрят глаза\r\nЛишь туда, лишь туда, лишь туда.\r\n\r\nВижу неба красивый наряд\r\nПочему \"нет Творца\" говорят\r\nИ умом я понять не могу\r\nПочему, почему, почему.\r\n\r\nВсе сияет в своей красоте\r\nНебо мне говорит о Творце\r\nИ поет эту песню земля\r\nДля меня, для меня, для меня.\r\n\r\nБуду петь я о том, что спасен\r\nЧто жить буду с Иисусом Христом\r\nУ Него кровь стекала с чела\r\nЗа весь мир, за тебя, за меня.\r\n\r\nСвой восторг я хочу передать\r\nЛюдям всем о Христе рассказать\r\nНо слов лучше найти не могу\r\nКак Иисуса всем сердцем люблю.\r\n'),
  (32, 'Кистью Творца созданы небеса', 'Кистью Творца созданы небеса,\r\nНебо и земля созданы Его рукой.\r\nЯ благодарю Тебя, мой Господь,\r\nМой Господь и славлю я Тебя!\r\n\r\nПрипев:\r\nКраски Твои созданы из любви\r\nИ душа моя лишь живёт Тобой.\r\nМне подарил этот прекрасный мир\r\nБлагодать Твоя, Господи, надо мной.\r\n\r\nВсё я отдам, что получил от Тебя.\r\nМой Господь, Тебе я принадлежу теперь.\r\nЯ благодарю Тебя, мой Господь,\r\nМой Господь и славлю я Тебя.\r\n'),
  (33, 'Прихожу к Тебе я с хвалою', 'Прихожу к Тебе я с хвалою,\r\nЧудеса Твои велики\r\nИ любовь Твоя сильна! (2 раза)\r\n\r\nО, Ты велик! Ты прекрасен и велик!\r\nИ другого нет как Ты!\r\nИ другого нет как Ты! (2 раза)\r\n\r\nТы один достоин хвалы\r\nМы возносим к небу руки,\r\nВ Тебе радуемся мы.\r\n\r\nО, Ты велик! Ты прекрасен и велик!\r\nИ другого нет как Ты!\r\nИ другого нет как Ты! (2 раза)\r\n\r\nБуду петь Тебе я вечно\r\nЗа Твою любовь и милость,\r\nЧто Ты даришь нам с небес!\r\n\r\nО, Ты велик! И дела Твои сильны!\r\nВсемогущий и Святой,\r\nНет подобного Тебе!\r\n'),
  (34, 'Я буду петь Тебе, мой Бог Господь', 'Я буду петь Тебе, мой Бог Господь, прими мою хвалу.\r\nЯ душу распахну перед Тобой, Господь, прими мою мольбу!\r\n\r\nПрипев:\r\nСлёз нет в радости Твоей из пепла стал венец,\r\nГрусти нет в хвале Тебе я буду вечно восхвалять!\r\n\r\nЯ руки возношу к Тебе, Мой Бог, Господь, прими мою хвалу\r\nЯ душу распахну перед Тобой, Господь, прими мою мольюу.\r\n'),
  (35, 'Ты мне близок, словно берег морю', 'Ты мне близок, словно берег морю.\r\nТы мне дорог словно детям мать.\r\nТы пришел, чтоб сладким сделать горе.\r\nТы пришел, чтобы, простив, обнять.\r\n\r\nБез Тебя и жизни мне не надо,\r\nБез Тебя я только что дышу.\r\nТы Один мне в жизни здесь услада,\r\nБудь всегда моим, Тебя прошу.\r\n\r\nТам, где небо встретится с землею,\r\nЧтоб принять из рук ее меня,\r\nТам впервые встречусь я с Тобою,\r\nИ глаза мои узрят Тебя.\r\n\r\nА теперь надеждою живою\r\nЯ живу, пока в свой час умру.\r\nТы мне ближе, чем песок прибою,\r\nИ дороже, чем вода в жару.\r\n'),
  (36, 'Когда мне тоскливо - я слышу', 'Когда мне тоскливо - я слышу крик чайки,\r\nГлаза поднимаю вверх /2р.\r\nЯ знаю, что скоро придёт мой Спаситель,\r\nВозьмёт Он меня к Себе /2р.\r\n\r\nЧайка, чайка, кричи, кричи,\r\nМеня глаза вверх поднять зови,\r\nЧтоб мы смотрели чаще в небеса,\r\nЧтоб в них мы видели Христа.\r\n\r\nКогда я обижен - я слышу крик чайки,\r\nГлаза поднимаю вверх /2р.\r\nЯ знаю, что скоро придёт мой Спаситель,\r\nВозьмёт Он меня к Себе /2р.\r\n\r\nКогда одинок я - я слышу крик чайки,\r\nГлаза поднимаю вверх /2р.\r\nЯ знаю, что скоро придёт мой Спаситель,\r\nВозьмёт Он меня к Себе /2р.\r\n'),
  (37, 'Көктегі Әкемізге болсын деймін мадақ', 'Көктегі Әкемізге болсын деймін мадақ.\r\nОның мейірімділігі бізбен бірге болмақ.\r\nКөктегі Әкемізге болсын деймін мадақ.\r\nОның рақымдылығы бізге жетіп тұрмақ.\r\n\r\nҚайырмасы:\r\n//Сән-салтанат Иемізге. Бар мақтау Құдайға!\r\nКиелі Рухың біздерге жар болсын Аумин!//\r\n\r\n//Құтқарушым Саған да болсын деймін мадак,\r\nСенде ғана сая тапты күнәһар ұрпақ.//\r\n\r\n//Киелі Рух Саған да болсын деймін мадақ!\r\nҚұтқарылу, шындық жолын көрсеттің аңық.//'),
  (38, 'Міне, бүгін келді уақыт', 'Міне, бүгін келді уақыт Саған, Алдың ауыр жаза,\r\nБолсаңдығы мейірімге толған, Күнәлардан таза.\r\n//Жас келеді мөлдіреп жанарыма, Қалай ғана шаыдадың азабына?\r\nАғашта айқыш қағылған темір шеге, Жазықсыз қолдарыңа//\r\n\r\nШақырасың барлық адамзатты, Ғажайып өмірге,\r\nӨз қолыңмен салып бердің жолды, Жұмақтың төріне.\r\n//Мәңгіліктің жетелеп есігіне, Құтқарылу сыйладың бар әлемге.\r\nАйтшы, неге бардың Сен Өз еркіңмен, Осы бір өлімге//\r\n\r\nБіздер үшін бұл дүниеге келіп, Өлімнен тірілдің,\r\nЖер бетіне Рухынды жіберіп, Биіктен көріндің.\r\n//Сенемін мен, Сен менің қасымдасың, Саған деген сенімім таусылмасын,\r\nКүтемін мен Өзінді Иса Ием, Айтқаның орындалсын//'),
  (39, 'Кружится, как пушинка', 'Кружится, как пушинка, в космосе земля.\r\nСредь бесконечной вселенной как она мала!\r\nЛюди, дайте руки друг другу-( 3 раза),\r\nА Богу ваши сердца .\r\n\r\nВремя забыть раздоры, вспомнить о любви,\r\nЧтоб на земные просторы радость, мир сошли.\r\nЛюди, улыбнитесь друг другу-( 3 р.),\r\nПусть свет струится из глаз .\r\n\r\nЛюбит Отец небесный всех людей земли,\r\nТак поспешим под чудесный свет Его любви.\r\nЛюди, воздадим Богу славу -(3 раза )\r\nПрославим Имя Творца.\r\n\r\nЛюди, дайте руки друг другу,\r\nЛюди, улыбнитесь друг другу,\r\nЛюди, воздадим Богу славу, Прославим Имя Творца.\r\n'),
  (40, 'Куда теперь идти мне?', 'Куда теперь идти мне?\r\nКуда направить путь,\r\nК земным ли благам жизни,\r\nГде можно отдохнуть?\r\n\r\nПрипев:\r\nЛишь там найду покой я,\r\nЛишь там не будет слёз, не будет слёз.\r\nЛишь там забуду скорбь я,\r\nГде Сам царит Христос!\r\n\r\nКак трудно всё оставить,\r\nСтремиться лишь туда,\r\nГде ждёт меня Отчизна –\r\nЖеланная страна.\r\n\r\nНе знаю, что со мною\r\nСлучилось вдруг теперь,\r\nА сердце всё тоскует\r\nО Родине моей.\r\n'),
  (41, 'Бог искупил этот мир', 'Бог искупил этот мир Жертвой Голгофской.\r\nК нам из любви Божий Сын В мир пришёл.\r\nДвери Он людям открыл В дом Отцовский\r\nТем, кто от наших грехов В жизни устал.\r\n\r\nПрипев:\r\nИ Он взял мира грехи на Свои плечи.\r\nОн и твою вину на крест понёс.\r\nИисус сегодня идёт К тебе навстречу –\r\nНе оттолкни же Его святую любовь.\r\n\r\nМы в беззакониях тьмы некогда жили\r\nНо Он исполнил за нас Божий закон.\r\nИ за всё злое, что наши руки творили\r\nБыли прибиты к кресту руки Его.\r\n\r\nСколько раз Он стучал, друг, в твоё сердце\r\nБросить ты был готов порочную жизнь.\r\nВраг рисовал миражи на горизонте\r\nИ ты опять уходил в пустыню грехов.\r\n'),
  (42, 'Любит Иисус меня', 'Любит Иисус меня, любит меня,\r\nПесня об этом пусть будет моя.\r\nИ не скрываю я, пусть знают все:\r\nЛюбит Иисус меня, Его люблю я!\r\n\r\nПрипев:\r\nОн любит меня, Его люблю я,\r\nЧудесная дружба с Ним у меня.\r\nВсегда и везде мне с Ним хорошо,\r\nОн близок в беде, Он рядом со мной.\r\n\r\nМир предлагает мне дружбу свою,\r\nНо говорю я: нет! И песню пою.\r\n\r\nДруг, подойди к Христу, сердце открой,\r\nРуку отдай свою в руку Его.\r\nВечную радость ты обретешь\r\nИ песню новую с Ним запоешь.\r\n'),
  (43, 'Кто есть на небе у меня?', 'Кто есть на небе у меня? Только Ты!\r\nА в жизни только песнь моя: Мой Христос.\r\nОн за меня в горниле был, Меня Он кровью искупил\r\nИ душу с Богом примирил - Мой Христос!\r\n\r\nНе милы мне богатств дары - Только Ты!\r\nЯ не хочу их мишуры - Мой Христос!\r\nМир вечного нам не дает, Все тленья облик в нем несет,\r\nМоя же радость не пройдет - Мой Христос!\r\n\r\nХотя б я бедным плотью стал - Только Ты!\r\n\"Он знает лучше!\" - я б сказал - Мой Христос!\r\nПусть в винограднике беда, И пусть маслины без плода,\r\nИ пусть бичует мор стада - Мой Христос!\r\n\r\nХоть я среди вражды, огня - Только Ты!\r\nИ осаждает грех меня - Мой Христос!\r\nХоть мир готовит грозный бой, Хоть все враги идут войной,\r\nЯ в Нем силен - прорву их строй - Мой Христос!\r\n\r\nКогда ж расстанусь я с землей - Только Ты!\r\nВ Его руках найду покой - Мой Христос!\r\nКогда мне скорби дух стеснят, Природы силы замолчат,\r\nЯ буду петь сквозь смерти яд: Мой Христос!\r\n'),
  (44, 'Пусть на пороге неведомых дней', 'Пусть на пороге неведомых дней\r\nСердце не дрогнет твое,\r\nПусть если даже и будет темней, -\r\nТы не страшись ничего.\r\n\r\nЗнай, по тропинке с тобою идет Тот,\r\nКто весь мир победил;\r\nВерь, что чрез скорбь Он тебя проведет,\r\nВерь, и Он даст тебе сил.\r\n\r\nПусть на пороге неведомых дней\r\nСилу черпает душа,\r\nСилу, которую дал нам ручей,\r\nТот, что течет со креста.\r\n'),
  (45, 'Как лань желает, Как лань желает', 'Как лань желает, Как лань желает к потокам воды,\r\nТак желает душа, Так желает душа моя к Тебе, мой Бог!\r\nЖаждет душа моя, К Богу живому стремится душа.\r\nНо когда я приду И когда я явлюсь перед лицом Твоим?\r\n\r\nПрипев:\r\nЧто унываешь ты, душа моя?\r\nИ что смущаешься?\r\nТы уповай на Бога, ибо я\r\nБуду вечно Его хвалить!\r\n\r\nДнем явит милость, Днем Свою милость мне явит Господь.\r\nНочью песнь у меня И молитва летит моя К тебе, мой Бог!\r\n\r\nПлачу я день и ночь Слезы мои стали хлебом моим\r\nИ враги мои вновь Говорят мне: \"Ну, где же Бог?\r\nНу, где твой Бог?\"\r\n'),
  (46, 'Кто живёт под покровом Всевышнего', 'Кто живёт под покровом Всевышнего,\r\nПокоится под сенью Его,\r\nСкажет Богу: «прибежище, защита моя,\r\nГосподь, на Тебя уповаю».\r\n\r\nПрипев:\r\nЯ хочу жить всегда под кровом Господа,\r\nВ безопасности будет душа моя.\r\nВесь мой страх уже прошёл,\r\nБожий мир ко мне сошёл,\r\nРука Господня охраняет меня.\r\n\r\nТебя избавит от сети ловца,\r\nСохранит Бог от гибельной язвы,\r\nПерьями Своими осенит тебя,\r\nПод крылами Его безопасность.\r\n\r\nНе убоишься ты ужасов ночи,\r\nНи стрелы, что летит днём в тебя.\r\nУвидишь, подле тебя падут тысячи,\r\nНо ничто не коснётся тебя.\r\n\r\nБудет Ангелам Его повеление –\r\nНа небесных крылах понести,\r\nЧтобы не были камни преткновением\r\nБезопасны чтоб были в пути.\r\n'),
  (47, 'Благо есть славить Господа', 'Благо есть славить Господа\r\nИ петь имени Твоему, Всевышний,\r\nВозвещать утром милость Твою\r\nИ истину Твою везде.\r\n\r\nПрипев:\r\nТы возвеселил душу мою,\r\nЯ Тебе, Господь, хвалу пою!\r\nДела Твои велики, мой Отец,\r\nТы мой Творец! (х2)\r\n\r\nВозвожу мои очи к горам,\r\nВозношу мои руки к небу.\r\nЯ познал, что Господь есть\r\nБог и истина Его вовек.\r\n\r\nТы укажешь мне жизни путь,\r\nДа не постыжусь, что к Тебе взываю,\r\nНа Тебя уповаю я,\r\nБлагослови, Господь, меня.\r\n\r\nГосподи, Ты защита моя,\r\nТы-мой щит и твердыня жизни,\r\nНа земле, в небесах высоко\r\nСлава Твоя во век веков.\r\n'),
  (48, 'Если грех тебя теснит', 'Если грех тебя теснит, – Все скажи Иисусу.\r\nЕсли враг тебе грозит, –Все скажи Иисусу.\r\n\r\nПрипев:\r\nС верой на Него взирай, На Него лишь уповай,\r\nЛишь Его словам внимай, Все скажи Иисусу.\r\n\r\nИль заботы тяготят – Все скажи Иисусу.\r\nТы не бойся, пусть грозят– все скажи Иисусу.\r\n\r\nПуть твой узок, одинок – Все скажи Иисусу.\r\nИль наградою высок – Все скажи Иисусу.\r\n\r\nИ в работе, и в труде – Все скажи Иисусу.\r\nОбещает Он помочь – Все скажи Иисусу.\r\n'),
  (49, 'Он моя сила, Он мой щит', 'Он моя сила, Он мой щит\r\nКогда я слаб Он защитит\r\nОн мое все во всем\r\nСледовать буду я за Ним\r\nИ всегда буду Им храним\r\nОн мое все во всем.\r\n\r\nПрипев:\r\nИисус мой Господь\r\nОн достоин славы.\r\n\r\nВзяв мою боль, мой грех, мой крест\r\nУмер Господь мой вместо всех\r\nОн мое все во всем\r\nВсе мои нужды день за днем\r\nОн обеспечит, верен Он\r\nОн мое все во всем.\r\n'),
  (50, 'Царь царей, Господь господ', '//Царь царей, Господь господ,- Слава, Аллилуйя!//\r\n//Иисус-наш Мессия, Слава, Аллилуйя!//\r\n'),
  (51, 'Юность белоснежную береги от зла', 'Юность белоснежную береги от зла,\r\nКак фиалку нежную пред собой неся.\r\nМиру тьмы, лукавому глаз не отдавай\r\nИ греху всевластному ты скажи: «Прощай»\r\n\r\nСердце тебе чистое подарил Христос,\r\nСолнышко лучистое, шум морей и гроз.\r\nИ в одежду святости Он тебя одел,\r\nНе испачкай пятнами недостойных дел.\r\n\r\nПусть работой чистою годы протекут\r\nИ венцы небесные увенчают труд.\r\nИ в святых обителях, получивши жизнь,\r\nНа алтарь Спасителю молодость сложи.\r\n'),
  (52, 'Мимо текла, текла река', 'Мимо текла, текла река, Плыли куда-то облака,\r\nШел человек, была дорога нелегка,\r\nИ человек мечтал о том, Что он построит где-то дом,\r\nИ поселится счастье в нем, В доме одном.\r\n\r\nЕсли когда-то уставал, Oн неизменно напевал,\r\nПесню любимую свою, ту, что пою.\r\nДом, как известно всем давно,Это не стены, не окно,\r\nДаже и стулья со столом - Это не дом.\r\n\r\nДом – это то, куда готов, Ты возвращаться вновь и вновь,\r\nЯростным, добрым, нежным, злым, Еле живым.\r\nДом – это там, где вас поймут, Там, где надеются и ждут,\r\nГде ты забудешь о плохом - Это твой дом.\r\n\r\nЗа облаками, в вышине, Дан лучший дом тебе и мне,\r\nНам приготовлен он Отцом, Чтобы жить в нем.\r\nНо чтоб в небесном доме жить, Надо в земных домах хранить\r\nМир и любовь, уют и свет, Божий завет.\r\n'),
  (53, 'Хвала тебе, наш Господь', '//Хвала тебе, наш Господь, Хвала тебе -\r\nБог святой, Хвала за Иисуса - Сына Твоего.//\r\n\r\n//Теперь, слабый скажет \"Я силен\" Бедный скажет:\r\n\"Я богат\", Благодаря тому, что Бог нам дал.//\r\n'),
  (54, 'Льются нежные мотивы', 'Льются нежные мотивы И мелодии любви\r\nЗвуки струн гитары верной Между нами посреди.\r\nЛьются светлые улыбки Круг друзей вокруг тебя\r\nВсе запомнится, наверно, Между нами говоря…\r\n\r\nПрипев:\r\nТы смотри друзей своих Забывать не смей на миг\r\nУ груди своей храни их лик … / 2раза\r\n\r\nЭто чудное мгновенье Словно сон перед тобой\r\nТы сидишь немного грустный И задумчивый такой.\r\nВсе собрались для веселья Твою радость разделить\r\nИ желаньями благими Твою жизнь сопроводить…\r\n\r\nВот и лодка у причала, путь далекий впереди.\r\nБудешь с бурями бороться, Нужно к цели ведь дойти.\r\nБудешь ты не раз склоняться На колени у кормы\r\nИ просить у Бога силы: Помоги, прости, веди.\r\n\r\n\r\nА вдали увидишь пристань, гавань тихая блестит.\r\nТы вставай скорей, бодрися. Цель уж видна впереди.\r\nТам опять мы будем вместеТак же, как вот и сейчас,\r\nТам друзья нас ждут и близкие. Пир готов уже для нас.\r\n'),
  (55, 'О, Иисус, войди скорее', 'О, Иисус, войди скорее в мою жизнь,\r\nГосподь, в духовной жажде\r\nИстомилась плоть.\r\nТебя в смирении я жду.\r\n\r\nДень ото дня\r\nДуша моя скорбит и ждет Тебя,\r\nВ духовной жажде изнываю я.\r\nПриди и успокой меня.\r\n\r\nО, Иисус,\r\nЯ твердо верю - ты войдешь ко мне,\r\nОткрыта дверь и свечи все в огне.\r\nТебя в смирении я жду.\r\n'),
  (56, 'Мир вам! Настал общения час', 'Мир вам! Настал общения час.\r\nМир вам! И мы приветствуем вас.\r\nМир вам! Пусть будет слышен смех детей.\r\nМир вам! И больше радостных дней.\r\nМир вам! Сердца пусть будут добрей.\r\nМир вам! Вы не судите людей.\r\nМир вам! И вашим близким и друзьям.\r\nМир братья, сёстры вам!\r\n\r\nМир вам, братья, сёстры! «Мир вам» — так же просто\r\nВы говорите всем друзьям.\r\nМир вам и с любовью, счастья и здоровья:\r\nМир, братья, сёстры вам.\r\n\r\nМир вам! Мы говорим в этот час.\r\nМир вам! Господь да будет средь вас.\r\nМир вам! Пусть крепнет вера каждый час.\r\nМир вам! Болезнь покинет пусть вас.\r\nМир вам! Огонь любви пусть горит.\r\nМир вам! Пусть крепче дом ваш стоит.\r\nМир вам! Конца не будет вашим дням.\r\nМир братья, сёстры вам!\r\n'),
  (57, 'Много лет учили тебя и меня', 'Много лет учили тебя и меня:\r\nЧтобы лодка жизни удачно плыла,\r\nГлавное - не бросить вёсла свои,\r\nНо, Иисус, однажды открылся мне Ты.\r\n\r\nПрипев:\r\nТы мой белый парус, моя ладья,\r\nРассекая волны, плывёт в небеса.\r\nИисус мой парус, бросил вёсла я,\r\nВетер благодати влечёт меня.\r\n\r\nБерега не видно, всё объяла мгла.\r\nЯростные волны бьются о борта.\r\nВёсла переломаны, только надо мной\r\nВсё такой же белый парус мой.\r\n\r\nВ нашей жизни вёсла - это дела,\r\nЧто без Бога сделали, чтоб спасти себя.\r\nСпасенье приготовлено Богом моим,\r\nБелым, белым парусом моим.\r\n'),
  (58, 'Не случайно стоим мы здесь', 'Не случайно стоим мы здесь,\r\nГосподь нас всех сроднил.\r\nОн спасение подарил,\r\nПрощение и любовь.\r\n\r\nПрипев:\r\nВот и теперь здесь стоим\r\nПеред Ним, да перед Ним,\r\nПеред нашим Иисусом Христом.\r\nКаждый день, каждый час\r\nБлагословляешь Ты нас,\r\nБлагодарим мы Тебя, Иисус.\r\n\r\nПомоги нам жизнь прожить\r\nТак, как Ты учил.\r\nВерой и надеждой жить,\r\nЛюбовью всем служить.\r\n'),
  (59, 'В день, когда труба Господня', 'В день, когда труба Господня Всех пред Ним нас соберет,\r\nВстретимся ль мы в небе у кристальных вод (чистых вод)?\r\nТам на брачную вечерю Иисус Христос введет\r\nМногочисленный бессмертный Свой народ.\r\n\r\nПрипев:\r\nО, какая встреча будет (о какая встреча будет,\r\nвечно там народ пребудет)!\r\nИзбавленье возвестит глас трубный нам (трубный нам).\r\nО какая встреча (там мы встретим наших\r\nблизких среди райских рощ тенистых)!\r\nНеразлучны будем мы навеки там.\r\n\r\nВозвестит Господний Ангел: Времени уж больше нет!\r\nИ восхищены мы будем в облаках (облаках).\r\nТам на нас Творец Вселенной изобьет\r\nСвой дивный свет, о, как счастливы мы будем в небесах!\r\n\r\nВсе, что тайно и сокрыто,- В день явления\r\nЕго Будет явно и открыто пред судом (пред судом).\r\nСкажет праведным: Войдите в радость Бога Своего!\r\nО, какое мы блаженство там найдем!\r\n\r\nТам при звуках гуслей стройных, Арф небесных, золотых,\r\nГимн победы в день спасенья воспоем (воспоем).\r\nПесню Агнца, Моисея с сонмом праведных, святых,\r\nПеснь неведомую миру мы споем.\r\n'),
  (60, 'На земле, на земле много', 'На земле, на земле много мести и скорбей,\r\nНо меня хранит надежда.\r\nЯ смотрю на небеса вижу - яркая звезда\r\nСветит также, как и прежде.\r\n\r\nПрипев:\r\nВедь где то там, в светлой дали, есть страна любви.\r\nТам не будет слез печали и разлуки.\r\nТам город есть Иерусалим, Я туда стремлюсь\r\nНа груди Христа забыть страданья, муки.\r\n\r\nНе хочу, не хочу унывать я и грустить.\r\nРадость ждет меня святая.\r\nВ час, когда Господь придет, за Собою поведет\r\nГрех забуду навсегда я.\r\n'),
  (61, 'Над церковными куполами', 'Над церковными куполами\r\nНебо синее-синее…\r\nЯ иду. Под моими ногами\r\nстепь пролегла родимая.\r\n\r\nПрипев: Тихим светом далеким светится\r\nНебо, звездами озаренное,\r\nИ легко душе моей верится\r\nВ вечный рай, где живут спасенные.\r\n\r\nВ чистом небе поют херувимы.\r\nСердце рвется от счастья и радости.\r\nДля меня этот мир — любимый —\r\nПолон света, добра и благости.\r\n\r\nНа земле моя жизнь окончится,\r\nПролетит незаметно, как птица.\r\nИ мне очень, очень хочется\r\nЗа родной Казахстан молиться!\r\n'),
  (62, 'Найду покой, В Тебе Христос', 'Найду покой, В Тебе Христос, Верю я, в могущество Твоё!\r\n\r\nПрипев:\r\nПусть бушует шторм и гром гремит,\r\nНад бурей буду я с Тобой парить,\r\nМой Отец Ты Царь над всей Землей,\r\nЯ не боюсь, мой Бог со мной.\r\n\r\nСкрой меня, Своим крылом, Сохрани, под сильною рукой!\r\n'),
  (63, 'Нас всех рисует время', 'Нас всех рисует время, всё пишет свой портрет\r\nОт самой колыбели до тех прощальных лет.\r\nС дороги не сбиваясь оно ведёт свой счёт —\r\nТо выстелит морщинку, то складку насечёт.\r\n\r\nПрипев:\r\nМеняет время лица, Бог — души и сердца,\r\nИ это длится века, века, века. (2 раза)\r\n\r\nЗа радости сверх меры, за прежние грехи,\r\nБезжалостно и строго кладёт оно штрихи.\r\nТо длинный штрих положит, а то взмахнёт слегка\r\nИ белая дорожка ложится у виска.\r\n\r\nИ снова белым, белым, и входит цвет в права\r\nИ зимнею порошей повита голова.\r\nСпешат за годом годы, идёт за следом след,\r\nА время всё рисует, всё пишет свой портрет.\r\n'),
  (64, 'Нам жизнь дана', 'Нам жизнь дана не для пустых мечтаний\r\nНе для служенья царству суеты,\r\nНо чтобы в ней среди земных скитаний\r\nНайти следы небесной красоты.\r\n\r\nНам жизнь дана, чтоб обрести спасенье\r\nИ всю ее Иисусу посвятить,\r\nСлужить Ему и в жизненном хожденье,\r\nВ себе самом Христа отобразить.\r\nСлужить Ему и в жизненном хожденье,\r\nВ себе самом Христа отобразить.\r\n\r\nНам жизнь дана нести Святое Слово\r\nСреди людей, томящихся в грехах,\r\nИ чтоб и я, и ты были готовы Служить\r\nХристу и в горе, и в скорбях.\r\nИ чтоб и я, и ты были готовы Служить\r\nХристу и в горе, и в скорбях.\r\n\r\nСомненьям, проникающим порою,\r\nКак христиане смело скажем: Прочь!\r\nНаш Друг Христос Божественной рукою\r\nПоможет все преграды превозмочь.\r\nНаш Друг Христос Божественной рукою\r\nПоможет все преграды превозмочь.\r\n\r\nЗемная жизнь сурово нас встречает,\r\nНо мы пришли сюда не пировать,\r\nВокруг нас мир в неверье погибает,\r\nА мы желаем грешников спасать.\r\nВокруг нас мир в неверье погибает,\r\nА мы желаем грешников спасать.\r\n\r\nМы молимся, и Бог мольбе внимает,\r\nОн хочет просьбы наши исполнять,\r\nОн хочет силы и здоровье дать нам,\r\nЧтоб, здесь живя, Его нам прославлять.\r\nОн хочет силы и здоровье дать нам,\r\nЧтоб, здесь живя, Его нам прославлять.\r\n'),
  (65, 'Так уж бывает летней порой', 'Так уж бывает летней порой,\r\nКогда луч солнца радует нас,\r\nЧас испытанья будто рукой\r\nНебо закроет от нас.\r\n\r\nПрипев:\r\nИ если не станет овец в загоне,\r\nИ виноград не даст плода,\r\nВсе равно я буду славить\r\nГоспода Христа!\r\n\r\nЕсли закружит меня волною\r\nВ водовороте суетного дня,\r\nВерой поднявшись над суетою,\r\nВновь запоет душа моя:\r\n\r\nЕсли однажды теряешь что-то,\r\nВспомни слова Иисуса Христа:\r\n«Где ваша ценность - там сердце будет!»\r\nА значит, потеря не велика!\r\n\r\nЕсли однажды жизненный выбор:\r\nРади Иисуса и славы Отца\r\nНужно лишиться овец в загоне,\r\nХочу, чтобы спела душа моя:\r\n'),
  (66, 'Я вижу дом, где молятся Ему', 'Я вижу дом, где молятся Ему,\r\nГде воздают всю славу одному Творцу.\r\nЯ вижу дом, размером со страну,\r\nГде светит свет, спасения свет.\r\n\r\nПрипев:\r\nКазахстан, ты будешь спасён,\r\nСвет любви воссияет.\r\nКазахстан, здесь Бог только Он,\r\nЗдесь Царь только Он –  Иисус Христос.\r\n\r\nЯ вижу день, когда река любви\r\nНаполнит всё, чтоб приготовить путь Творцу.\r\nЯ вижу день, когда народы всей земли\r\nВоскликнут все: Господь, гряди!\r\n'),
  (67, 'Не надо нам счастья земного', 'Не надо нам счастья земного, не вечно оно будет длиться\r\nДуша преклоняясь пред Богом желает Ему молиться\r\nПорой тяжело бывает, но где бы я в жизни не был\r\nДуша моя твердо знает стремиться ей нужно в небо\r\n\r\nПрипев:\r\nНебо оно не случайно, небо глубокая тайна\r\nНо в Библии свет перед нами нам открывает её\r\nЗа облаками за облаками есть вечное бытие\r\n\r\nКогда я уверовал в Бога, на призыв Христа отозвался\r\nТо от притяженья земного, не сразу, но оторвался\r\nНе космос нам нужен а небо, где слабых не обижают\r\nГде не умирают без хлеба, где вечность и мир обитают\r\n\r\nДуша в мире зла не пригрелась, Земля ей не стала домом\r\nХотя для нее все имелось и было в достатке готово\r\nНо перед грехом склоняться бессмертной душе нелепо\r\nДуша моя ввысь стремится нужны ей свобода и небо!\r\n'),
  (68, 'Он призвал тебя однажды', 'Он призвал тебя однажды, Исцелил от слепоты,\r\nЧтоб Христа увидел тоже И пошел Его тропой,\r\nЧтоб узнал о том прохожий, Что соделал Он с тобой;\r\n\r\nЧтоб израненные души Не блуждали в темноте,\r\nЧтоб на море и на суше Все узнали о Христе.\r\nОн сказал: \"Иди за Мною, Для тебя работа есть\".\r\nЯ пойду тропой земною Возвещать Благую Весть.\r\n'),
  (69, 'Однажды, однажды Господь', 'Припев:\r\nОднажды, однажды Господь придет за Церковью Своей,\r\nНа небо, на небо Он заберет Своих детей, Своих детей.\r\n\r\nТы пришел, чтобы дать нам спасенье, Ты- мой Бог! Ты - живая вода!\r\nИисус, от греха избавленье, Тынам дал навсегда, навсегда!\r\nЯркий свет, свет любви неземной\r\nОзарил мою жизнь, дал мне мир и покой.\r\nОзарил мою жизнь, дал мне мир и покой. О, Господь! Ты меня измени\r\nИ согрей меня светом Своей любви, Своей любви.\r\n\r\nБыть хочу в руках Твоих, Бог мой, Как свеча, что Тобой зажжена\r\nЧтоб сиять Твоим светом навсегда, навсегда.\r\nЯркий свет, свет любви неземной\r\nОзарил мою жизнь, дал мне мир и покой.\r\nОзарил мою жизнь, дал мне мир и покой. О, Господь! Ты меня измени\r\nИ согрей меня светом Своей любви, Своей любви.\r\n'),
  (70, 'Пожелаем тебе удачи              ', 'Пожелаем тебе удачи,\r\nЧтобы видеть как звёзды гаснут\r\nЧтобы слышать как люди плачут,\r\nКогда их обижают напрасно. /2р.\r\n\r\nПожелаем тебе дороги,\r\nИ песка, и снега, и ветра.\r\nПожелаем тебе тревоги,\r\nЧтобы был ты всегда человеком.\r\n\r\nПожелаем тебе печали,\r\nЧтобы видеть чужое горе,\r\nЧтобы слышать как стонут чайки,\r\nКогда шторм закипает в море.\r\n\r\nПожелаем тебе удачи,\r\nВ каждом слове, и в каждом деле,\r\nЕсли с Господом путь твой начат,\r\nЗначит верно идёшь ты к цели.\r\n'),
  (71, 'О, Благодать, Спасен', 'О, Благодать, Спасен Тобой я из пучины бед;\r\nБыл мертв и чудом стал живой, Был слеп и вижу свет.\r\nСперва внушила сердцу страх, Затем - дала покой.\r\nЯ скорбь души излил в слезах, Твой мир течет рекой.\r\n\r\nПрошел немало я скорбей, Невзгод и черных дней,\r\nНо ты всегда была со мной Ведёшь меня домой.\r\nСловам Господним верю я, Моя вся крепость в них:\r\nОн - верный щит, Он - часть моя Во всех путях моих.\r\n\r\nКогда же плоть моя умрет, Придет борьбе конец,\r\nМеня в небесном доме ждет И радость, и венец.\r\nПройдут десятки тысяч лет, Забудем смерти тень,\r\nА Богу также будем петь, Как в самый первый день.\r\n'),
  (72, 'Аллилуйя, аллилуйя', 'Аллилуйя, аллилуйя, Ты-наш Бог-2р.\r\nВ благодати Ты велик\r\nВ милосердье терпелив\r\nАллилуйя, аллилуйя, Ты наш Бог\r\n\r\nАллилуйя, аллилуйя, Наш Отец 2р.\r\nОкружил заботой всех\r\nНе иссякнуть ей вовек\r\nАллилуйя, аллилуйя, Наш Отец\r\n\r\nАллилуйя, аллилуйя, Иисус 2р.\r\nТвои муки на кресте\r\nПринесли спасенье мне\r\nАллилуйя, аллилуйя, Иисус\r\n\r\nАллилуйя, аллилуйя, Дух Святой 2р.\r\nЖизнь и силу Ты даёшь\r\nДухом к небу нас влечёшь\r\nАллилуйя, аллилуйя, Дух Святой\r\n'),
  (73, 'От погибели спасла', 'От погибели спасла меня Милость Божия,\r\nВ день отчаянья нашла меня Милость Божия.\r\nТо, что я ещё живу, то, что Господу служу,\r\nЭто милость Божия, Это милость Божия.\r\nТо, что я ещё живу, то, что Господу служу,\r\nЭто милость Божия - это милость Божия.\r\n\r\nЧто грехи мои Он смыл, это милость Божия,\r\nИ Свой Дух Святой излил – это милость Божия.\r\nТо, что вера мне дана и ведёт меня она,\r\nЭто милость Божия, это милость Божия.\r\nТо, что вера мне дана и ведёт меня она,\r\nЭто милость Божия, это милость Божия.\r\n\r\nВновь и вновь благодарю эту милость Божию\r\nИ желанием горю славить милость Божию,\r\nЭту бездну доброты, эту силу чистоты,\r\nЭту милость Божию, эту милость Божию.\r\nЭту бездну доброты, эту силу чистоты,\r\nЭту милость Божию, эту милость Божию.\r\n\r\nВсё однажды здесь пройдёт, даже милость Божия,\r\nОт земли Бог заберёт свою милость Божию.\r\nТам где место для святых будет вечно им святить\r\nЭто милость Божия, это милость Божия.\r\nТам где место для святых будет вечно им святить\r\nЭто милость Божия, это милость Божия.\r\n'),
  (74, 'Свят Бог, святы небеса', 'Свят Бог, святы небеса\r\nСвят Бог, святы небеса,\r\nАгнец Божий свят, Тебе хвала\r\nТы взял вину мою, проливши Кровь Свою\r\nТебе моя хвала!\r\n\r\nТы взял болезни, понес мои грехи,\r\nТы – Сын Давида вовек!\r\nТы – Пастырь добрый людских сердец,\r\nТы есть начало и конец!\r\n\r\nСпаситель мира, вкусивший смерть за нас,\r\nОтдавший Тело и Кровь.\r\nЯ чище снега, мой грех омыт\r\nГолгофский крест – Твоя любовь!\r\n'),
  (75, 'Он пришёл, спасая всех', 'Он пришёл, спасая всех от земных тревог\r\nНастоящий человек, настоящий Бог.\r\nСамарянкой найден свет у Христовых ног\r\nЖаждал Он как человек, а поил как Бог.\r\n\r\nЛазарь мёртв и Он при всех, слёз сдержать не смог.\r\nПлакал Он как человек, воскрешал как Бог.\r\nБуря, шторм – не виден брег. Волны бьют челнок.\r\nНа корме спит человек, шторм смиряет Бог.\r\n\r\nАд и смерть, злобу и грех победить Он смог\r\nУмер Он как человек, а воскрес как Бог.\r\nКто к Нему свой смертный груз с верой не принёс\r\nТем известен Иисус, не знаком Христос.\r\n\r\nОн для них философ, лекарь, гений, вождь, пророк\r\nИм известен человек, но не ведом Бог.\r\nВознесён во славе вверх, Тот, Кто в яслях был\r\nОн пришёл как человек, Богом уходил.\r\n'),
  (76, 'Бог избрал нас из разных народов', 'Бог избрал нас из разных народов,\r\nДал нам имя Свое.\r\nБог призвал нас из разных племён и языков,\r\nНо дал нам сердце одно.\r\n\r\nПрипев:\r\nКогда мы вместе, мы сильны.\r\nКогда мы вместе, Господь среди нас.\r\nКогда мы вместе, потоки Божьей любви\r\nЛьются с небес на нас.\r\n\r\nМы есть церковь, мы есть тело Христа,\r\nМы искуплены кровью Его.\r\nВрата ада не одолеют нас,\r\nПотому что непобедимы мы.\r\n'),
  (77, 'Открой глаза мои, чтоб я', 'Открой глаза мои, чтоб я\r\nУзрел небесных тайн лучи,\r\nЧудесный ключ Твой мне вручи,\r\nЧтоб на свободу вышел я,\r\nВ молчанье жду... О, дай ответ\r\nИ волю мне Твою открой...\r\nОткрой глаза, пошли Твой свет,\r\nО Дух Святой.\r\n\r\nОткрой мне слух, чтоб слышал я\r\nГлаголы истины Твоей,\r\nПри звуках вечной правды слов\r\nУмолкнет шум земных речей.\r\nВ молчанье жду... Явись мне Сам\r\nИ правду вечную открой.\r\nОткрой мне слух к Твоим словам,\r\nО Дух Святой!\r\n\r\nОткрой уста мои, чтоб я\r\nТвою мог правду возвещать;\r\nОткрой мне сердце, чтоб всегда\r\nЛилась потоком благодать.\r\nВ молчанье жду... Приди скорей.\r\nИсточник силы мне открой,\r\nОткрой мне сердце, будь во мне,\r\nО Дух Святой!\r\n'),
  (78, 'Перед Тобой склоняюсь я', 'Перед Тобой склоняюсь я, Всемогущий Бог.\r\nИсточник утешения Я нашёл у Твоих ног.\r\n\r\nПрипев:\r\nО Ты велик, Нет Тебе подобного.\r\nПеред Тобой склоняюсь я,Ты любовь моя.\r\n'),
  (79, 'Поднимаю глаза и смотрю в небеса ', 'Поднимаю глаза и смотрю в небеса.\r\nТам я вижу Христа одесную Отца.\r\nНебо, небо небес приготовил Он мне,\r\nЦель моя и мечта – неба вся красота.\r\n\r\nПрипев:\r\nНебо, небо небес, город вечных чудес,\r\nЯ мечтаю о нем, там мечтаю и здесь.\r\nНебо, небо небес, путь туда через крест.\r\n\r\nЧтобы в небо попасть, нужно жизнь так прожить,\r\nЧтобы Бог мог сказать «Ты достоин в нем жить».\r\nТы достоин смотреть Иисусу в глаза,\r\nЕсли только войдешь ты к Нему в небеса.\r\n\r\nНе слыхал человек, не видал того глаз,\r\nКак там в небе небес, что там будет для нас.\r\nНо я знаю одно – красотой тех небес\r\nБудет тот, Кто понес на Голгофу наш крест.\r\n'),
  (80, 'Отче наш небесный!', 'Отче наш небесный! Да святится имя Твоё,\r\nГосподь, Твоё царство и Твоя воля,\r\nДа будут на земле, как на небе.\r\nОтче наш небесный! Да святится имя Твоё,\r\nГосподь, Твоё царство и Твоя воля,\r\nДа будут на земле, как на небе.\r\n\r\nПрипев:\r\nХлеб наш насущный дай на сей день,\r\nНе сокрой лицо Твоё нам\r\nГосподь, прости нам наши долги,\r\nКак и мы должникам.\r\n\r\nВраг жаждет искусить нас, но сохрани от зла.\r\nТвоя рука, Господь, сильна, Тебе лишь слава вечная!\r\nТвоему царству нет конца!\r\n'),
  (81, 'Помоги, Господь, не ссориться нам', 'Помоги, Господь, не ссориться нам.\r\nПомоги не делать больно друзьям.\r\nПомоги на зло добром отвечать,\r\nПомоги, где нужно, просто смолчать,\r\nПомоги стерпеть обиду и боль,\r\nЗа врагов к Тебе обращаться с мольбой.\r\nПомоги понять убогую грусть,\r\nЕсли в сердце жжет, к Тебе обращусь.\r\n\r\nПрипев:\r\nМне быть, как Ты, Господь, помоги,\r\nЖить для людей тепло нести.\r\nИ если друг упал в пути, опорой быть помоги.\r\n\r\nПомоги себя не ставить в пример,\r\nНа Тебя смотря, учиться и мне.\r\nНе решать, как хочет грещная плоть,\r\nТвой завет хранить в сердце, Господь.\r\n\r\nИ нужду людей к Тебе приносить,\r\nКаждый день за ближних с верой просить,\r\nне искать услады в жизни земной,\r\nА нести добро тем, кто рядом со мной.\r\n'),
  (82, 'Помоги, Господь, помоги', 'Помоги, Господь, помоги,\r\nУкрепи дух мой, укрепи.\r\n//В мире зла с Тобой не боюсь ничего;\r\nВ мире тьмы с Тобой мне светло.//\r\n\r\nОтойдет весь страх далеко,\r\nОт молитвы станет легко.\r\n//В мире зла с Тобой не боюсь ничего;\r\nВ мире тьмы с Тобой мне светло.//\r\n');
INSERT INTO `SONG` (`ID`, `DESCRIPTION`, `TEXT`) VALUES
  (83, 'Радуйся, мир! Господь грядет', 'Радуйся, мир! Господь грядет.\r\nЗемля, ликуй пред Ним!\r\nПрими скорей Царя царей\r\nИ пойте новый гимн\r\nИ пойте новый гимн\r\nИ пойте, пойте новый гимн.\r\n\r\nРадуйся, мир! Христос пришел.\r\nВ церквах поют о Нем.\r\nПоля, сады, леса, холмы\r\nИм вторят с торжеством.\r\nИм вторят с торжеством.\r\nИм вторят, вторят с торжеством.\r\n\r\nЧтоб грех не ослеплял народ,\r\nЧтоб терны не росли,\r\nОн жизнь дает, снимает гнет\r\nВо всех концах земли.\r\nВо всех концах земли.\r\nВо всех, во всех концах земли.\r\n\r\nПринес Он мир и благодать,\r\nЧтоб все могли познать,\r\nКак Бог велик и справедлив,\r\nКак нас Он возлюбил.\r\nКак нас Он возлюбил.\r\nКак нас, как нас Он возлюбил.\r\n'),
  (84, 'Он здесь, Он здесь', 'Он здесь, Он здесь,\r\nХочет встретится с тобой,\r\nОн здесь.\r\nПред Тобою, мой Господь.\r\nПреклоняются сердца,\r\nЗнаю Всемогущий Бог, что Ты здесь.\r\n\r\nОн здесь, Он здесь,\r\nХочет встретится с тобой,\r\nОн здесь.\r\nНежно глас Его звучит,\r\nДля тебя Он говорит,\r\nСвет любви Его святой всегда горит.\r\n\r\nОн здесь, Он здесь,\r\nХочет встретится с тобой,\r\nОн здесь.\r\nХочет встретится с тобой\r\nЧтобы дать душе покой,\r\nДвери сердца для Иисуса ты открой.\r\n'),
  (85, 'Пред Тобою, Отче, в мольбе стою', 'Пред Тобою, Отче, в мольбе стою\r\nУдали от сердца всю боль мою!\r\nТьма неверья, стены кругом встают,\r\nЖить по правде истинной не дают.\r\n\r\nПомоги мне, Господи! Помоги!\r\nБез Тебя не справлюсь я на пути.\r\nЧерез испытания все пройти\r\nПомоги мне. Господи, помоги!\r\n\r\nНе хочу иметь с миром ничего,\r\nНо хочу я, Господи, одного:\r\nЧтобы Ты всегда был моей звездой,\r\nИ руководил весь мой путь земной.\r\n\r\nЗнаю, что осталось немного мне\r\nПо тропинке узкой идти к стране,\r\nГде увижу город я неземной,\r\nГде навеки вечно я буду Твой.\r\n'),
  (86, 'Расставаясь, не будем печальны', 'Расставаясь, не будем печальны,\r\nРасставаясь, не будем грустить -\r\nМы еще повстречаемся с вами,\r\nБудем Бога об этом просить\r\n\r\nМы в общении с вами забыли Все печали,\r\nчто есть на Земле! Как хотелось,\r\nчтоб вместе мы были Не часы, а тысячу лет.\r\n\r\nПусть дороги все будут открыты,\r\nПусть открытыми будут сердца,\r\nНикогда пусть не будут забыты\r\nНа Земле всех людей адреса\r\n\r\nМы еще повстречаемся с вами,\r\nДень тот будет бесспорно красив,\r\nПусть исполнится наше желание -\r\nБудет Бога об этом просить.\r\n'),
  (87, 'Солнце простилось с землею', 'Солнце простилось с землею,\r\nСолнце сказало: \"До встречи!\".\r\nС первой на небе звездою\r\nТихо пришел к нам вечер.\r\n\r\nПрипев:\r\nТам, в вышине небес Ангелы славят Его,\r\nА мы здесь на земле Славим Господню любовь.\r\n\r\nСумрачней стало и глуше,\r\nПтицы заснули в гнездах.\r\nНе уходи, послушай,\r\nКак напевают звезды.\r\n\r\nГимн их торжественно - плавных -\r\nПеснь Сотворившему все.\r\nСлава Христу, слава,-\r\nпусть воспевают все.\r\n\r\nЕсли тревожит душу\r\nМысль: \"Для чего я создан?\" -\r\nНе уходи послушай:\r\nСлавят Создателя звезды.\r\n\r\nГолос свой влей в их пенье,\r\nВсем о любви скажи,\r\nЧтоб к небе6сам стремленье\r\nВ души людей вложить.\r\n'),
  (88, 'Сердцем, устами, умом и душой', 'Сердцем, устами, умом и душой\r\nБуду хвалить я Тебя, Бог благой.\r\nЗдесь, на земле, среди жизни земной,\r\nПосле, на небе, где встречусь с Тобой.\r\n\r\nПрипев:\r\nАллилуйя! Аллилуйя!\r\n\r\nБуду я вечно Иисуса хвалить,\r\nПесней, делами о Нём говорить.\r\nОн утром, вечером будет со мной,\r\nС первой на небе потухшей звездой.\r\n\r\nПрипев:\r\nАллилуйя! Аллилуйя!\r\nАллилуйя кровь Христа,\r\nАллилуйя нас спасла.\r\nАллилуйя! Аллилуйя!\r\n\r\nАлым рассветом воскресного дня\r\nПеред Тобою склоняюсь и я.\r\nЗдесь, на земле, среди жизни земной,\r\nПосле, на небе, где встречусь с Тобой.\r\n\r\nПрипев:\r\nАллилуйя! Аллилуйя!\r\n\r\nПусть наша жизнь будет песней хвалы\r\nС ранней весны до холодной зимы.\r\nС детства и юности, с первой любви,\r\nВ зрелости, в старости, до седины.\r\n\r\nПрипев:\r\nАллилуйя! Аллилуйя!\r\nАллилуйя кровь Христа,\r\nАллилуйя нас спасла.\r\nАллилуйя! Аллилуйя!\r\n'),
  (89, 'Сколько б ни было в мире путей', 'Сколько б ни было в мире путей,\r\nСколько б ни было в мире дорог,\r\nНет дороги прекрасней чем Бог\r\nПриготовил в заботе Своей.\r\nЛишь со Христом сможешь ты счастлив быть,\r\nИ только Он один тебя научит жить.\r\nСлышишь ли, плачет твоя душа,\r\nЖизнь не такая ей здесь на земле нужна.\r\n\r\nСколько б ни было здесь у тебя\r\nДорогих и любимых друзей,\r\nНо случится с тобою беда —\r\nНе поможет никто тебе в ней.\r\nЛишь Христос — Друг самый дорогой,\r\nСправиться сможет Он с самой большой бедой.\r\nСлышишь ли, плачет твоя душа,\r\nЖизнь не такая ей здесь на земле нужна.\r\n\r\nТак подумай, о друг, ты теперь,\r\nПо какой бы дороге пойти,\r\nНе закрыта пока еще дверь,\r\nДверь спасенья в земном пути.\r\nО, приди ты ко Христу теперь,\r\nМир и покой найдешь в нем для души своей.\r\nЛишь со Христом сможешь ты счастлив быть,\r\nИ только Он один тебя научит жить.\r\nСлышишь ли, плачет твоя душа,\r\nЖизнь не такая ей здесь на земле нужна.\r\nСлышишь ли, плачет твоя душа,\r\nЖизнь не такая ей здесь на земле нужна.\r\n'),
  (90, 'Рождество - это праздник света', 'Рождество - это праздник света,\r\nДень рожденье большой любви.\r\nЭто с неба отзвук привета,\r\nЭто праздник для каждой семьи.\r\n\r\nПрипев:\r\nРождество - это воля Бога это новая в небе звезда,\r\nК жизни вечной прямая дорога, это хлеб и живая вода\r\nРождество - это воля Бога это новая в небе звезда,\r\nК жизни вечной прямая дорога, это хлеб и живая вода\r\n\r\nРождество - это праздник мира,\r\nПримирение людей с Творцом,\r\nПусть сердца открываются шире,\r\nВ этот праздничный день пред Христом.\r\n'),
  (91, 'Словно травы, шелестят слова', 'Словно травы, шелестят слова…\r\nКак слова и травы в жизни схожи!\r\nГоворят: целебная трава -\r\nЕсть слова целительные тоже.\r\n\r\nПрипев:\r\nЯ смотрю на луг, в цветы одетый,\r\nКак прекрасен сочных трав покров…\r\nНаучи нас, Бог, дарить букеты,\r\nСостоящие из добрых, мудрых слов\r\n\r\nЕсть слова, исполненные яда,\r\nЕсть - простые, словно васильки.\r\nНо порой с собою нет услады,\r\nЕсли в речи всходят сорняки.\r\n\r\nСловом можно душу искалечить,\r\nСловом можно раны исцелить.\r\nТяжкий груз недобрых слов на плечи\r\nБерегитесь ближнему взвалить.\r\n'),
  (92, 'Снова по небу скучаю я', 'Снова по небу скучаю я,\r\nНо знаю, Ты видишь, что в сердце моём.\r\nЧасто в мольбе призываю я\r\n\"Останься со мною. Введи в Отчий дом\"\r\n\r\nПрипев:\r\nОткликнется душа моя на зов Небесного Отца\r\nИ голос долгожданный, я услышу, наконец.\r\nПо-новому увижу я, тот мир, что окружал меня\r\nИ в сердце зазвучит тогда, мелодия небес...\r\n\r\nРуки Твои берегут меня –\r\nМеня направляют, упасть не дают\r\nОтче, святая любовь Твоя\r\nОткрыла мне двери в обитель Твою.\r\n'),
  (93, 'Снова я с Тобой наедине', 'Снова я с Тобой наедине,\r\nПосле шума и забот дневных.\r\nЧто Ты видишь Иисус во мне?\r\nЧто Ты скажешь о делах моих?\r\nСтыдно мне глаза свои поднять;\r\nЧто я перед святостью Твоей?\r\nИ шепчу я с трепетом опять:\r\n«Дай хоть крошечку мне милости Твоей».\r\n\r\nПрипев:\r\nВспоминая улетевший день,\r\nСознавая духа нищету,\r\nРавнодушие своё и лень,\r\nИ ошибки все Тебе несу.\r\n\r\nУ источника живой воды\r\nЯ склоняюсь, чтобы пить и пить,\r\nИисус, один лишь только Ты\r\nМожешь жажду сердца утолить.\r\nКак Ты близок в этот час ночной!\r\nКак приятно слышать голос Твой,\r\nИ когда в вечерней тишине\r\nТы даришь душе моей покой.\r\n\r\nТолько кровь Твоя открыла мне\r\nПуть к истокам милости Твоей;\r\nЯ к ногам Твоим спешу прильнуть,\r\nТы прости меня и пожалей.\r\n'),
  (94, 'Стая лебедей над хрустальным озером', 'Стая лебедей над хрустальным озером,\r\nОкрашенном в закате дня цветом розовым.\r\nСтая лебедей уплывает в даль небес,\r\nОставляя позади зеленый лес.\r\n\r\nПрипев:\r\nРозовые лебеди, вы уносите мою мечту о небесном городе,\r\nГде Спаситель ждет меня. Там буду я!\r\n\r\nКак хотелось бы улететь скорей домой,\r\nНо в лебединой песне слышу я:\"Трудись и будь готов!\"\r\nСкоро уж придет на землю к нам Спаситель наш\r\nИ кто будет готов, с Ним уйдет тотчас.\r\n'),
  (95, 'С небес на горы и моря', 'С небес на горы и моря река любви течёт Твоя,\r\nв сердце двери распахну, в Тебе свободу обрету,\r\nЯ счастлив в правде пребывать\r\nи руки к небу поднимать,\r\nЛишь снизойдёт любовь Твоя, Господь, я буду\r\n\r\nПрипев:\r\nО любви Твоей петь всегда\r\nЛюбви Твоей петь всегда\r\nПеред Тобой ликую- мне радости не скрыть.\r\nЯ знаю- пред Тобою будут все ликовать, и я буду!\r\n'),
  (96, 'Мы идём не одни', 'Мы идём не одни,\r\nВедёт нас к жизни Бог любви.\r\nОдной семьёй мы стали во Христе.\r\nПусть мир любовь увидит в полноте.\r\nБудем вместе до конца,\r\nГорят любовью в нас сердца.\r\nДай руку мне, пусть мир увидит вновь\r\nКак велика Христа любовь.\r\n\r\nПрипев:\r\nМы во Христе—одна семья,\r\nЕго любовь здесь вижу я.\r\nМы вместе можем много,\r\nВозлюбим же друг друга,\r\nМы во Христе—одна семья!\r\n\r\nИдём дорогой в небеса,\r\nХоть труден путь, но до конца\r\nС тобой пройдём, ведь с нами наш Отец,\r\nНаш Бог и Царь, покой наших сердец.\r\nСвоею Кровью съединил\r\nИ нас любовью сроднил;\r\nДай руку мне, пусть мир увидит вновь\r\nКак велика Его любовь.\r\n'),
  (97, 'Слава Богу за все даяния', 'Слава Богу за все даяния,\r\nСлава Богу за все страдания,\r\nСлава Богу за ночи мрачные,\r\nДни удачные и неудачные.\r\n\r\nПрипев:\r\nСлава Богу, за все Ему слава,\r\nОн имеет на это право!\r\n\r\nСлава Богу за дни погожие,\r\nЗа характеры наши несхожие,\r\nЗа дороги тяжелые, дальние,\r\nЗа разлуки, всегда печальные.\r\n\r\nСлава Богу за слезы с песнями.\r\nЧто Он тропками вел чудесными.\r\nСлава Богу за многие милости.\r\nЧто помог христианами вырасти.\r\n\r\nЧто Он путь нам всегда указывал,\r\nЧто не больно Он нас наказывал,\r\nЧто прощал, забывая прошлое.\r\nЧто давал Он нам только хорошее!\r\n'),
  (98, 'Приходи почаще на Голгофу', 'Приходи почаще на Голгофу -\r\nЗдесь берет начало жизнь твоя,\r\nЗа грехопаденья катастрофу\r\nКрови жертвенной течет струя\r\nКровь из ран Спасителя струилась,\r\nБожий Сын, страдая, умирал.\r\nНа Голгофе в небо дверь открылась,\r\nБог в любви спасенье людям дал.\r\n\r\nПусть твоя житейская тропинка\r\nНе обходит старого креста,\r\nЧтоб душа, как детская слезинка,\r\nОт порока стала здесь чиста.\r\nСчастье вдалеке искать не надо -\r\nПоспеши сюда, и ты поймешь:\r\nЗдесь оно совсем с тобою рядом,\r\nУ креста источник обретешь.\r\n\r\nЗдесь - мечты и чаянья людские,\r\nЗдесь - надежда светлая веков,\r\nВо Христе, Чьи руки дорогие\r\nМир спасли от гибельных оков.\r\nПусть прошли века, стирая даты,\r\nНо Спаситель хочет вновь сказать:\r\n\"Искупленье, данное без платы,\r\nДруг мой, опасайся потерять\".\r\n'),
  (99, 'Был момент в моей жизни', 'Был момент в моей жизни, когда задумался\r\nЗачем я живу здесь, в этом мире?\r\nК чему вся жизнь моя?\r\nКак мне жить счастливой жизнью?\r\nГде мне взять ответ?\r\nКак мне быть, и кто поможет мне?\r\nЛишь Ты  нашёл меня,\r\nПривёл сюда и теперь\r\n\r\nПрипев:\r\nЯ буду здесь всегда, это мой Дом\r\nЗдесь я нашёл всё, что искал\r\nНикогда, никто не уведёт\r\nМой взгляд от Тебя.\r\n\r\nМожет быть, было сложно в себе всё изменить\r\nТеперь я навечно в Доме Божьем\r\nПроведу всю жизнь, Буду петь я песни эти,\r\nСлавить и хвалить Лишь Тебе открою сердце\r\nЗнаю и верю: Ты спас меня, привёл сюда и теперь\r\n\r\nВсем сердцем я люблю Тебя, Тебя, Бог\r\nМоя душа в Твоих руках сегодня и всегда\r\nНе оставлю Божий Дом я никогда, я никогда\r\nБуду жить навеки в нём я навсегда, я навсегда.\r\n'),
  (100, 'Господи, зову Тебя', 'Господи, зову Тебя,\r\nСкорее приди, к Тебе я воззвал.\r\nГолос мой услышь, когда\r\nК Тебе взываю я.\r\n\r\nПрипев:\r\nДа будет молитва моя для Тебя -\r\nБлаговонием.\r\nИ жертвой вечерней пусть будет\r\nМолитва моя.\r\n\r\nГлаза мои устремлены к Тебе.\r\nГосподь, в Тебе спасенье мое.\r\nНе дай попасть в капканы мне,\r\nОт зла храни меня.\r\n'),
  (101, 'Медленно кружится снег', 'Медленно кружится снег\r\nА вокруг тишина.\r\nСнова встречаем мы все\r\nРадостный день Рождества.\r\n\r\nБог пришел людям дать\r\nМир земле, благодать.\r\nГромче пусть песня звучит,\r\nК яслям, душа, поспеши.\r\n\r\nО, поклонись же Ему,\r\nИисусу Христу.\r\nМир даст душе Он твоей\r\nБог мира, Царь всех царей.\r\n'),
  (102, 'Сумерки сгущаются за твоим окном', 'Сумерки сгущаются за твоим окном,\r\nЗвёзды зажигаются в небе голубом.\r\nСветят в отдалении огоньки селения,\r\nВетерок доносит аромат цветов.\r\n\r\nСпи, дитя любимое, баюшки баю,\r\nПесню колыбельную я тебе спою.\r\nПусть она запомнится добрым наставлением,\r\nПредостережением на всю жизнь твою.\r\n\r\nГоды беззаботные быстро пролетят,\r\nПервые невзгоды несмело постучат.\r\nВ жизни встретишь разное, — и печаль и радости,\r\nДумы безотрадные затуманят взгляд.\r\n\r\nЕсли хочешь радостным и счастливым быть,\r\nПостарайся Господу сердце подарить.\r\nИ тогда о прожитом, обо всём, что пройдено\r\nНе придётся больше никогда тужить.\r\n\r\nВ жизни бесконечное множество путей,\r\nМного в ней наставников и учителей.\r\nЛишь в Христе не встретишь ты разочарования,\r\nНе грозят блуждения с Ним душе твоей.\r\n'),
  (103, 'Благодарю Тебя, Спаситель мой', 'Благодарю Тебя, Спаситель мой\r\nЧто к истине меня Ты пробудил.\r\nБлагодарю, что скорбный путь земной\r\nНебесным светом чудно озарил,\r\nБлагодарю, что скорбный путь земной\r\nНебесным светом чудно озарил.\r\n\r\nСвободу чтобы дать рабам греха,\r\nВ смиренье встретил Ты Голгофский час.\r\nЯ пред Тобой, Господь, в долгу всегда,\r\nПо вере получив спасенья часть,\r\nЯ пред Тобой, Господь, в долгу всегда,\r\nПо вере получив спасенья часть.\r\n\r\nЗа нежную траву, за майский дождь,\r\nЗа щедрый урожай, за дар садов,\r\nЗа облака вдали и в поле рожь\r\nБлагодарю Тебя, Творец миров,\r\nЗа облака вдали и в поле рожь\r\nБлагодарю Тебя, Творец миров.\r\n\r\nС признательность верю и дышу,\r\nНо скоро над землей я воспарю...\r\nИ первое, что я Тебе скажу:\r\n\"Благодарю, мой Бог, благодарю\".\r\nИ первое, что я Тебе скажу:\r\n\"Благодарю, мой Бог, благодарю\".\r\n'),
  (104, 'Твоя любовь границ не знает', 'Твоя любовь границ не знает\r\nИ ярче солнца свет Твой мне,\r\nВ Твоём общении отдыхаю,\r\nТебя люблю, Господь, пою Тебе:\r\n\r\nПрипев:\r\nГосподь мой, я Тебя прославляю,\r\nГосподь мой, Ты спаситель души моей,\r\nГосподь мой, я Тебя воспеваю,\r\nГосподь мой, Ты достоин хвалы, Ты достоин хвалы.\r\n\r\nВся жизнь моя в Тебе сокрыта,\r\nЯ льну к Тебе, мой добрый Бог.\r\nТвоим теплом душа согрета,\r\nМне хорошо с Тобой, навек я Твой.\r\n'),
  (105, 'Счастье - жизнь свою отдать Христу', 'Счастье - жизнь свою отдать Христу.\r\nСчастье - жизнь наполнить Божьим словом.\r\nБез Тебя, Господь, я не могу\r\nСбросить жизни тленной все оковы.\r\n\r\nПрипев:\r\nЛишь у Тебя найду покой.\r\nЛишь у Тебя, мой Бог святой,\r\nИсточник жизни для души,\r\nСпасенье вечное от тьмы.\r\n\r\nСчастье - за Христом идти повсюду,\r\nНесмотря на горести скорбей.\r\nЛишь с Тобой, Господь, я сильным буду.\r\nО, прошу Тебя, приди скорей.\r\n\r\nСчастье - встретить Господа на небе.\r\nСчастье - в церкви Божьей пребывать.\r\nПомоги, Господь мой, где б я не был,\r\nСвет Твой вечный в мире отражать.\r\n'),
  (106, 'Бог тебя простил, о новый человек', 'Бог тебя простил, о новый человек!\r\nБог тебя простил и Он с тобой вовек!\r\nБог тебя простил, твою жизнь Он обновил,\r\nРожденный Духом Божьим, новый человек!\r\n\r\nБог тебя принял, о новый человек!\r\nБог тебя принял и Он с тобой вовек!\r\nБог тебя принял, Свою жизнь за тебя отдал,\r\nРожденный Духом Божьим, новый человек!\r\n\r\nПрославь Господа, о новый человек!\r\nПрославь Господа ибо Он с тобой вовек!\r\nПрославь Господа и оставь мир навсегда,\r\nРожденный Духом Божьим, новый человек!\r\n'),
  (107, 'Тихая ночь, дивная ночь', 'Тихая ночь, дивная ночь!\r\nМир земной, сон сковал\r\nВ Вифлееме покой, тишина,\r\nЛишь не спит там святая чета:\r\nЧудный Младенец рождён,\r\nЧудный Младенец рождён.\r\n\r\nТихая ночь, дивная ночь!\r\nЛишь звезда в небесах\r\nЯрким светом над миром горит\r\nИ живущим она говорит:\r\n«Свыше вас Бог посетил» (2р. )\r\n\r\nТихая ночь дивная ночь!\r\nГрешным всем принесла\r\nРадость вечную, мир от Отца\r\nИ восторгом горят все сердца -\r\nНыне Спаситель нам дан.\r\nНыне Спаситель нам дан.\r\n\r\nТихая ночь, дивная ночь!\r\nМир земной, сон сковал\r\nВ Вифлееме покой, тишина,\r\nЛишь не спит там святая чета:\r\nЧудный Младенец рождён,\r\nЧудный Младенец рождён.\r\n'),
  (108, 'Ты - оплот моего спасенья', 'Ты - оплот моего спасенья,\r\nТы - сила жизни моей,\r\nНадежду даешь Ты и вдохновенье,\r\nВзываю к Тебе я, Господь!\r\nВерю я в Тебя, верю я и\r\nлюблю Господь Тебя.\r\nПомощь Ты всегда, спасенье,\r\nК Тебе, мой Иисус, я иду.\r\n'),
  (109, 'Создатель всей вселенной', 'Создатель всей вселенной, всемогущий Бог,\r\nИ жизнь мою другой лишь только Ты соделать смог.\r\nЯ петь хочу о том, что подарил мне Ты,\r\nО той любви, что бесконечна как и Ты.\r\n\r\nПрипев:\r\nТебе, Господь, хвалу я вознесу,\r\nПеред Тобой я голову склоню.\r\nТебе Господь, любовь и жизнь моя,\r\nПрими молитву эту от меня.\r\n\r\nСоздатель всей вселенной, всемогущий Бог,\r\nСреди ночи Ты мне увидеть свет помог.\r\nС тех пор как я узнал, Твою любовь ко мне,\r\nХочу служить всем сердцем лишь Тебе.\r\n'),
  (110, 'Ты Господи везде вокруг меня', 'Ты Господи везде вокруг меня\r\nИ спереди и сзади.\r\nИ на мне Твоя Рука, на мне Твоя Рука.\r\nНамного лучше знаешь Ты меня,\r\nЧем сам могу познать я.\r\nОт Духа Твоего куда могу уйти, куда.\r\n\r\nПрипев:\r\nКуда от Твоего Лица мне скрыться:\r\nЯ в Небеса пойду - Ты в Небесах\r\nИ даже под землёй мне не укрыться,\r\nЗа дальнею зарей умчусь -- Ты там.\r\nЗа дальними морями не укроюсь,\r\nИ там найдет меня Рука Твоя.\r\nИ если в темноте ночной сокроюсь,\r\nТы видишь всё равно, как днём меня.\r\n\r\nМеня Ты знал от самого созданья,\r\nКогда еще ничей не видел глаз,\r\nСлубовью Ты творил и пониманьем\r\nВсю внутренность мою и внешность враз.\r\n\r\nИ в замыслах Твоих я от начала,\r\nТы самый первый думал обо мне,\r\nИ в жизни нет такой детали,\r\nЧтоб не подвластная была Тебе.\r\n'),
  (111, 'Учитель мой, не оставляй меня', 'Учитель мой, не оставляй меня,\r\nТы дал мне жизнь, ведь я – Твоё творенье,\r\nТы дал мне искорку сердечного огня,\r\nДуше моей послал освобожденье.\r\n\r\nУчитель мой, не оставляй меня,\r\nВеди Своей могучею рукою,\r\nТы знаешь всё, и пусть душа моя\r\nСогрета будет лишь Твоей любовью.\r\n\r\nУчитель мой, не оставляй меня,\r\nВедь я так слаб, и мне нужна опора.\r\nИ лишь с Тобой могу я устоять\r\nВ борьбе с волнами жизненного моря.\r\n\r\nМой Иисус, преобрази меня\r\nВ Твой образ чудный, кроткий и смиренный,\r\nВеди меня в лазурные края,\r\nВ тот сонм святых и в город тот нетленный.\r\n'),
  (112, 'Юность светлая, весна ранняя', 'Юность светлая, весна ранняя,\r\nМы ее Творцу принесем.\r\nМолодые мы в золотые дни,\r\nЛишь для Господа живем.\r\nМолодые мы в золотые дни,\r\nЛишь для Господа живем.\r\n\r\nТьма неверия окружает нас,\r\nТуча черная впереди.\r\nМы Христа огни,\r\nВ эти злые дни\r\nРасставляем на пути.\r\nМы Христа огни,\r\nВ эти злые дни\r\nРасставляем на пути.\r\n\r\nХоть и труден путь,\r\nИ тернистый он,\r\nСо Христом его мы пройдем.\r\nМолодые мы в золотые дни,\r\nЛишь для Господа живем.\r\nМолодые мы в золотые дни,\r\nЛишь для Господа живем.\r\n\r\nТак спеши же друг\r\nПосвятить Христу\r\nУтро юности, жизни дни.\r\nИ Он даст тебе золотой венец,\r\nБудешь царствовать ты с Ним.\r\nИ Он даст тебе золотой венец,\r\nБудешь царствовать ты с Ним.\r\n'),
  (113, 'Хвали Творца, лишь день начнется', 'Хвали Творца, лишь день начнется,\r\nХвали, когда земля проснется,\r\nС восходом солнечным хвали —\r\nХвалу приемлет Бог любви.\r\n\r\nПрипев:\r\nХвала Творцу земли, Хвала Творцу небес,\r\nХвала Тебе за все, Великий Бог Отец!\r\nПрими хвалу сердец За Сына и за крест,\r\nЗа щедрый Дух Святой, За милость и покой.\r\n\r\nХвали Творца за все созданье,\r\nЗа пенье птиц, их щебетанье,\r\nС их общим хором слей свой дух.\r\nБог близок, Божий всюду слух.\r\n\r\nХвали Творца, как полдень светит,\r\nХвали и ночью, Он заметит.\r\nИ в ночь, и в день твой чистый дар-\r\nМолитвы шепот, сердца жар.\r\n'),
  (114, 'Жизнь посвящаю Тебе', 'Жизнь посвящаю Тебе,\r\nИ отдаюсь я в мольбе.\r\nТы управляй ею Сам,\r\nИ направляй к небесам.\r\nЖизнь эта – не моя,\r\nТак будет пусть Твоя,\r\nЕё Ты Сам мне дал,\r\nЧтоб я Тебя прославлял.\r\n\r\nМир искушеньями полн,\r\nХочет увлечь меня он,\r\nЧтобы ему я служил\r\nИ по-греховному жил.\r\nНо я Тебя молю:\r\nСилу пошли Свою\r\nИ мне защиту дай,\r\nВ битве с грехом укрепляй.\r\n\r\nЧестно желаю я жить,\r\nБлижних хочу всех любить,\r\nЗло и обиды прощать\r\nИ о Тебе возвещать.\r\nО мой Господь и Бог,\r\nЕсли б я в жизни смог\r\nТвою любовь познать\r\nИ всем о ней рассказать!\r\n\r\nСкоро Ты в славе придешь\r\nИ Свою Церковь возьмешь.\r\nТак приготовь и меня,\r\nЧтобы достоин был я\r\nВечно с Тобой пребыть,\r\nЗлобу людей забыть\r\nИ в небе увидать\r\nТо, что Ты хочешь нам дать.\r\n'),
  (115, 'Тебе хочу я воспеть «Аллилуйя»', 'Тебе хочу я воспеть «Аллилуйя»,\r\nО, мой Господь, я Тобою дышу,\r\nБлагодарю я Тебя за спасенье,\r\nИ «Аллилуйя» пою.\r\n\r\nЯ так стремлюсь удостоиться чести\r\nВечно с Тобой славить имя Твое,\r\nЧашу страданий с Тобой разделяю,\r\nИ «Аллилуйя» пою.\r\n\r\nО, мой Господь, Ты утешь меня в скорби,\r\nМир окружил меня мрачный и злой.\r\nДай Духа сил, я Тебя умоляю, И «Аллилуйя» пою.\r\n\r\nСила Твоя даже смерть победила,\r\nТвоей любовью я сердце смирил,\r\nВечный мой Бог, я Тебя прославляю,\r\nИ «Аллилуйя» пою.\r\n'),
  (116, 'Встань, слава с небес приходит', 'Встань, слава с небес приходит,\r\nВстань, время пришло сердца отдать.\r\nВстань, ты сотворён, чтоб славить,\r\nВстань, перед тобой Твой Бог сейчас,\r\nВстань.\r\n\r\nПрипев:\r\nОднажды мир узнает, что Ты Бог богов,\r\nCклонится пред Тобою земля,\r\nВсе Твои богатства принадлежат\r\nТем, кто избрал Тебя.\r\nВстань.\r\n'),
  (117, 'Я в бессмертье иду, наступая на зло', 'Я в бессмертье иду, наступая на зло,\r\nИз скорбей и страданий дорога,\r\nНа душе у меня и тепло, и светло,\r\nПотому что я верую в Бога.\r\n\r\nЗнаю я, что мне можно и что мне нельзя.\r\nЗнаю, что хорошо и что плохо.\r\nЗнаю, что на земле существую не зря,\r\nПотому что я верую в Бога.\r\n\r\nПротив истины войско ведет сатана.\r\nДухи зла ополчились, как звери.\r\nЖдет меня впереди неземная страна,\r\nПотому что я в Господа верю.\r\n\r\nДаже если встречаю следы катастроф,\r\nИсчезает из сердца тревога,\r\nКак подумаю я, что есть любящий Бог,\r\nТо я счастлив, что верую в Бога.\r\n\r\nНет от радости слов – очень мне повезло.\r\nМною найдена в небо дорога.\r\nНа душе у меня и тепло и светло,\r\nПотому что я верую в Бога.\r\n'),
  (118, 'Дни, как звук, отшумят', 'Дни, как звук, отшумят и исчезнут вдали,\r\nВ синем небе летят над землёй журавли.\r\nВ журавлином пути быстрый бег твоих лет.\r\nИ оставишь ли ты, и оставишь ли ты на земле добрый след.\r\nВ журавлином пути быстрый бег твоих лет.\r\nИ оставишь ли ты, и оставишь ли ты на земле добрый след.\r\n\r\nКак пройти этот путь, чтобы что-то отдать,\r\nлюдям счастье вернуть, веру не потерять.\r\nПусть же юность твоя ярким светом горит,\r\nИ крупицы добра, и крупицы добра, людям щедро дарит.\r\nПусть же юность твоя ярким светом горит,\r\nИ крупицы добра, и крупицы добра, людям щедро дарит.\r\n'),
  (119, 'Я пойду за Тобой', 'Я пойду за Тобой Куда бы Ты ни повел меня\r\nЯ исполню всё Для чего Ты меня призвал\r\nМоя радость в Тебе. Всю свою жизнь я Тебе отдал\r\nМои слезы и боль, Поглотила Твоя любовь\r\n\r\nПрипев:\r\nМой Бог, мой Бог - большая скала\r\nСтена из камня, защита моя\r\nСпасенье мое, крепость жизни моей\r\nКого мне страшиться теперь?\r\n'),
  (120, 'Я избрал путь христианский', 'Я избрал путь христианский,\r\nПуть тернистый и святой\r\nИ иду тропою узкой в вечность,\r\nГде найду покой.\r\n\r\nПрипев:\r\nХристианская надежда,\r\nТы влечешь меня вперед,\r\nХристианская надежда,\r\nСамый светлый огонек.\r\n\r\nРадость сердце окрыляет,\r\nХоть дорога тяжела,\r\nС неба помощь обещает\r\nВеры твердая рука.\r\n\r\nЦарство Божье наполняет\r\nСердца хижину мою.\r\nХоть и слаб, Бог помогает,\r\nВерю: к цели я дойду.\r\n\r\nБуду вечно с Иисусом,\r\nУ груди Его лежать.\r\nСвоим духом с Ним сольюсь я.\r\nЧтобы с Богом обитать.\r\n'),
  (121, 'Я слышу зов любви', 'Я слышу зов любви, на крест Христа гляжу.\r\nИ в жертве праведной крови мир к сердцу нахожу.\r\n\r\nПрипев:\r\nЛюбовь Христа превыше дальних звезд с небес,\r\nОна обьемлет всех людей земли.\r\nЛюбовь Христа спасла от гибели\r\nВсех нас, дала покой и мир в душе моей .\r\n\r\nНад мною облака проходят в вышине\r\nЕго любовь всегда сильна не изменяет мне.\r\n\r\nЯ в вере слаб порой в делах не совершен,\r\nно мир Его всегда со мной и нет в Нем перемен.\r\n\r\nИзменчив я, но Он ко мне все так же благ,\r\nя знаю Он всегда со мной во всех моих путях.\r\n'),
  (122, 'В нашей жизни встречи', 'В нашей жизни встречи, расставанья.\r\nГрусть и радость тесно сплетены.\r\nПосле смеха - горькие рыданья,\r\nПосле слез - дыханье тишины.\r\nНе всегда нам в жизни все понятно:\r\nТо, что горем кажется порой,\r\nОбернется счастьем необъятным\r\nВдруг с неуловимой быстротой.\r\nОбернется счастьем необъятным\r\nВдруг с неуловимой быстротой.\r\n\r\nИногда мы слезы льем напрасно,\r\nОттого что будущность во мгле.\r\nКажется тяжелой и опасной\r\nЖизнь на этой суетной земле.\r\nЛет дано для жизни семь десятков,\r\nВосемь - коль здоровьем мы сильны;\r\nТруд, болезнь, заботы, неполадки -\r\nМы летим к вратам иной страны.\r\nТруд, болезнь, заботы, неполадки -\r\nМы летим к вратам иной страны.\r\n\r\nМы идем туда, где нет страданья,\r\nСтраха нет и нет житейских гроз;\r\nСветит радость благостным сияньем\r\nВ той стране, где все во всем Христос.\r\nСтоит для Спасителя трудиться\r\nИ за правду Божью пострадать.\r\nЧтоб навеки с Ним соединиться\r\nИ хвалу Спасителю воздать.\r\nЧтоб навеки с Ним соединиться\r\nИ хвалу Спасителю воздать.\r\n'),
  (123, 'Я пред Тобой становлюсь', 'Я пред Тобой становлюсь на колени\r\nОтче Небесный Отец во Вселенной\r\nРади Иисуса ради Христа\r\nГосподи помилуй Ты меня\r\n\r\nПрипев:\r\nБудь всегда со мною рядом\r\nОкружи меня любовью Своей\r\nНичего мне не надо\r\nПросто будь всегда рядом\r\nИ холодное сердце согрей\r\n\r\nПод куполами небес мы лишь птицы\r\nМолит душа в небо к Богу стремится\r\nТы мой Хранитель от зла и тревог\r\nГосподи мой Царь и мой Бог\r\n\r\nВерю Господь я в Твое воскресенье\r\nВерю даруешь второе рожденье\r\nЯ пред Тобою в молитве склонюсь\r\nГосподи Тебе я молюсь.\r\n'),
  (124, 'Я хочу за Тобою идти', 'Я хочу за Тобою идти,\r\nМой Спаситель, куда б Ты не вёл\r\nПомоги крест тяжелый нести\r\nЧтоб без ропота с радостью шёл.\r\nМне легко за Тобою идти\r\nКогда всё улыбается мне\r\nНо коль скорби придут, помоги\r\nМне дойти к той небесной стране.\r\n\r\nПрипев:\r\nКровь Твоя меня омот грех теперь не для меня.\r\nКровь Твоя меня спасёт от ада вечного огня.\r\nКровь Твоя меня омоет грех теперь не для меня\r\nКровь Твоя меня спасёт...\r\n\r\nПоложиться мне дай на Тебя\r\nТвёрдость веры во мне сохрани.\r\nТы страдал на кресте за меня\r\nНужно мне часть страданий нести.\r\nНо ведь я человек без Тебя\r\nНету сил, я прах пред Тобой\r\nВся надежда моя на Тебя\r\nТолько Ты моя радость, покой\r\n'),
  (125, 'Я словно странник земной', 'Я словно странник земной,\r\nПуть мой лежит в край родной,\r\nВера, надежда, любовь, рядом идут со мной.\r\nЯркой звездой надо мною горит\r\nГород чудесный Иерусалим,\r\nЭто любовь Твоя зовет меня.\r\n\r\nПрипев:\r\nВ небесный дом иду за Тобой,\r\nВеди меня Своею рукой,\r\nТвоя любовь зовет меня в край\r\nНеземной, неземной, неземной. ...\r\n\r\nТам, где царит тепло,\r\nТам, где нет горя и слез,\r\nВ радости вечного дня знаю, Ты ждешь меня.\r\nВ славе небесной, в величьи святом,\r\nАгнец воскресший Спаситель Христос,\r\nЭто любовь Твоя зовет меня.\r\n\r\nЯ словно странник земной,\r\nПуть мой лежит в край родной,\r\nКаждое слово Твое в сердце моем живет.\r\nО, мой Господь, через ночи и дни,\r\nВ город Чудесный меня Ты веди.\r\n'),
  (126, 'Аллилуйя! Аллилуйя!', 'Аллилуйя! Аллилуйя!\r\nБуду славить я Того, Кто всех дороже\r\nБуду славить я Царя здесь на земле и в небесах.\r\nАллилуйя! Аллилуйя!\r\n\r\nАллилуйя! Аллилуйя!\r\nОн  пролил за всех нас кровь Свою святу\r\nИ за это благодарность все Ему мы воспоем.\r\nАллилуйя! Аллилуйя!\r\n\r\nАллилуйя! Аллилуйя!\r\nБуду петь от всей души Тебе, Спаситель.\r\nИисус, благодарю Тебя за дар любви святой,\r\nЗа прощенье, за спасенье.\r\nЭто любовь Твоя зовет меня.\r\n'),
  (127, 'Ангелы в небе Господа славят', 'Ангелы в небе Господа славят,\r\nСлаву достойную Богу поют, Богу поют:\r\nВечная слава, вечная слава, вечная слава,\r\nСлава Христу, слава Христу!\r\nВечная слава, вечная слава, вечная слава,\r\nСлава Христу, слава Христу!\r\n\r\nХочется с ангельским пением слиться,\r\nВ хоре искупленных, спасённых Господом.\r\nИ сердце Господу, Богу единому\r\nХристу Спасителю славу поёт, славу поёт:\r\nВечная слава, вечная слава, вечная слава,\r\nСлава Христу!\r\n\r\nЗдесь на земле людей Церковь томится\r\nК нему стремится, дух к жениху влечёт.\r\nО, гряди, Господи, видишь, невеста ждёт\r\nТебе единому славу поёт, славу поёт:\r\nВечная слава, вечная слава, вечная слава,\r\nСлава Христу!\r\n\r\nЧудный, прославленный, Царственный Божий Сын,\r\nДивный Христос Господь\r\nВ небе нас ждёт, в небе нас ждёт.\r\nИ сердце Господу, Богу Единому\r\nХристу Спасителю славу поёт, славу поёт:\r\nВечная слава, вечная слава, вечная слава,\r\nСлава Христу, слава Христу!\r\n'),
  (128, 'Высока любовь и дивна', 'Высока любовь и дивна\r\nЧиста свята глубока\r\nИзливается из сердца\r\nНеизменного Христа\r\n\r\nПрипев:\r\nОн врата небес откроет\r\nЧтобы мне туда войти\r\nПодарил Он мне спасенье\r\nИ прощает все грехи\r\n\r\nЯ на дно греха спустился\r\nКак заблудшая овца\r\nВ горе в скорби я томился\r\nОн в любви нашёл меня\r\n\r\nЧудная любовь Иисуса\r\nВсе грехи Он мне простил\r\nЧесть Ему хвала и слава\r\nЗа меня Он Кровь пролил\r\n'),
  (129, 'Благодарю Тебя я', 'Благодарю Тебя я, Господь из Назарета,\r\nЗа счастье, что Тобою мне дано,\r\nЗа то, что вот однажды меня Ты в жизни встретил,\r\nИ стало на путях моих светло.\r\n\r\nТы шел ко мне навстречу, Господь из Назарета,\r\nПовел путем, где прежде Ты ходил,\r\nВезде встречались люди, которых Ты утешил,\r\nКоторых Ты нашел и исцелил.\r\n\r\nЯ видел слезы счастья в глазах освобожденных,\r\nЯ слышал Твои кроткие слова:\r\nСмотри и будь таким же, смотри и делай то же,\r\nЛюби людей, как любишь ты себя.\r\n\r\nВеди меня Дух Божий, Господь из Назарета,\r\nПусть там, где мне пройти с Тобой дано,\r\nНе будет обделенных, не будет не согретых,\r\nТвой свет во мне пусть светит далеко.\r\n\r\nВеди меня и дальше, Господь из Назарета,\r\nЯ знаю, что найду Твои следы\r\nЛишь там, где людям тяжко, лишь там, где ищут света,\r\nГде боль людей безмерной глубины.\r\n'),
  (130, 'Велики и чудны дела Твои', 'Велики и чудны дела Твои,\r\nГосподи Боже Вседержитель.\r\nПраведны и истинны пути Твои Царь Святых.\r\nКто не убоится не прославит Тебя, Ибо Ты един свят.\r\nВсе народы поклонятся Пред Тобой, ибо Ты един свят.\r\nСвят, свят, Ты един свят. Свят, свят, Ты един свят.\r\n'),
  (131, 'Благодарю, что наяву', 'Благодарю, что наяву\r\nЯ вижу Божий мир чудесный,\r\nИ эту землю, и траву,\r\nИ солнца яркий луч небесный.\r\nБлагодарю Творца за все:\r\nЗа хлеб насущный и удачу,\r\nЗа холод, зной и за тепло,\r\nБлагодарю за то, что плачу.\r\n\r\nПрипев:\r\nЗа дождь, за дождь за ветер и траву,\r\nЗа крест Голгофский и мученья,\r\nЗа дар небесный искупленья\r\nБлагодарю! Благодарю!\r\n\r\nЗа небо, горы и цветы,\r\nБлагодарю за птичье пенье,\r\nИ за погасшие мечты,\r\nИ за святое вдохновенье.\r\nБлагодарю за помощь мне\r\nСреди невзгод житейской битвы,\r\nБлагодарю, что в тишине\r\nСлагаю к небу я молитвы.\r\n'),
  (132, 'Величит душа моя Бога', 'Величит душа моя Бога,\r\nОн – Царь и Владыка миров.\r\nКогда на душе тревога,\r\nСпешу под Его я покров.\r\n\r\nПрипев:\r\nОн правдой меня осеняет,\r\nДаёт мне под крыльями тень,\r\nОт коварных путей охраняет\r\nИ душу мою сберегает\r\nПо милости каждый день.\r\n\r\nЕму Аллилуйя, Осанна!\r\nОн полон небесных щедрот.\r\nЯ в Нём нахожу непрестанно\r\nОтраду в минуты невзгод.\r\n\r\nИ я каждый день направляю\r\nМолитвы в небесную синь\r\nИ долго Его прославляю\r\nСердечной хвалою, аминь!\r\n\r\nБлагодарю Творца за мир,\r\nЗа жизнь, за свет и мирозданье,\r\nЗа ночь глухую и эфир,\r\nЗа ветра тихое дыханье.\r\n'),
  (133, 'Ты куда идешь, скажи мне', '\"Ты куда идешь, скажи мне, странник с посохом в руке?\" -\r\n\"Дивной милостью Господней к лучшей я иду стране.\r\nЧерез горы и долины, через степи и поля,\r\nЧрез леса и чрез равнины я иду домой, друзья\".\r\n\r\nПрипев:\r\n\"Странник, в чем твоя надежда в той стране твоей родной?\" -\r\n\"Белоснежная одежда и венец весь золотой.\r\nТам источники живые и небесные цветы,\r\nЯ иду за Иисусом через жгучие пески\".\r\n\r\n\"Страх и ужас не знакомы разве на пути тебе?\" -\r\n\"Да, но Божьи легионы охранят меня везде!\r\nИисус Христос со мною, Он направит Сам меня\r\nНеуклонною тропою прямо, прямо в небеса\".\r\n\r\n\"Так возьми ж меня с собою, где чудесная страна\". -\r\n\"Да, мой друг, пойдем со мною, вот тебе моя рука!\r\nНедалеко уж родная и желанная страна.\r\nВера чистая, живая нас введет с тобой туда\".\r\n'),
  (134, 'В моей жизни славься Господь', 'В моей жизни славься Господь, славься Господь,\r\nВ моей жизни славься, Великий Бог.\r\n\r\nВ моём сердце славься Господь, славься Господь,\r\nВ моём сердце славься, Великий Бог.\r\n\r\nВ моей песне славься Господь, славься Господь,\r\nВ моей песне славься, Великий Бог.\r\n\r\nВ моей церкви славься Господь, славься Господь,\r\nВ моей церкви славься, Великий Бог.\r\n'),
  (135, 'Бог с нами в долине удачи', 'Бог с нами в долине удачи и близок в часы неудач,\r\nКогда мы смеемся и плачем, Он с нами, Господь наш и Врач.\r\nВ глубокие топи унынья, в ущелья, где смертная тень,\r\nСпускается Он и поныне, чтоб нам помогать каждый день.\r\n\r\nПрипев:\r\nСкажем вместе: Слава Богу! Слава Богу!\r\nСкажем вместе: мы идем за Тобой.\r\nСкажем вместе: Слава Богу! Слава Богу!\r\nСкажем вместе: мы идем за Тобой.\r\n\r\nБог с нами, когда наше знамя, которому имя любовь,\r\nТоржественно реет над нами, вобрав в себя образ Христов.\r\nКогда мы людей утешаем, слагая дела и слова,\r\nЛюбовью своей подтверждая, что вера у нас не мертва.\r\n'),
  (136, 'Крепко люблю я, Иисус', 'Крепко люблю я, Иисус, Тебя! Жизни доро-\r\nже Ты для меня, Нежно припал я К груди\r\nсвятой; Там мир нашел я, Там мой покой.\r\n\r\nПрипев:\r\nИисус, люблю я Крепко Тебя; Жизни\r\nдороже Ты для меня.\r\n\r\nКрепко люблю я, Иисус, Тебя! Крепче же друж-\r\nбы Нет, как Твоя. Полн состраданья, Миром светя,\r\nТы мой Отец, Ведь Я же дитя.\r\n\r\nТы ведь простишь мне, Если споткнусь; Ты уте-\r\nшенье, Гимн мой - Иисус, Мой Искупитель - Друг\r\nнеземной; Ближе к Тебе Сам Сделай дух мой.\r\n\r\nДух Твой люблю я Сердцем, умом; Дух мой сми-\r\nрился, Будь мне Царем! Дух Твой люблю я, Вечно Ты\r\n- мой; В жизни иль смерти Духом я Твой.\r\n'),
  (137, 'Бога легко искать', 'Бога легко искать. Бога легко найти.\r\nБог - это благодать, Бог - это свет в пути.\r\nЭто - любовь моя, это добро твое,\r\nЭто - средь бурь маяк, отдых в конце боев.\r\n\r\nБог - это первый вздох, первый ребенка крик,\r\nВ новую жизнь порог, в вечность манящий миг.\r\nБог - это наш судья, истины вечный страж.\r\nБог - это суть моя, совести голос наш.\r\n\r\nБог - это в сотах мед, Бог - это хлеб и соль.\r\nТот, кто нам мир дает, тот, кто врачует боль.\r\nЭто любовь людей, это - прощенье зла.\r\nЭто - в душе моей мощный родник тепла.\r\n\r\nЭто - весенний гром, это - осенний дождь,\r\nЭто - по телу дрожь в храме Его святом,\r\nЭто - молитвы стон, гимнов хваленья звук.\r\nЭто - земной поклон, это - скрещенье рук.\r\n\r\nБог - это вечный дух, это - стихий закон.\r\nБог - это лучший друг, в бедах помощник Он.\r\nЭто - воскресный день, это - Голгофский крест,\r\nЭто - благая весть, крыльев могучих сень.\r\n'),
  (138, 'В Иисусе нашёл я счастье', 'В Иисусе нашёл я счастье,\r\nВ Нём Одном знаю я покой.\r\nИисус – Ты мой Искупитель,\r\nТы мой Бог, я навеки Твой.\r\n\r\nПрипев:\r\nИисус, Ты моя защита,\r\nИисус, Ты мой щит и меч,\r\nИисус, Ты моя победа,\r\nТы мой Царь, Ты мой Бог навек.\r\n\r\nНу а если бывает в жизни\r\nНелегко по пути идти,\r\nЯ тогда говорю в молитве:\r\n“Иисус, Ты меня защити.”\r\n\r\nС Иисусом пойду я смело,\r\nЗнаю, Он меня поведёт\r\nПо дороге к святой отчизне,\r\nВ царство света и мир красот\r\n'),
  (139, 'Бой часов настойчиво и властно', 'Бой часов настойчиво и властно\r\nВозвещает жителям планеты:\r\nС каждым годом, с каждым днём и часом\r\nВсё короче благодати лето.\r\n\r\nПрипев:\r\nБьют часы, светлеют неба дали,\r\nБлизок дня желанного рассвет.\r\nБьют часы, спасённых ожидает\r\nВечность со Христом без счёта лет.\r\n\r\nДни летят, бегут тысячелетья,\r\nПриближая вечности пределы.\r\nПред судом Всевышнего ответит\r\nВсякий, кто небрежно строил дело.\r\n\r\nБой часов усердно отзовётся\r\nВ каждом сердце, пол-ом сокрушенья.\r\nЧас пробьёт, и жизни нить прервется,\r\nСмолкнет нежный зов любви, спасенья.\r\n'),
  (140, 'Всякий жаждущий, иди и пей', 'Всякий жаждущий, иди и пей\r\nС источника воды живой.\r\nКто верит в Сына, кто любит Сына,\r\nВсю твою жизнь Он обновит!\r\n\r\nПрипев:\r\nАллилуйя! Тебе хвала!\r\nАллилуйя! Тебе хвала!\r\nАллилуйя! Тебе хвала!\r\nАллилуйя! Тебе, Иисус, хвала! 2-раза\r\n\r\nЕсли ты устал, изнемог идти,\r\nОн рад помочь на земном пути,\r\nНоши все снимает, силы обновляет,\r\nВо всех скорбях помощник только Он.\r\n\r\nТы проси огня, и Он даст тебе.\r\nВстанешь радостно, и вперёд пойдёшь.\r\nЛишь Ему доверься, на Него надейся,\r\nВсю твою жизнь Он обновит!\r\n'),
  (141, 'Будь верен в испытаньях', 'Будь верен в испытаньях - Бог благословит тебя,\r\nИ через все страдания, заботясь и любя,\r\nТебя Он проведёт, и силы даст перенести\r\nВсё, что встретится в пути.\r\n\r\nПрипев:\r\n// Если в сердце вера - не страшны печали,\r\nПустота и серость не гнетут ночами;\r\nИ, что бы в этой жизни мы не повстречали,\r\nГлавное, что есть любовь!//х2\r\n\r\nБудь с Богом в мире этом - и не бойся ничего,\r\nОн ни за что на свете не оставит Своего.\r\nПусть жизнь твоя свечою воспылает в темноте,\r\nКак того хотел Отец.\r\n\r\nХрани Его завет, и не сворачивай с пути,\r\nИ в сердце обогретом имя Господа святи,\r\nИ все твои желанья Он исполнит и мечты,\r\nВ этом будь уверен ты.\r\n\r\nЯ хочу сказать тебе: \"Не бойся!\r\nНе волнуйся, лучше успокойся!\r\nВ тишине пойми, что Сам Господь\r\nУтешение даёт.\r\n'),
  (142, 'Господь велик в словах и делах', 'Господь велик в словах и делах\r\nЕго милости нет конца. Пусть на земле и на небесах\r\nЕму не смолкает хвала. Господь велик и достоин славы\r\nОн лишь один достоин хвалы Господь велик и потому, и потому\r\nСлава Ему! Слава Ему!\r\n'),
  (143, 'Всем известно людям', 'Всем известно людям, как прекрасна жизнь. Но еще\r\nпрекрасней, как познаешь смысл. Как Творца вселенной\r\nсердцем обретешь, Жизнью полноценной лишь тогда живешь.\r\n\r\nТогда жизнь понятна, ясно цель видна, Слышу голос\r\nвнятно: \"Ты - мое дитя, Я - Отец твой вечный, Я люблю\r\nтебя, На пути к Отчизне сохраню от зла\".\r\n\r\nО, как мы счастливы, кто познал Христа, Радость в\r\nнашем сердце льется, как река. Нам и в этой жизни\r\nхорошо с Христом. У Отца в Отчизне мы приют найдем.\r\n\r\nПоспеши к Иисусу ты прийти, друг мой, Он с любовью примет,\r\nдаст тебе покой. И вот в этой жизни будешь Им храним,\r\nА потом в Отчизне вечно будешь с Ним.\r\n'),
  (144, 'В тихий вечер склоняю', 'В тихий вечер склоняю я колени в тиши\r\nИ Тебя призываю: О, Властитель души.\r\nТы приди в мою душу, тихо свет свой пролей.\r\nЯ Твой голос услышу: Твое слово - елей.\r\n\r\nВсе заботы земные отошли далеко,\r\nИ в минуты ночные мне бывает легко.\r\nЗнаю я, Ты ответишь на вопросы души.\r\nПриходи в тихий вечер, о Иисус, приходи.\r\n\r\nСветом правды всегда Ты нашу жизнь озари,\r\nКак в Эммаус когда-то, рядом с нами иди.\r\nРазъясняй слово жизни, чтоб горели сердца,\r\nПо дороге к Отчизне с нами будь до конца.\r\n'),
  (145, 'Встает заря, и солнца луч', 'Встает заря, и солнца луч на небо всходит,\r\nЗасияв в бескрайней синеве.\r\nЗа новый день хвалу Творцу поет природа\r\nВместе с нами в радостной хвале\r\n\r\nПрипев:\r\nСлава Богу  Слава Богу\r\nЗа милость, за прощение грехов\r\nСлава Бога  Слава Богу за дивную Его любовь\r\n\r\nКак дивно все, что сотворил наш Бог великий,\r\nДа звучит и слава, и хвала\r\nПрекрасно все, что создано Творцом Владыкой,\r\nСлавим Бога за дивные дела\r\n\r\nСпасенья дар имею я и счастлив этим -\r\nЭто есть сокровище мое.\r\nИ жизни путь Его дарами здесь отмечен,\r\nИ осыпан милостью Его.\r\n\r\nО, сердце, верь, прийди к Нему, спеши, не медли,\r\nОн - Источник счастья твоего.\r\nВзойдет заря в душе твоей и не замедлит,\r\nТы прославишь Бога своего.\r\n'),
  (146, 'Дай нам силы, чтоб смирялись', 'Дай нам силы, чтоб смирялись верно мы\r\nДухом, телом и душою всей.\r\nДай, чтоб были в этой жизни мудрыми\r\nСредь земных и временных скорбей.\r\nНа Тебя хочу душой взирать,\r\nЗа Тебя хочу идти страдать\r\nИ всем грешным людям возвещать Благодать.\r\n\r\nНа земле дай сил стремиться к святости\r\nВ мыслях, чувствах и во всех делах,\r\nНе лишиться нам чтоб вечной радости\r\nВ чистых и лазурных небесах.\r\nКак хочу душой туда лететь,\r\nЧтоб с Христом общение иметь\r\nИ увидеть в вечных небесах Чудеса!\r\n\r\nДай нам духа святости и простоты\r\nК ближним, дальним и ко всем врагам,\r\nЧтобы в жизни славился лишь только\r\nТы, Наш Христос, предвечный Пастырь Сам.\r\nНаучи нас всех людей любить,\r\nПомоги всем людям говорить,\r\nЧто спасенье Ты для всех принес, Царь Христос.\r\n'),
  (147, 'Мы желаем вам удачи', 'Мы желаем вам удачи,\r\nЧтобы видеть как звёзды гаснут\r\nЧтобы слышать как люди плачут,\r\nКогда их обижают напрасно. /2р.\r\n\r\nМы желаем вам  дороги,\r\nИ песка, и снега, и ветра.\r\nПожелаем тебе тревоги,\r\nЧтобы был ты всегда человеком.\r\n\r\nМы желаем вам печали,\r\nЧтобы видеть чужое горе,\r\nЧтобы слышать как стонут чайки,\r\nКогда шторм закипает в море.\r\n\r\nМы желаем вам удачи,\r\nВ каждом слове, и в каждом деле,\r\nЕсли с Господом путь твой начат,\r\nЗначит верно идёшь ты к цели.\r\n'),
  (148, 'Даров Господних чаша', 'Даров Господних чаша\r\nДля всех нас не пуста.\r\nПусть \"Слава Богу\" чаще\r\nЗвучит в твоих устах.\r\n\r\nПрипев:\r\nА если над тобою\r\nСкрыт в тучах солнца луч,\r\nЗнай: небо голубое\r\nГораздо больше туч.\r\n\r\nИ если дух встревожен,\r\nТы в благость Божью верь.\r\nЦени щедроты Божьи,\r\nИх больше, чем потерь.\r\n\r\nГруз будней предстоящих\r\nСегодня не поднять;\r\nБог милостей вчерашних -\r\nБог завтрашнего дня!\r\n'),
  (149, 'Спаситель мой, Ты очи', 'Спаситель мой, Ты очи мне открыл,\r\nНа пагубном пути остановил.\r\nМой новый путь лежит к Тебе, Творец,\r\nМоя надежда на Тебя, Отец.\r\n\r\nПрипев:\r\nНадежда, надежда, надежда на Тебя,\r\nНадежда, надежда, надежда на Тебя.\r\n\r\nОтрадно мне, что Ты, Спаситель, есть,\r\nЧто в мир пришла евангельская весть.\r\nЛетят как птицы к дальним островам\r\nНадежды благодатные слова.\r\n\r\nХоть мне и трудно за Тобой идти,\r\nТерновник, а не розы на пути,\r\nНо я с надеждой к вечности дойду,\r\nИ после бурь блаженство обрету.\r\n'),
  (150, 'В часы тревог, унынья и сомненья', 'В часы тревог, унынья и сомненья,\r\nНе видя света, света пред собой,\r\nДуша болит, терзается в волнении\r\nСпешу, Господь, прийти к Тебе с мольбой\r\n\r\nПрипев:\r\nКак счастлив я, что я Христа имею\r\nКак счастлив я, что Он всегда со мною\r\nЖиву я Им, об этом не жалею,\r\nКак счастлив я, что Он со мной, Друг мой\r\n\r\nО, образ дивный, кроткий и смиренный,\r\nХристос, Господь, живи во мне всегда,\r\nЧтоб в сердце был моём запечатленный,\r\nНе дал упасть под тяжестью креста.\r\n\r\nК кому пойду я в горести сердечной,\r\nС участьем кто протянет руку мне?\r\nЛишь ты один с любовью бесконечной\r\nМеня простил, меня привлёк к Себе.\r\n\r\nНо мне порой так тяжело бывает,\r\nИ кажется, оставил Бог меня.\r\nНо вновь душа словам Твоим внимает:\r\n\"Я не оставлю, Я люблю Тебя!\"\r\n'),
  (151, 'Всё от Него и Им сотворено', 'Всё от Него и Им сотворено,\r\nВсё от Него и Им сотворено.\r\n\r\nПрипев:\r\nЕму за всё слава Ему за всё слава,\r\nЕму за всё слава Во веки, аминь.\r\n\r\nО, бездна богатства, премудрости Христа.\r\nНепостижимы судьбы Господа.\r\n'),
  (152, 'Молодые, вы рождёны для Бога жить', 'Молодые, вы рождёны для Бога жить,\r\nЧтобы в мире Иисусу Христу служить;\r\nМолодые, перед вами Господь идёт.\r\nОн—надёжная опора и наш Друг во всём.\r\n\r\nПрипев:\r\nПосвятим же Христу годы жизни нашей,\r\nОтдавшись Ему до конца,\r\nВедь знаем все мы, что наградой нашей\r\nБудут небеса!\r\n\r\nВ этом мире, где вокруг есть злоба и боль,\r\nМолодые, вы должны быть как свет и соль,\r\nЧтобы люди, увидав в вас Иисуса любовь,\r\nОбернулись и вернулись блудным сыном домой.\r\n\r\nМолодые, да хранит вас Господь во всём,\r\nВ этом мире, где вокруг есть злоба и боль!\r\nМолодые, будьте верны Иисусу Христу,\r\nЧтобы в мире этом лютом победить сатану.\r\n'),
  (153, 'Мәңгілік Сенімен бірге болғым келеді', 'Мәңгілік Сенімен бірге болғым келеді.\r\nӨмірлік серігім, жаным сағаң сенеді.\r\n//Құдайымсың Сен, құанамын мен\r\nКуат алып әрбір сөзіңнен.//\r\n\r\nҚараңғы түндерде сырласамын Өзіңмен.\r\nЖарық, нұр төгілер мейірім шашқан жүзіңнен.\r\n//Құдайымсың Сен, құанамын мен Қорғаушымдай қасымда жүрсең.//\r\n\r\nКөктен Сен келерсің күндердең бір күнінде.\r\nКүтемін Сені мен еш үзілмес үмітпен.\r\n//Құдайымсың Сен, қүанамын мен Ерекше бір ғажап сезіммен.//'),
  (154, 'В нашей жизни самое прекрасное', 'В нашей жизни самое прекрасное\r\nНе ценою денег покупается.\r\nДаром с неба светит солнце ясное,\r\nИ луна нам даром улыбается.\r\n\r\nПрипев:\r\nДаром, даром, Бог дает прощение,\r\nДаром, даром, Бог дает спасение.\r\n\r\nДаром на распаханные полосы\r\nЛьётся дождь со щедростью обильною.\r\nДаром ветер гладит наши волосы\r\nС дуба листья рвёт Рукою сильною.\r\n\r\nДаром пеньем птичек наслаждаемся\r\nЗорями восходами закатами.\r\nС близкими любимыми встречаемся\r\nИ вдыхаем воздух не за плату мы.\r\n\r\nНо всего дороже, драгоценнее\r\nНам в подарок Богом преподнесено.\r\nВ Иисусе вечное спасение\r\nПринимай и улыбайся весело.\r\n\r\nПосмотри, как Бог к тебе склоняется\r\nИ пойми, что как свет солнца ясного.\r\nНе ценою денег покупается\r\nВ нашей жизни самое прекрасное.\r\n\r\nНикакой монетой не заплатите\r\nЗа ребёнка ласку необычную.\r\nЗа супругов нежные объятия\r\nЗа любовь за дружбу бескорыстную.\r\n'),
  (155, 'Долго в сумраке скитался', 'Долго в сумраке скитался\r\nНа путях греховных я,\r\nВ жизни разочаровался,\r\nМир обманывал меня.\r\nЯ взывал - кто мне поможет?\r\nСкорбь мою увидит кто?\r\nИ услышал Божий голос:\r\n\"Я люблю тебя давно!\"\r\n\r\nПрипев:\r\nТак давно, так давно,\r\nЯ люблю тебя давно!\r\nО, как сладок голос Божий:\r\n\"Я люблю тебя давно!\"\r\n\r\nОн меня с любовью принял\r\nИ от всех грехов омыл.\r\nЯ к ногам Его склонился\r\nИ за все благодарил.\r\nИ теперь всегда покоюсь\r\nЯ на пажитях Его,\r\nПостоянно слышу голос:\r\n\"Я люблю тебя давно!\"\r\n\r\nИ ни в чем мне нет отрады,\r\nМир греха мне чуждым стал,\r\nВо Христе лишь, Друге дивном,\r\nУтешенье я познал.\r\nО, как чудно быть в объятьях\r\nУ Того, Кто жизнь дает,\r\nСлышать голос утешенья:\r\n\"Я люблю тебя давно!\"\r\n\r\nО, приди, обремененный,\r\nЛишь доверься, Он простит.\r\nС верой взглянь на крест Голгофский -\r\nИ тебя Он оживит.\r\nЕсли искренне покаясь\r\nВ сердце примешь ты Его,\r\nНепременно ты услышишь:\r\n\"Я люблю тебя давно!\"\r\n '),
  (156, 'Возвожу мои очи к горам', 'Возвожу мои очи к горам,\r\nС них придет вскоре помощь моя.\r\nПомощь моя от Господа, сотворившего все\r\nОн не даст колебаться ноге твоей.\r\n\r\nГосподь-хранитель твой,\r\nНе воздремлет хранящий тебя.\r\nГосподь сень твоя, с твоей правой руки.\r\nСохранит Он тебя от всякого зла.\r\n\r\nГосподь будет охранять\r\nВыхожденье, вхожденье твое.\r\nОтныне и вовеки веков,\r\nОтныне и вовеки веков.\r\n'),
  (157, 'Дни прошли и снова собрались', 'Дни прошли и снова собрались друзья\r\nЧтобы добрым словом ободрить, любя\r\nТак за днями дни пройдут как легкий дым\r\nВсе пройдет и горе и радость вместе с ним\r\n\r\nПрипев:\r\nИ тогда вернется снова весна\r\nВсе мы соберемся у ног Христа\r\nИ тогда не будем годы считать\r\nИ не будем страдать\r\n\r\nСколько дней ты будешь радость иметь\r\nСколько дней ты будешь в горести скорбеть\r\nМы не знаем только будь ты со Христом\r\nИ борись всегда, всегда борись со злом\r\n\r\nСколько зла на свете, но ты добрым будь\r\nЛюдям принеси тепла, хотя б чуть-чуть\r\nРасцветут прекрасные на пути цветы\r\nИ расскажут нам о радости они.\r\n'),
  (158, 'Люблю я мыслить о стране', 'Люблю я мыслить о стране, где Ангелы живут\r\nИ в яркой снежной белизне о Господе поют.\r\n\r\nПрипев: Греха не будет! Греха не будет там!\r\n\r\nЛюблю я мыслить о стране, где Бог любви царит,\r\nГде краше лилий по весне цвет радости горит.\r\n\r\nЛюблю я мыслить о стране, где путники земли,\r\nПроливши кровь в святой войне, венец побед нашли.\r\n\r\nЛюблю я мыслить о стране, где после битвы злой,\r\nОткроют вход свободный мне и я найду покой.\r\n'),
  (159, 'Вы миру свет, светите ярче', 'Вы миру свет, светите ярче,\r\nНе бойтесь тьмы, горите жарче\r\nВы миру свет, не только тем\r\nКто свет не знает, - светите всем.\r\n\r\nПрипев:\r\nПусть свет ваш будет средь тьмы маяк,\r\nПусть он развеет гнетущий мрак.\r\nПусть людям счастье несёт ваш свет -\r\nВ грозу, ненастье, средь бурь и бед.\r\n\r\nВы освещайте пути вперёд,\r\nВы растопите холодный лёд.\r\nВы расточите гранит сердец,\r\nВы всем скажите, Кто ваш Отец.\r\n\r\nВы прославляйте любовь Творца,\r\nТак, чтоб внималм вам все сердца,\r\nТак, чтоб из мрака к свету шли\r\nЧтоб не теряли то, что нашли.\r\n\r\nНо чтобы ярче во тьме гореть,\r\nЧтоб разгораться, а не слабеть -\r\nПоддержку нужно нам от Творца;\r\nСклоняйтесь чаще у ног Христа.\r\n');
INSERT INTO `SONG` (`ID`, `DESCRIPTION`, `TEXT`) VALUES
  (160, 'Превыше власти земных царей', 'Превыше власти земных царей, сильнее смерти, превыше всех людей\r\nВсего что мог представить человек- был и есть и будешь Ты вовек!\r\nПревыше славы любых чудес, превыше звезд и ангелов небес\r\nВсех мудрых слов, сокровищ всех глубин- выше всех и вся лишь Ты Один\r\n\r\nПрипев:\r\nБыл распят, погребен во тьме!\r\nТы жил в скорбях отвержен на земле!\r\nКак цветок растерзанный в пыли,\r\nТы за меня пошел на смерть из-за любви.\r\n'),
  (161, 'Господь! Ты днем и ночью', 'Господь! Ты днем и ночью\r\nЗришь на меня, всегда\r\nХранишь любовью Отчей.\r\nОставлю ль я Тебя?\r\n\r\nПрипев:\r\nМой Бог! Я, без сомненья,\r\nНавеки буду Твой,\r\nИ все мои стремленья -\r\nПребыть в любви святой.\r\n\r\nЯ часто унываю\r\nВ боренье со грехом,\r\nНо и тогда взываю\r\nК Тому, Кем я спасен.\r\n\r\nВсе радости земные\r\nЧужие для меня,\r\nМечты мои иные -\r\nНести свой крест всегда.\r\n\r\nГосподь мой! Я взираю\r\nНа дивный образ Твой\r\nИ людям возвещаю,\r\nКак хорошо с Тобой!\r\n'),
  (162, 'Господи, как странно', 'Господи, как странно,\r\nЧто я жил, Тебя не зная,\r\nЖил без благости Твоей святой\r\nИ без Твоей любви.\r\nНо ныне я с Тобой,\r\nМеня в семью своих детей Ты принял.\r\nЯ благодарю Тебя\r\nИ прославлять Тебя хочу я.\r\n\r\nПрипев:\r\nСлавьте, славьте Бога,\r\nПрославляйте Бога,\r\nПрославляйте Бога\r\nВсё больше день за днём.\r\nСлавьте, славьте Бога,\r\nПрославляйте Бога,\r\nПрославляйте Бога\r\nВ сердце своём.\r\n\r\nГосподи, душа моя хвалой\r\nТебе наполнена, лишь Ты\r\nОковы снял с нее и всю нечистоту,\r\nЛюбовь мне смертью доказал,\r\nЖизнь вечную мне в сердце дал,\r\nНазвал Своим родным дитем\r\nИ стал мне Богом и Отцом.\r\n'),
  (163, 'Где ты, прекрасная, светлая', 'Где ты, прекрасная, светлая, ясная, Родина вечная,\r\nОтчизна моя? Мысли к тебе летят, ввысь устремляются, В\r\nсинее небо направляю я взор.\r\n\r\nПрипев:\r\nА надо мною синее небо, Стайкою темной летят журавли.\r\nВ крике их птичьем радости много, К родине вечной возвратились они.\r\n\r\nТам в вышине небес, в крае безоблачном, Пастырь\r\nИисус Христос ждет уж меня. Скоро достигну я той красоты небес.\r\nИ поднимаю снова к небу глаза.\r\n\r\nЗнаю, Спаситель мой скоро придет за мной\r\nИ в белоснежную Церковь возьмет. Только прошу Тебя:\r\n\"Дай устоять в пути, Чтобы без пятнышка в небо войти\".\r\n'),
  (164, 'Окропи меня и омой меня', 'Окропи меня и омой меня\r\nИ буду белее снега я.\r\nМногократно омой от греха моего,\r\nОчисти меня.Сердце чистое сотвори во мне,\r\nОбнови мой дух внутри меня\r\nНе отвергни меня от лица Твоего, Господь.\r\nПрости, прости мой грех, омой, омой меня,\r\nПрости, прости нас всех! Прошу, прошу Тебя!\r\n'),
  (165, 'Если в бурях жизни дух', 'Если в бурях жизни дух твой удручен,\r\nТучами унынья путь твой омрачен,\r\nВспомни, сколько милости Господь явил,\r\nУдивляться будешь, что Он совершил.\r\n\r\nПрипев:\r\nМилости Господни вспоминай, считай,\r\nВсе их до единой в сердце повторяй.\r\nВспомни, как Он щедро наделил,\r\nУдивляться будешь, что Он совершил.\r\n\r\nБремя ли заботы на тебе лежит,\r\nТяжкое ли горе душу тяготит,-\r\nМилости, как вспомнишь, вознесешь главу,\r\nСбросишь все сомненья, воспоешь хвалу.\r\n\r\nВидишь ли богатых на земных путях,\r\nВспомни: есть сокровище на небесах!\r\nВсякую нужду восполнит Сам Господь,\r\nЗолота дороже то, что Он дает.\r\n\r\nОбодрись же духом, на Христа взирай,\r\nСилою Его все скорби побеждай,\r\nВ памяти все милости Его храни,\r\nИ найдешь отраду на земном пути.\r\n'),
  (166, 'Найду покой в тебе Христос', 'Найду покой в тебе Христос, Верю я в могущество Твоё.\r\n\r\nПрипев:\r\nПусть бушует шторм и гром гремит,\r\nНад бурей буду я с Тобой парить.\r\nМой Отец, Ты Царь над всей землёй,\r\nЯ не боюсь, мой Бог со мной.\r\n\r\nСкрой меня своим крылом, Сохрани под сильною рукой.\r\n'),
  (167, 'Бог так любит всех нас', 'Бог так любит всех нас,\r\nЧто остaвил небо.\r\nИ родился в это мире,\r\nЧтоб людей спaсти,\r\nЧтобы кaждый смертный\r\nНa плaнете этой\r\nМесто горя, скорби\r\nРaдость счaстье мог нaйти\r\n\r\nПрипев:\r\nОн родился, чтобы грех твой нa Себя принять,\r\nЧтоб быть рaсспятым нa древе зa тебя стрaдaть,\r\nЧтоб тебе отдaть всю слaву и любовь Свою,\r\nНaвсегдa вернуть в Небесную Стрaну\r\n\r\nОн тaк любит всех нaс,\r\nЧто остaвил небо\r\nИ нет больше той цены\r\nЧто зa тебя Он дaл\r\nТaк преди ж сегодня\r\nНa призыв Господен\r\nОтзовись, ведь Он тебя\r\nТaк долго ждaл.\r\n\r\nНеужели в сердце для Христa нет местa\r\nНеужели не поймет оно Его любви?\r\nНеужели нaпрaсно\r\nНa кресте том стрaшном\r\nЗa тебя Он проливaл\r\nПоток Святой крови?\r\n'),
  (168, 'В двери закрытые поздней порой', 'В двери закрытые поздней порой\r\nКто-то стучит, кто-то стучит...\r\nОтзыва нет и усталой рукой\r\nКто-то стучит, стучит.\r\nЭто Спаситель у двери стоит,\r\nКротко стучит, нежно стучит.\r\nСердце открой - Он недуг исцелит:\r\nСнова Христос стучит.\r\n\r\nВ сердце уныние, радости нет -\r\nКто-то стучит, кто-то стучит!\r\nВ доме погас утешения свет,\r\nКто-то стучит, стучит.\r\nЭто Спаситель у двери стоит,\r\nКротко стучит, нежно стучит.\r\nСердце открой - Он недуг исцелит:\r\nСнова Христос стучит.\r\n\r\nСовесть томит обличения глас -\r\nКто-то стучит, кто-то стучит!\r\nЗов не смолкает в душе ни на час,\r\nКто-то стучит, стучит!\r\nЭто Спаситель у двери стоит,\r\nКротко стучит, нежно стучит.\r\nСердце открой - Он недуг исцелит:\r\nСнова Христос стучит.\r\n\r\nСтук всё сильнее звучит за стеной -\r\nКто-то стучит, кто-то стучит!\r\nГрешник, покайся и сердце открой!\r\nКто-то стучит, стучит!\r\nЭто Спаситель у двери стоит,\r\nКротко стучит, нежно стучит.\r\nСердце открой - Он недуг исцелит:\r\nСнова Христос стучит.\r\n'),
  (169, 'Как хорошо, что со мною Спаситель', 'Как хорошо, что со мною Спаситель,\r\nВечный Творец, Он так любит меня!\r\nВ жизни моей Он мой друг и Хранитель,\r\nВсюду со мной среди ночи и дня.\r\nВ жизни моей Он мой друг и Хранитель,\r\nВсюду со мной среди ночи и дня.\r\n\r\nГде бы не шла моей жизни дорога,\r\nБуду я жить для Иисуса Христа,\r\nМне не страшна никакая тревога:\r\nВерю Ему, в Нем любви полнота.\r\nМне не страшна никакая тревога:\r\nВерю Ему, в Нем любви полнота.\r\n\r\nПуть наш суров, устаем временами,\r\nДвижемся мы по житейским волнам.\r\nСкоро придет наш Спаситель за нами,\r\nБудет конец нашим бедам скорбям.\r\nСкоро придет наш Спаситель за нами,\r\nБудет конец нашим бедам скорбям.\r\n'),
  (170, 'Если нет драгоценного дара любви', 'Если нет драгоценного дара любви,\r\nЕсли сердце холодно, как лед,\r\nИ живешь для себя, и лишь занят собой,\r\nИ на подвиг души не влечет,\r\nИ живешь для себя, и лишь занят собой,\r\nИ на подвиг души не влечет,\r\n\r\nТо напрасно живешь на земле ты, мой друг,\r\nБесполезно проходят года;\r\nИ молитвы твои не угодны Творцу,\r\nНе угодны Ему и дела,\r\nИ молитвы твои не угодны Творцу,\r\nНе угодны Ему и дела.\r\n\r\nЛишь зажженные пламенем вечной любви\r\nДуши тех, кто на жертву готов,\r\nКто готов возвещать о Христовой Крови,\r\nЧто смывает проказу грехов,\r\nКто готов возвещать о Христовой Крови,\r\nЧто смывает проказу грехов.\r\n\r\nТолько в этих сердцах пребывает Христос,\r\nИ они прибывают лишь в Нем,\r\nЭто-Божии вестники вечной любви,\r\nОни в славе пребудут с Христом,\r\nЭто-Божии вестники вечной любви,\r\nОни в славе пребудут с Христом.\r\n'),
  (171, 'За все, за все благодарю', 'За все, за все благодарю:\r\nЗа путь Твой крестный, за спасенье,\r\nЗа жизни светлую зарю\r\nВ Твоем победном воскресенье!\r\n\r\nПрипев:\r\nЗа Слово чудное Твое,\r\nЧто слаще меда, тверже стали.\r\nЗа Дух, который в нас живет\r\nИ открывает неба дали!\r\n\r\nЗа солнце, за морской простор,\r\nЗа пенье птиц весенней ранью,\r\nЗа красоту могучих гор,\r\nЗа наши скромные собранья!\r\n\r\nЗа всех друзей в родном краю,\r\nЗа клич святого пробужденья\r\nТебе осанну воспою -\r\nЛишь Ты достоин поклоненья!\r\n\r\nИ если жизненный мой путь\r\nПойдет долиной испытаний,\r\nТы дашь мне силы не свернуть\r\nК тропе позорных колебаний.\r\n\r\nЗа Слово чудное Твое,\r\nЧто слаще меда, тверже стали.\r\nЗа Дух, который в нас живет\r\nИ открывает неба дали!\r\n\r\nПрипев:\r\nЗа все хочу благодарить:\r\nЗа мрак тюрьмы, за боль разлуки,-\r\nИ всей душой Тебя любить,\r\nМой Иисус, принявший муки!\r\n'),
  (172, 'В небесах моя отчизна', 'В небесах моя отчизна,\r\nГде не будет больше слёз,\r\nПриготовил там обитель\r\nДля меня мой Друг, Христос.\r\n\r\nПрипев:\r\nВ небе, в небе скоро буду я,\r\nБуду там, где ждут друзья,\r\nБуду там, где мой Иисус,\r\nТуда лишь я стремлюсь.\r\n\r\nТам есть место для покоя\r\nОт нужды и суеты,\r\nТам любовь Христа святая\r\nВсех в одно соединит.\r\n\r\nО, отчизна дорогая,\r\nСтранник жаждет по тебе,\r\nИ, шаги всё ускоряя,\r\nОн спешит к родной стране.\r\n'),
  (173, 'Любви святой дыханье', 'Любви святой дыханье\r\nСвет жизни нам дает,\r\nИ уст ее лобзанье\r\nВ сердца отраду льет.\r\nЛюбовь у колыбели\r\nО счастье нам поет\r\nСклонившись у постели,\r\nЗа нас молитву шлет.\r\n\r\nКогда гроза с ветрами\r\nНадежду потрясет,\r\nЛюбовь горюет с нами\r\nИ помощь нам несет.\r\nКогда наш брат стенает\r\nВ неволе иль больной,\r\nЛюбовь повелевает\r\nНам жертвовать собой.\r\n\r\nЛюбовь врагам внушает\r\nДруг другу руку дать,\r\nИ грешных нас прощает\r\nИ учит нас прощать.\r\nИ речи дар прекрасный,\r\nИ поприще крови,\r\nИ подвиг веры властной\r\nНичтожны без любви.\r\n\r\nИ знанье увядает,\r\nИ солнце дня зайдет -\r\nЛюбовь не умирает,\r\nОдна любовь живет.\r\nЛюбовью мир несчастный\r\nВосставлен к счастью вновь;\r\nЕй ангелы прекрасны,\r\nИ вечный Бог - Любовь.\r\n'),
  (174, 'Вечный край, таинственный', 'Вечный край, таинственный, далекий\r\nСердце так волнуется порой\r\nВот шагает путник по дороге\r\nЧто ему назначено судьбой\r\n\r\nПрипев:\r\nНебо на земле так не понятно\r\nМного неизвестно нам о нем\r\nА душа немой таской объята\r\nЖдет переселенья своего\r\n\r\nЖизнь земная призрачна, туманна\r\nПролетит как стая журавлей\r\nА когда-нибудь совсем нежданно\r\nНебо постучится у дверей\r\n\r\nЯ глаза когда-нибудь открою\r\nДалеко в неведомом краю\r\nНа колени стану всей душою\r\nИ за все Иисуса обниму.\r\n'),
  (175, 'В дикой пустыне воды', 'В дикой пустыне воды нет и жизни,\r\nА лишь раскаленный песок.\r\nИз рабства Египта в блаженное царство\r\nБожий народ идет.\r\nВидели очи дела святые, что Господь сотворил,\r\nСлавили Бога, Бога живого, Бог чудеса творил.\r\n\r\nОткрылося небо, и падала манна\r\nНа раскаленный песок;\r\nНо среди манны я слезы увидел:\r\nПлакал великий Бог.\r\n«Дети мои, Мои дорогие, Я вас Своими назвал,\r\nИз рабства Египта в блаженное царство\r\nЯ вас одних позвал.\r\n\r\nЗачем же роптать вам, зачем же не верить\r\nВ любовь Мою и благодать?\r\nЧермное море сегодня свидетель:\r\nЯ вас пришел спасать.\r\nМертвые скалы по воле Бога сильны вам воду давать.\r\nВерьте Мне, дети, не бойтесь пустыни,\r\nЯ буду вас охранять.»\r\n\r\nВремя умчится быстрой стрелою, кончится трудный путь…\r\nБеды, пустыни, горе, тревоги, – скажет Иисус, – забудь.\r\n«Дети мои, Мои дорогие, небо открыто для вас!»\r\nПрощайте, люди, прощай, пустыня, Бог забирает нас.\r\n'),
  (176, 'Иисус с небес сошел', 'Иисус с небес сошел,\r\nЧтобы мир сердцам послать.\r\nНа Голгофу крест Он нес,\r\nЧтобы всем прощенье дать.\r\n\r\nПрипев:\r\nО Иисус! О Иисус!\r\nМы Тебя благодарим\r\nСвоей кровью Ты омыл\r\nВсе пороки и грехи,\r\nВсе прекрасным сделал Ты.\r\n\r\nИсцеленье Он давал\r\nЛюдям тем, кто болен был.\r\nМертвых к жизни воскрешал,\r\nДля добра лишь только жил.\r\n\r\nЛюбит грешников Иисус,\r\nДруг, и ты к Нему приди!\r\nИсцелит Он твой недуг\r\nИ грехи твои простит.\r\n'),
  (177, 'Быстрыми, полетными часами', 'Быстрыми, полетными часами\r\nЮность быстрокрылая летит,\r\nМы ее для Господа отдали,\r\nЖизнью нашей Он руководит.\r\n\r\nПрипев:\r\nПусть мчатся годы вереницей,\r\nМы ближе к вечности идем;\r\nЗа крайней жизненной границей\r\nВ небесный дом войдем.\r\n\r\nКончатся земные испытанья,\r\nЗемлю мы оставим навсегда.\r\nВспоминать мы будем дни скитанья,\r\nДни благословенного труда.\r\n\r\nКончится несказанная мука,\r\nБоль души, сердечная тоска.\r\nС грешным миром будь скорей, разлука!\r\nНаша цель ясна и высока.\r\n\r\nНебо - нам желанная отрада,\r\nНебо - нашей жизни всей мечта,\r\nЗа святые подвиги награда,\r\nСчастья неземного красота.\r\n'),
  (178, 'Всё Иисусу отдаю я', 'Всё Иисусу отдаю я,\r\nВесь Ему принадлежу.\r\nВ уповании и смирении\r\nПред лицом Его хожу.\r\n\r\nПрипев:\r\nВсё я отдаю, всё я отдаю,\r\nВсё Тебе, мой Искупитель,\r\nВсё я отдаю.\r\n\r\nВсё Иисусу отдаю я,\r\nВсё кладу к Его ногам.\r\nСуету отверг земную,\r\nНаправляюсь к небесам.\r\n\r\nВсё Иисусу отдаю я.\r\nВесь хочу Христовым быть.\r\nДух Предвечный да научит нас\r\nЗнать Христа, Христа любить.\r\n\r\nВсё Иисусу отдаю я.\r\nБоже, храм во мне создал!\r\nБлагодатью и любовью\r\nУм и сердце наполняй.\r\n\r\nВсё Иисусу отдаю я.\r\nЧудо Он во мне свершил:\r\nМир и радость дал святую.\r\nСлава, слава Богу сил!\r\n'),
  (179, 'Я верю вести той', 'Я верю вести той, Что я спасен Тобой,\r\nЧто все грехи мои Ты снял, Жизнь вечную мне дал.\r\n\r\nТы умер за меня,Ты дал спасенье мне,\r\nЧтоб умер для греха и я И жил бы лишь в Тебе.\r\n\r\nТеперь я Твой, я Твой, Навек спасен Тобой;\r\nНикто меня не похитит: Ты Сам, Господь, мой щит.\r\n\r\nО Господи, я слаб; Моя же сила - Ты;\r\nТобой искупленный я раб, Меня поддержишь Ты.\r\n\r\nТы здесь меня хранишь, И мне покой даришь,\r\nИ Сам ведешь меня с Собой В небесный край родной.\r\n\r\nГосподь, не дай любить Мне в мире ничего,\r\nНо дай всегда мне только жить Для дела Твоего.\r\n\r\nКогда ж Ты жизнь прервешь, Теперь уж знаю я,\r\nМеня в обитель Сам введешь - В том радость вся моя.\r\n'),
  (180, 'Для тебя, о человек', 'Для тебя, о человек, Бог создал небо и поля,\r\nДля тебя цветут сады и плод дает земля,\r\nДля тебя моря и горы, все живое – для тебя,\r\nДля тебя на небе солнце и луна.\r\n\r\nПрипев:\r\nЧто отдал ты Ему за эту красоту?\r\nЧто отдал ты за эти чудеса?\r\nПодумай, человек, ведь твой недолгий век.\r\nОтдай Ему себя! Отдай Христу себя!\r\n\r\nДля тебя звучит мелодия в далекой вышине,\r\nДля тебя роса на травах на заре,\r\nДля тебя моря и горы, все живое – для тебя,\r\nДля тебя все это создала Его рука.\r\n'),
  (181, 'Жить с Иисусом я желаю', 'Жить с Иисусом я желаю\r\nИ Ему служить хочу,\r\nОн создал меня я знаю,\r\nЯ Ему принадлежу.\r\n\r\nПрипев:\r\nЯ люблю Его всем сердцем\r\nОн от бед хранитель мой.\r\nЗнаю, после жизни этой\r\nОн возьмет меня домой.\r\n\r\nГрех творить я не желаю\r\nДелать я хочу добро\r\nЯ из Библии читаю,\r\nПастырь мой не любит зло.\r\n\r\nЖить с Иисусом я желаю\r\nОн мой щит, моя скала\r\nС верой в сердце принимаю\r\nЧто Голгофа мне дала.\r\n\r\nПосмотри на Иисуса\r\nОн давно тебя зовет.\r\nТы отдай Ему все сердце\r\nПусть Он сам в тебе живет.\r\n'),
  (182, 'Верю я, что есть Великий Бог! ', 'Верю я, что есть Великий Бог!\r\nТолько Он создать такое мог,\r\nМир, людей и жизнь земли - это Его дела.\r\n//Перед Ним в молитве я склонюсь\r\nИ с благодареньем помолюсь,\r\nВедь Его рука меня вела в мире зла. //2р.\r\n\r\nВерю я, что через всё пройду,\r\nЧерез испытанья и беду,\r\nИ никто меня не вырвет из Господних рук.\r\n//Я иду в небесный вечный дом\r\nИ увижу всё, что будет в нём,\r\nЭто стоит всех земных разлук, стоит мук. //2р.\r\n\r\nВерю я, что все мои друзья,\r\nКто прожил земную жизнь не зря,\r\nБудут в небесах носить венцы прекрасные.\r\n//Кто отдал себя на труд Христа,\r\nВечные года получит в дар,\r\nВерой достигая свой удел, он преуспел. //2р.\r\n'),
  (183, 'Только в Иисусе счастлив я всегда', 'Только в Иисусе счастлив я всегда,\r\nТолько в Иисусе отдых от труда;\r\nДоверяюсь я Спасителю во всем,\r\nВерного Друга я нашел лишь в Нем!\r\n\r\nПрипев:\r\nО, какое счастье другом быть Христа,\r\nЭтого не могут выразить уста!\r\nРадость обретает сердце только в Нем,\r\nВ Иисусе, Друге дивном моем!\r\n\r\nДруг, с Которым вечно буду жить в любви,\r\nДруг, Который спас меня в Своей Крови,\r\nДоказал любовь Свою Он на кресте,\r\nВерного Друга я нашел в Христе!\r\n\r\nСоюз вечной дружбы заключил со мной,\r\nС юных лет ведет меня Своей рукой\r\nВ ту страну, откуда Он придет за мной,\r\nГде вечно буду ликовать душой!\r\n'),
  (184, 'В утро юности прекрасной', 'В утро юности прекрасной,\r\nЖизни радостной рассвет,\r\nМы спешим на призыв властный\r\nИсполнять Христа завет.\r\n\r\nПрипев:\r\nДля труда на Божьей ниве\r\nМы готовы все отдать;\r\nО, не будем же ленивы,\r\nПока день, не будем спать!\r\n\r\nЛюбим имя Иисуса\r\nИ о Нем благовестим;\r\nМы о милости Христовой\r\nЛюдям грешным возвестим.\r\n\r\nПосвятим же силы, время\r\nДля спасенья душ людей,\r\nОблегчая жизни бремя\r\nИм трудом любви своей.\r\n'),
  (185, 'Если невзгоды встретишь в пути', 'Если невзгоды встретишь в пути,\r\nИль тяжело будет крест свой нести,\r\nТы обратись к Иисусу на грудь,\r\nОн скажет слово и ты не забудь:\r\n\r\nПрипев:\r\n\"От Меня это было, Мой брат и сестра,\r\nЭто Я приготовил сей путь для тебя\".\r\nТак иди ж по нему, не ропщи средь скорбей,\r\nНо Иисуса люби горячей.\r\n\r\nЕсли нуждою порабощен,\r\nИли в болезни дух твой смущен,\r\nИли страдаешь невинно от всех.\r\nВ мире нет счастья, нет и утех.\r\n\r\nЕсли друзья тебя не поймут,\r\nИ не окажут любовь и приют,\r\nИли отвергнут ты будешь везде,\r\nНе отступай же, друг мой, ты в борьбе.\r\n'),
  (186, 'Взгляни вокруг: какая красота!', 'Взгляни вокруг: какая красота!\r\nЧто видишь ты - то дело рук Творца!\r\nОставь свой грех, приди к Христу скорей.\r\nЧтоб не остаться вне дверей.\r\n\r\nПрипев:\r\nОн для тебя творил и солнце и луну.\r\nОн для тебя творил леса, поля, моря.\r\nОн для тебя послал Спасителя Христа,\r\nТебе открыл Свои Он небеса.\r\n\r\nО, человек, о, друг мой или враг!\r\nЖизнь пролетит как час или как шаг.\r\nНастанет день - расстанемся мы с ней.\r\nО, не останься у закрытых ты дверей.\r\n\r\nУ тех дверей, где счастье и добро,\r\nИ где в помине не бывает зло.\r\nТам нет вражды, там нет нужды,\r\nИ нет земной там суеты.\r\n\r\nА чтобы двери те открыть ты смог,\r\nИскорени и грех свой и порок, -\r\nОстанься сердцем чист, душой -\r\nСпаситель хочет быть с тобой!\r\n'),
  (187, 'Есть на небе город', 'Есть на небе город, чудный город света,\r\nГде нужды, болезни и печали нету.\r\nТам живут святые, там играют гусли,\r\nВоздавая славу Господу Иисусу.\r\n\r\nПрипев:\r\nБолезни там нет, не гаснет там свет – Иерусалим,\r\nТам вечно живут, там песни поют – Иерусалим.\r\nТы город живых, ты город святых, Иерусалим.\r\nК тебе я стремлюсь, с тобою сольюсь, Иерусалим!\r\n\r\nЗолотом обложен, город мой прекрасный.\r\nЛишь в тебе, Иерусалим, вечно буду счастлив.\r\nЖизнь мою преобрази здесь, в земной долине,\r\nЧтоб душе покой найти в Иерусалиме.\r\n\r\nБратья мои, сёстры, там мы будем вместе\r\nВоспевать Иисусу радостные песни.\r\nТак давайте на земле вместе славить Бога,\r\nЧтоб светлее стала нам в небеса дорога.\r\n'),
  (188, 'Любить, Тебя, Господь', 'Любить, Тебя, Господь - 2 р.\r\nЛюбить, Тебя, Господь, и не взирать назад.\r\n\r\nИдти по следам Твоим, идти и не уставать,\r\nИ преклоняться пред Тобой, и не взирать назад.\r\n\r\nСлужить Тебе, Господь\r\nНести свой крест как Ты.\r\n'),
  (189, 'Глазами неба синего', 'Глазами неба синего взгляни в мои глаза,\r\nВзгляни мне в душу пристально, чтоб плакала душа,\r\nТвой взгляд, Спаситель, ласковый утешит и поймет,\r\nКо мне с поддержкой братскою в опасности придет.\r\n\r\nПрипев:\r\nТвои глаза прекрасные я в небе узнаю\r\nГлазами неба ясного Ты видишь жизнь мою,\r\nТы видишь все желания, все мысли и мечты,\r\nИ все мои страдания. Спаситель, видишь Ты.\r\n\r\nКогда мой путь неясен мне, когда я устаю.\r\nКогда судьбе в отчаяньи вопросы задаю,\r\nИ горькими рыданьями охвачена душа,\r\nСлезами сострадания мне вторят небеса.\r\n\r\nТвой взгляд. Господь, встречаю я в мерцанье дальних звезд,\r\nИ всем сознаньем чаю я: когда к Себе возьмешь,\r\nИ там, в объятьях вечности, где счастье и простор,\r\nРодной до бесконечности я Твой увижу взор.\r\n'),
  (190, 'Иисус - сокровище святое!', 'Иисус - сокровище святое!\r\nМожет ли душа моя найти\r\nВ этом мире что нибудь такое,\r\nЧто Тебя могло бы превзойти?!\r\nТы - источник мудрости и света,\r\nТы - источник вечного добра,\r\nЛишь Тобой душа моя согрета,\r\nТы сегодня Тот же, что вчера.\r\n\r\nЯ с Тобою счастлив и доволен\r\nДаже горькой чаши на земле,\r\nМне с Тобой и в тесноте привольно,\r\nМне с Тобою хорошо везде.\r\nИ ничто с Тобою не сравнится,\r\nМне с Тобой не надо ничего,\r\nИ мой дух к Тебе Иисус стремится,\r\nЛишь Тебя вижу я одного.\r\n'),
  (191, 'Дорога смерти в Иерусалиме', 'Дорога смерти в Иерусалиме\r\nПо ней ступает Человек совсем бессильный\r\nИзбит, измучен, весь кровоточит,\r\nи гвоздь заточен, чтобы жизнь Его закончить.\r\n\r\nПрипев:\r\nЕго любовь с креста тебе кричала:\r\n\"Здесь Мой смерть, а твоей жизни здесь начало\".\r\nОна давно тебя звала, искала,\r\nКогда во тьме ты болен был, Она стонала...\r\n\r\nКак нам легко и как нам просто\r\nидти, мой друг, по Виа Долоросса\r\nВид узких улиц, вслед смотрят косо\r\nвсем, кто пришел найти ответ его вопроса.\r\n\r\nДорога смерти в Иерусалиме\r\nпо ней ступает Человек совсем бессильный.\r\nИдет, чтоб вечность дать нам с тобою,\r\nпуть в бесконечность проложить Своею Кровью\r\n'),
  (192, 'В небесных доспехах', 'В небесных доспехах войдём в этот мир,\r\nВедёт эту битву Господь.\r\nЗемное оружье нас не устрашит,\r\nведёт эту битву Господь.\r\n\r\nПрипев:\r\nИисусу слава, сила, мудрость и крепость и честь.-2 раза\r\n\r\nПусть воинство тьмы выйдет из берегов,\r\nВедёт эту битву Господь.\r\nМы кровью Его одолеем врагов,\r\nВедёт эту битву Господь.\r\n\r\nИ если тяжёл для тебя этот бой,\r\nВедёт эту битву Господь.\r\nСмелее, мой друг, твой Спаситель с Тобой,\r\nВедёт эту битву Господь.\r\n'),
  (193, 'Лишь одно у меня стремленье', 'Лишь одно у меня стремленье:\r\nБольше, Боже, Тебя любить,\r\nПред Тобою ходить в смиренье,\r\nТвое славное имя чтить.\r\n\r\nПрипев:\r\nЛишь одно, мой Господь,-\r\nЖить с Тобой, для Тебя;\r\nЛишь одно у меня -\r\nПосвятить свою жизнь для Тебя!\r\n\r\nЛишь одно у меня стремленье:\r\nВсей душою Тебя познать,\r\nЧтоб Твое на кресте мученье\r\nМне во всей полноте понять.\r\n\r\nЛишь одно у меня стремленье:\r\nВозвещать о Тебе, мой Бог,\r\nБедным душам, кто в рабстве тленья,\r\nЧтоб и их оживить Ты мог.\r\n\r\nЛишь одно у меня стремленье:\r\nЗа Тобою везде идти;\r\nПусть меня окружает презренье,\r\nНо я знаю: Ты - помощь в пути.\r\n'),
  (194, 'За всё Тебя, Господь я благодарю', 'За всё Тебя, Господь я благодарю\r\nТы искупил от смерти душу мою.\r\nИ буду возвещать я по всей земле.\r\nО том, как милостив и благ Ты ко мне.\r\n\r\nПрипев:\r\nПусть вся слава Тебе, мой Иисус\r\nСпасенье дал мне, мой Иисус\r\nИ сердце во мне, мой Иисус\r\nПоёт о Тебе, всегда Иисус.\r\n\r\nС креста стекала кровь святая Твоя,\r\nС креста стекая, очищала меня,\r\nОдежду правды мне Иисус подарил,\r\nПростил, очистил и меня искупил.\r\n\r\nЯ посвящаю жизнь свою лишь Тебе\r\nИ благодать Твоя сегодня на мне\r\nПусть знает каждый, слыша из моих уст\r\nО том, что жив Господь, Спаситель Иисус.\r\n'),
  (195, 'За любовь, за милость', 'За любовь, за милость, за спасение,\r\nБлагодарность Ты прими от нас.\r\nПусть несется песнь благодарения\r\nГосподу - Он кровию нас спас.\r\n\r\nПрипев:\r\nБлагодарим, благодарим, за Твою любовь благодарим!\r\nДостоин Ты вечной хвалы, за Твою любовь благодарим!\r\n\r\nЗа Твои Голгофские страдания,\r\nЗа спасенье, данное Тобой,\r\nИ за все Твои благодеяния\r\nСердце для Тебя звучит хвалой!\r\n\r\nЗа прекрасный дом в лазурным небе,\r\nЗа святую вечность без конца\r\nПесть звучит сегодня гимн хваления,\r\nЭту песнь поют наши сердца.\r\n\r\nЗв друзей прими благодаренье\r\nИ за встречи, данные Тобой.\r\nПусть в собранье льются песнопенья,\r\nБог Отец, и Сын и Дух Святой.\r\n'),
  (196, 'Нам жизнь дана, чтобы любить', 'Нам жизнь дана, чтобы любить, Любить без меры, без предела,\r\nИ всем страдальцам посвятить Свой разум, кровь свою и тело.\r\n\r\nНам жизнь дана, чтоб утешать Униженных и оскорбленных\r\nИ согревать и насыщать Нуждой и скорбью угнетенных.\r\n\r\nНам жизнь дана, чтоб до конца Бороться с тьмой, бороться с ложью\r\nИ сеять в братские сердца Одну святую правду Божью.\r\n\r\nА правда в том, чтобы любить, Любить без меры, без предела,\r\nИ всем страдальцам посвятить Свой разум, кровь свою и тело.\r\n'),
  (197, 'Если ждет тебя дорога', 'Если ждет тебя дорога в неизвестный край,\r\nНа прощанье у порога думы не гадай,\r\nСлово доброе послушай и совет прими:\r\nВ этом мире гибнут души, ты свою храни, ты свою храни.\r\n\r\nЕсли ждет тебя дорога в неизвестный край,\r\nСпутником себе тревогу ты не выбирай,\r\nС ней душе одна забота и тоска в пути -\r\nВ звездном небе будто кто - то потушил огни, потушил огни.\r\n\r\nЕсли ждет тебя дорога в неизвестный край,\r\nНе суди упавших строго, лучше поднимай.\r\nМожет статься, сам в бессильи где - то упадешь.\r\nОслабеют сердца крылья, веру надорвешь, веру надорвешь.\r\n\r\nЖизнь кипит вокруг, как море, бьет тебя волной,\r\nИ в твоем огромном горе ты для всех чужой.\r\nПомощь есть для всех у Бога, ты к Нему взывай,\r\nЕсли ждет тебя дорога в неизвестный край, в неизвестный край.\r\n'),
  (198, 'Здесь на земле, мы только странники', 'Здесь на земле, мы только странники,\r\nИ часто путь далекий ждёт.\r\nПорою ноги от усталости болят,\r\nНо в небе Родина нас ждёт!\r\n\r\nПрипев:\r\nТам, там, там, в небесах, Ангелы поют.\r\nВ синих, синих, синих небесах Родина нас ждет!\r\n\r\nПорою мы друзей встречаем,\r\nИ снова дальний путь зовёт.\r\nУвидимся у ног Спасителя Христа,\r\nВедь в небе Родина нас ждет!\r\n\r\nВ дороге дальней, дорогие друзья,\r\nВы расскажите людям всем,\r\nКак велика любовь Спасителя Христа,\r\nЧто в небе Родина нас ждет!\r\n'),
  (199, 'Из праха коры земной Ты создал меня', 'Из праха коры земной Ты создал меня,\r\nДух жизни вдохнул Ты Свой, Создатель мой.\r\nЯ счастлив с Тобою, Господь, Мой Бог и Творец,\r\nВедь я - творенье Твое, Ведь я - человек\r\nЯ счастлив с Тобою, Господь, Мой Бог и Творец,\r\nВедь я - творенье Твое, Ведь я - человек.\r\n\r\nПовесил Ты шар земной ни на чем,\r\nИ дал ему путь Ты Свой - закон святой.\r\nВсе мирозданье поет Хвалу Тебе,\r\nКак чудно устроил Ты Все на земле!\r\nВсе мирозданье поет Хвалу Тебе,\r\nКак чудно устроил Ты Все на земле!\r\n\r\nТы - надежда моя, Ты - сила моя!\r\nСредь бурь и страданий земных - Опора моя.\r\nЯ счастлив с Тобою Господь, Создатель миров,\r\nВ Тебе, всесильный Бог, Познал я любовь.\r\nЯ счастлив с Тобою Господь, Создатель миров,\r\nВ Тебе, всесильный Бог, Познал я любовь.\r\n'),
  (200, 'Иисус, о Тебе я пою', 'Иисус, о Тебе я пою,\r\nСветом Ты озарил мою душу.\r\nЯ за все благодарен Тебе\r\nИ во всем вижу я Твою руку\r\n\r\nПрипев:\r\nЗнаю я, Ты всегда со мной.\r\nВ обещании Своем Ты верен\r\nКровь Твоя за меня лилась\r\nЧтобы в небо открыть мне двери\r\n\r\nСколько раз Ты меня хранил\r\nНа крутых поворотах жизни\r\nТы избрал меня, искупил\r\nИ наполнил Собой моё сердце\r\n\r\nЯ живу и покоюсь в Тебе\r\nТы надежда моя и радость\r\nИисус, счастье только в Тебе\r\nВ Тебе жизнь моя и отрада.\r\n'),
  (201, 'Зажги в моем сердце свечу', 'Зажги в моем сердце свечу,\r\nИ пусть освещает она\r\nПредвечный закон бытия,\r\nЗажги в моем сердце свечу.\r\n\r\nПрипев:\r\nОсвяти нас, сохрани нас,\r\nПомоги нам, Боже наш!\r\n\r\nО, не уходи от меня,\r\nИначе погаснет свеча,\r\nЗадуют ее силы злые,\r\nО не уходи от меня.\r\n\r\nИ я испрошу у Тебя,\r\nИ чтоб не погасла свеча,\r\nЛюбить, освещая, горя,\r\nО, я испрошу у Тебя.\r\n\r\nЗажги в моем сердце свечу\r\nИ крепче ее закрепи,\r\nИисус, Дух Святой и Отец,\r\nЖиви в моем сердце живи.\r\n'),
  (202, 'Как много звезд на небесах', 'Как много звезд на небесах\r\nГорит в бесчисленных лучах!\r\nНо лучше звезд всех красота\r\nЛюбви Божественной Христа\r\n\r\nПрипев:\r\nЛюбовь Христа!\r\nЛюбовь Христа!\r\nЛюбовь Христа!\r\nОна светлее всех светил.\r\n\r\nО сердце, торжествуй, всегда!\r\nКак Вифлеемская звезда,\r\nЛюбовь Христа в пути твоем\r\nВедет тебя в Отцовский дом.\r\n\r\nПришел Христос, с тех пор она\r\nДля всех искупленных видна;\r\nИ в мире лишь она одна\r\nТьму сердца просветить сильна.\r\n\r\nНад морем, сушью, ночью,\r\nДнем-всегда горит она огнем\r\nИ льет с небес свой кроткий свет\r\nМне в утешенье в  мире бед.\r\n\r\nОна ведет меня к Христу\r\nСквозь жизни мрак и темноту;\r\nЯ на нее хочу взирать\r\nИ ей одной лишь доверять.\r\n'),
  (203, 'Иисус - в нашем сердце!', 'Иисус - в нашем сердце! Нам, молодым,\r\nНет лучше той доли, чем быть вместе с Ним!\r\nИисус - в нашем сердце! Мы с Ним живем,\r\nОн нам скала и опора во всем.\r\n\r\nПрипев:\r\nОн - в нашем сердце! Он лишь один.\r\nДивный Учитель, мы счастливы с Ним!\r\nОмыты мы Кровью Его дорогой,\r\nИисус - в нашем сердце, Спаситель благой.\r\n\r\nИисус - в нашем сердце! Радость лишь в Нем!\r\nИисус в нашем сердце - победа над злом!\r\nРадость мирская не даст ничего,\r\nЛишь в Иисусе имеем мы все.\r\n\r\nИисус - в нашем сердце! Он ждет тебя,\r\nДает тебе счастье лишь Он навсегда!\r\nПрими Его в сердце, следуй за Ним,\r\nТебе предлагает Он дивный Свой мир.\r\n'),
  (204, 'Когда Христос меня простил', 'Когда Христос меня простил\r\nИ землю в небо превратил\r\nСреди скорбей, борьбы земной,\r\nТам небо, где Христос со мной!\r\n\r\nПрипев:\r\nХвала Христу! Он спас меня,\r\nПростил грехи, открыл Себя.\r\nНа суше, в море, над землей,\r\nТам небо, где Христос со мной!\r\n\r\nНебесный мир мне был далёк,\r\nКогда Иисус меня привлёк,\r\nКогда вошёл Он в жизнь мою,\r\nС тех пор всегда о Нём пою!\r\n\r\nНе важно, где теперь живу:\r\nНа высоте, внизу, во рву,\r\nВ стране родной, в стране чужой,\r\nТам небо, где Христос со мной!\r\n'),
  (205, 'Милости Твоей полна вся земля', 'Милости Твоей полна вся земля,\r\nМилости Твоей  полна  жизнь моя,\r\nМилость Свою Ты превознёс над судом,\r\nИскупил меня и ввёл в Свой дом.\r\n\r\nПрипев:\r\nТы спас меня  и  оправдал,\r\nОсвободил от вины,\r\nТы спас меня и   оправдал,\r\nОсвободил от вины.\r\nБлагодарю  Тебя,\r\nБлагодарю  Тебя!\r\n'),
  (206, 'Тихо сгущаются тени', 'Тихо сгущаются тени, Светит в окошко луна,\r\nМама стоит у постели, Молится Богу она.\r\nЯ знаю, почему У детей сладки сны,\r\nПотому что о них Молятся мамы.\r\n\r\nЕсли беда постучится, Кто же еще, как не ты,\r\nСтанет Иисусу молиться, Чтобы все беды прошли?!\r\nЯ знаю, почему Так легко мне всегда,\r\nПотому что о мне Молится мама.\r\n\r\nВремя летит незаметно, в каждом исчезнувшем дне,\r\nЗнаю, что ты, как и прежде, Богу молилась о мне.\r\nЯ знаю, почему так легко мне всегда,\r\nПотому что о мне молится мама.\r\n'),
  (207, 'Иисус, Ты мой лучший Друг!', 'Иисус, Ты мой лучший Друг!\r\nТы так близок, когда мне трудно,\r\nКогда сердце заноет вдруг,\r\nТы один Утешитель чудный.\r\nБез Тебя нет покоя мне,\r\nБез Тебя лишь одно томленье,\r\nЛишь в Тебе нахожу вполне\r\nДля души удовлетворенье.\r\n\r\nПрипев:\r\nЯ любовью Твоею живу,\r\nИ она мое сердце греет.\r\nСвоим Другом Тебя я зову,\r\nИ никто мне Тебя не заменит.\r\n\r\nТы Один понимаешь все,\r\nТы мечты мои знаешь и тайны.\r\nТы со мной! Что же нужно еще\r\nМне искать на земле печальной?\r\nКраток стих мой и беден язык,\r\nЧтобы излить от души благодарность.\r\nО, Иисус, как в любви Ты велик,\r\nТы и счастье мое, и радость!\r\n'),
  (208, 'Когда вы соберётесь вдвоём', 'Когда вы соберётесь вдвоём или втроём,\r\nКогда вы соберётесь о имени Моём,\r\nЯ вас не позабуду и в этот самый час,\r\nВ ту самую минуту Я буду среди вас.\r\n\r\nЯ вместе с вами сяду за трапезу любви,\r\nЛишь словом или взглядом Меня ты позови.\r\nИ чтоб не попросили мы вместе у Отца,\r\nВ Его любви и силе исполнить до конца.\r\n\r\nНе вы меня избрали - Я каждого избрал.\r\nНо всё же не рабами - друзьями вас назавал.\r\nЛюбите же друг друга, как возлюбил Я вас.\r\nВот для меня услуга, вот главное сейчас!\r\n'),
  (209, 'Каждый день настойчиво', 'Каждый день настойчиво и звучно\r\nКаждый день настойчиво и звучно\r\nМы в молитве просим Бога: “Дай,\r\nДай на каждый день нам хлеб насущный.\r\nБез любви своей не оставляй”.\r\n\r\nПрипев:\r\nПросим мы как маленькие дети\r\nИми мы и есть перед Творцом\r\nИ с надеждой светлою воздеты\r\nНаши руки пред Его лицом.\r\n\r\nИ идут нам щедрости от Бога\r\nИ за то мы гимн Ему поем,\r\nНо порою видим, что немного\r\nМы в ответ Твоей любви даем\r\n\r\nВедь любовь сдвигает даже горы\r\nЧерез шторм проводит корабли\r\nЧье же мы познали в жизни горе,\r\nГде, кому мы в жизни помогли\r\n'),
  (210, 'Как прекрасно все то', 'Как прекрасно все то, что Твое,\r\nМне повсюду Твой слышится голос,\r\nВетром в листьях звенит и поет,\r\nВ сердце шепчет, как зреющий колос.\r\nЭти горы, покрытые мхом,\r\nЭти волны, покрытые пеной,\r\nЭтот берег с горячим песком,\r\nЭто солнце в бескрайней Вселенной.\r\n\r\nПрипев:\r\nБоже мой, это Ты, это Ты;\r\nЯ с Тобою повсюду встречаюсь,\r\nКогда рву мимоходом цветы,\r\nИль на чей-то поклон отвечаю.\r\n\r\nМне повсюду видна твоя кисть,\r\nТвой резец и Твой почерк с размахом,\r\nВ детском взоре, что светел и чист,\r\nИ в цветущих на поле ромашках.\r\nЭто Ты нам даруешь друзей,\r\nЭто Ты научил нас трудиться,\r\nПотому я хочу все сильней\r\nИ все чаще усердней молиться.\r\n\r\nЭто Ты меня петь научил,\r\nПотому моя песнь не сполкает.\r\nЭто Ты в меня свет Свой пролил,\r\nОн и в вечности не угасает.\r\nКак прекрасно все то, что Твое,\r\nПред величьем Твоим я немею.\r\nЭто Ты мне, Спаситель, даешь\r\nВсе то доброе, что я имею.\r\n'),
  (211, 'Льётся тихий вечер', 'Льётся тихий вечер над землёю.\r\nТихо снег ложится на поля.\r\nПотрескивают свечи,\r\nА мы сидим все вместе\r\nИ думы об одном , лишь о Христе:\r\n\r\nПрипев:\r\nО, дай нам сердце чистое,\r\nКак снега сибирские,\r\nГорячие, как солнце над землёй.\r\n\r\nБледный лунный свет проник в окошко.\r\nЖёлтая завидует луна,\r\nЧто мы сидим все вместе\r\nПлечом к плечу и песню\r\nПоём лишь об одном, лишь о Христе.\r\n\r\nПролетают мысли, расстоянья,\r\nПроплывают годы и моря,\r\nИ видят, что все вместе\r\nСидят друзья и песню\r\nПоют лишь об одном, лишь о Христе.\r\n\r\nНо пройдут те дни все без возвратно,\r\nГолову покроет седина,\r\nНо мы споём все вместе\r\nОпять всё ту же песню,\r\nВсё ту же песню юности своей:\r\n'),
  (212, 'Жёлтые листья медленно', 'Жёлтые листья медленно кружат.\r\nЗемлю омыли косые дожди.\r\nВетры поют свои грустные песни.\r\nВянут цветы, холода настают.\r\n\r\nВ сердце моём тоже осень настала,\r\nА кругом лето, И птицы поют.\r\nСтало тепло, а душа так устала,\r\nИ холода все быстрей настают.\r\n\r\nГде потерял я любовь, упованье?\r\nГде потерял Иисуса Христа?\r\nГде появились в моей жизни тучи\r\nИ заслонили собою Творца?\r\n\r\nО Иисус, где Ты есть? — Погибаю,\r\nЯ одинок, будь Ты спутник мне.\r\nСердце согрей Своей вечной любовью,\r\nИ зацветёт весна снова в душе.\r\n'),
  (213, 'Научи меня, мой Господь', 'Научи меня, мой Господь, во всём\r\nВолю исполнять лишь Твою.\r\nЧтобы мне по слову Твоему доказать,\r\nКак Тебя я, Спаситель, люблю\r\n\r\nПрипев:\r\nНаучи меня среди тысячи\r\nГолос слышать лишь Твой,\r\nНаучи меня за Тобой идти\r\nВ край желанный, дорогой.\r\n\r\nНаучи меня в грешном мире жить,\r\nЧтобы свет Твой сиял во мне,\r\nГде не может скрыться город большой,\r\nСтоя на высокой горе.\r\n\r\nНаучи меня жизнь свою прожить,\r\nЧтоб в конце услышать её:\r\nДобрый верный раб скорее войди,\r\nВ радость Господина своего.\r\n'),
  (214, 'Факел веры угасает', 'Факел веры угасает,\r\nПесни мира замирают,\r\nНет надежды, нет любви,\r\nО мой брат, о мой друг, о где же ты?\r\n\r\nВсе забыл ты и забросил,\r\nКолос веры грубо скошен,\r\nТы оставил всех друзей,\r\nО мой друг, о мой брат, вернись скорей.\r\n\r\nТы покинул, друг, собранье,\r\nВидит Бог твои страданья,\r\nОн зовет тебя: Приди!\r\nТы на зов, ты на зов Его спеши.\r\n\r\nДруг мой бедный, где блуждаешь,\r\nГде напрасно ты страдаешь?\r\nМрак в душе твоей сильней.\r\nО мой друг, о мой брат, вернись скорей!\r\n\r\nТихо в небе песни льются,\r\nТо рыданья вдруг прорвутся;\r\nЭто слезы, друг, Творца\r\nЗа тебя, за тебя и за меня.\r\n\r\nСумрак ночи опустился,\r\nТы сегодня не молился,\r\nО, склони пред Ним главу\r\nИ скажи: Я у ног Твоих стою.\r\n'),
  (215, 'Иисуса Имя сладко мне', 'Иисуса Имя сладко мне.\r\nПокой и радость в Нём в душе.\r\nЛюблю твердить Его везде -\r\nТо имя Иисуса.\r\n\r\nПрипев:\r\nИисус, Тебе хвала\r\nНеизменно, навсегда.\r\nСлавьте, славьте все уста -\r\nТо имя Иисуса.\r\n\r\nСтрадальцам Имя сладко то,\r\nОтраду мир даёт Оно.\r\nЛюбовью Божией полно\r\nТо имя Иисуса.\r\n\r\nНет слов, чтоб выразить вполне\r\nКак дорого То Имя мне\r\nПрославлю в пламенной хвале\r\nТо имя Иисуса!\r\n'),
  (216, 'Как часто мы блуждаем', 'Как часто мы блуждаем, и просто забываем,\r\nЧто миром правит суета.\r\nПорой в любовь играем, грехов не замечаем,\r\nА в сердце боль и пустота.\r\n\r\nПрипев:\r\nПриди к Иисусу и скажи: я не могу так больше жить,\r\nЯ больше не хочу грешить – прости меня.\r\nПриди к Иисусу и поверь, что жизнь измениться теперь,\r\nИ что святая кровь Христа, спасёт тебя.\r\n\r\nГосподь простит любого, Он исцелит больного,\r\nНаполнит сердце добротой.\r\nЕго любовь святая, живёт и побеждает,\r\nПриносит радость и покой.\r\n'),
  (217, 'Когда бушует жизнь', 'Когда бушует жизнь, огнем меня крестя,\r\nОдно всегда твержу себе на память:\r\n\"Зачем мне унывать - имею я Христа,\r\nЧто может у меня Его отнять?!\"\r\n\r\nРазруха и война, волненья по местам,\r\nИ мор, и глад, грядущее тревожно...\r\nНе страшна мне судьба -\r\nИмею я Христа, Что может у меня Его отнять?\r\n\r\nА если иногда уносят поезда\r\nДрузей моих в далекую разлуку.\r\nЗачем мне унывать - имею я Христа,\r\nЧто может у меня Его отнять?\r\n\r\nЯ знаю, узок путь и тесны те врата,\r\nЧто нас ведут от смерти к жизни вечной.\r\nНо мне ли унывать - имею я\r\nХриста, Что может у меня Его отнять?\r\n'),
  (218, 'Как олени к воде весною', 'Как олени к воде весною, всей душой к Тебе спешу,\r\nТы один в сердце, Ты достоин! И Тебе я петь хочу.\r\nТы один- моя жизнь, мой щит, в Тебе одном дух мой воспарит.\r\nТы один в сердце, Ты достоин поклоненья и хвалы.\r\n\r\nПрославляю Тебя всем сердцем, возношу к Тебе мольбу,\r\nТы услышишь меня, и с неба явишь вновь любовь Свою.\r\n'),
  (219, 'Хочу, Господь, сильней', 'Хочу, Господь, сильней Тебя любить.\r\nХочу, Господь, всегда с Тобою быть,\r\nХочу, чтоб Ты меня не оставлял\r\nИ сердце грешное мое любовью наполнял.\r\n\r\nХочу, Господь, с Тобой достичь страны.\r\nГде нет несчастья, скорби и нужды.\r\nГде нету лжи, обмана и вражды,\r\nГде лишь любовь и мир живут\r\nв стране чудесной той.\r\n\r\nХочу быть верным, Господи, Тебе\r\nНа этой развращенной злой земле.\r\nХочу пройти весь путь земной с Тобой,\r\nВедь одному так тяжело.\r\nТы будь Хранитель мой.\r\n'),
  (220, 'Слышишь ли ты голос Божий?', 'Слышишь ли ты голос Божий?\r\nНежно для тебя звучит,\r\nУ дверей стоит Прохожий,\r\nСлушай, что Он говорит.\r\n\r\nПрипев:\r\nНежно так Христос тебя зовет,\r\nПоспеши к Нему, тебя Он ждёт.\r\nО, приди к Нему скорее,\r\nСердца дверь открой сильнее,\r\nМир найдешь ты для души,\r\nЛишь к Иисусу поспеши.\r\n\r\nЗов любви Его ты слышишь,\r\nОн зовет тебя, мой брат.\r\nТолько Им одним ты дышишь,\r\nТолько Им одним богат.\r\n\r\nПрипев:\r\nВ двери сердца я давно стучу,\r\nОтвори, войти к тебе хочу,\r\nОбратись ко Мне скорее,\r\nЯ любовью обогрею, мир найдешь\r\nТы для души, о не медли, поспеши.\r\n\r\nНичего, что ты споткнулся,\r\nПоспеши к Нему опять.\r\nТы упал, с грехом столкнулся,\r\nМожет Он тебя поднять.\r\n'),
  (221, 'Церковь Божия, все вместе', 'Церковь Божия, все вместе,\r\nВосхвалим Царей Царя.\r\nОн есть любящий Отец наш,\r\nА мы все Его семья.\r\n\r\nПрипев:\r\nАллилуйя, аллилуйя!\r\nГоспода благословим,\r\nЧто дал радость неземную,\r\nПребывать в общеньи с Ним.\r\n\r\nСлава Богу, Слава Богу,\r\nМы Отца благодарим.\r\nЗа Иисуса Кровь святую,\r\nЧто её за нас пролил.\r\n\r\nИ сегодня в день воскресный,\r\nВсех Ты нас благослови.\r\nИ пошли нам Дух Небесный,\r\nЧтоб пребыть в Твоей любви.\r\n\r\nГосподи, о, аллилуйя,\r\nДушам радость ты даёшь.\r\nВсех нас Духом наполняешь,\r\nИ в обитель всех ведёшь.\r\n'),
  (222, 'Когда в Твои слова вникаю', 'Когда в Твои слова вникаю, Христос, Спаситель дивный мой,\r\nТогда всё глубже понимаю, что без Тебя я – прах земной.\r\nТы есть моя Лоза святая, а я – лишь веточка Твоя,\r\nТвоими соками питаюсь  я, мой Спаситель, от Тебя.\r\nТы для меня Лоза святая, а я – лишь веточка Твоя,\r\nТвоими соками питаюсь я, мой Спаситель, от Тебя.\r\n\r\nТвой сок во мне всё оживляет, несёт мне радость и покой,\r\nДуша так близко ощущает Тебя, Господь, Спаситель Мой.\r\nКогда ж случается порою, не пребываю я с Тобой,\r\nТогда терзаюсь я душою, не находя душе покой.\r\nКогда ж случается порою, не пребываю я с Тобой,\r\nТогда терзаюсь я душою, не находя душе покой.\r\n\r\nО, как слова Твои правдивы, что без Тебя ничтожны мы,\r\nИ на дела у нас нет силы в греховном мире суеты.\r\nИ потому мое желанье - в Тебе, Христос мой, пребывать,\r\nЧтоб в жизни все мои деянья Твоею силой совершать.\r\nИ потому мое желанье - в Тебе, Христос мой, пребывать,\r\nЧтоб в жизни все мои деянья Твоею силой совершать.\r\n\r\nХристос, Ты в мире путь для жизни, дающий смысл для бытия,\r\nЦеною жизни дверь в отчизну открыл ты грешникам, как я.\r\nТы для меня Лоза святая, а я – лишь веточка Твоя,\r\nТвоими соками питаюсь, дай пребывать в Тебе всегда.\r\nТы. для меня Лоза святая, а я – лишь веточка Твоя,\r\nТ.воими соками питаюсь, дай пребывать в Тебе всегда.\r\n'),
  (223, 'К Небу взор я возношу', 'К Небу взор я возношу\r\nИ Тебя, Господь, прошу:\r\nБудь со мной как верный друг,\r\nПусть хоть свет, хоть мрак вокруг.\r\n\r\nПрипев:\r\nРядом с Тобой идти — цель лишь одна,\r\nЧашу Твою испить пусть хоть до дна.\r\nРадость Тебе дарить,\r\nЗа всё благодарить буду я всегда.\r\n\r\nЖизнь моя в Твоих руках,\r\nХоть я пред Тобою прах.\r\nГосподи, Твоя любовь\r\nГреет сердце вновь и вновь.\r\n\r\nДень за днём идут года,\r\nКак с вершины гор вода,\r\nТак мы к вечности идём,\r\nГде ждёт встреча со Христом.\r\n'),
  (224, 'Как к источнику лань', 'Как к источнику лань,\r\nТак душа моя жаждет\r\nЛишь к Тебе, Святый Боже,\r\nВсе летит и летит.\r\nЛишь к Тебе, о мой дивный,\r\nСреди дня, среди ночи\r\nПоднимаю я очи,\r\nХоть слеза в них блестит.\r\n\r\nГде безмерно веселье,\r\nГде безмерно неверье,\r\nТам ведь нет утешенья\r\nДля души без Христа.\r\nЯ найду утешенье,\r\nТолько в братском общеньи,\r\nГде любовь есть святая,\r\nГде царит чистота!\r\n\r\nТы царишь над мирами,\r\nНад землей, над морями!\r\nТы мой Царь и спаситель,\r\nМой Господь и Творец.\r\nИ к Тебе – Богу жизни,\r\nСловно к лилии пчелка,\r\nИ, как лань к потоку,\r\nЯ стремлюсь всей душой!\r\n'),
  (225, 'Люблю, Господь, Твой дом', 'Люблю, Господь, Твой дом -\r\nЧертог любви Твоей;\r\nЛюблю я Церковь из людей,\r\nИскупленных Христом.\r\nЛюблю я Церковь из людей,\r\nИскупленных Христом.\r\n\r\nЛюблю, предвечный Бог,\r\nЯ Церковь - Твой алмаз;\r\nТы, как зрачок лучистых глаз,\r\nХранишь её порог.\r\nТы, как зрачок лучистых глаз,\r\nХранишь её порог.\r\n\r\nО Церкви дорогой,\r\nСобранье душ живых,\r\nВзываю сердцем и душой\r\nВсегда в мольбах своих.\r\nВзываю сердцем и душой\r\nВсегда в мольбах своих.\r\n\r\nЯ рад иметь всегда\r\nОбщенье духа с ней,\r\nНести все тяжести труда\r\nИ крест её скорбей.\r\nНести все тяжести труда\r\nИ крест её скорбей.\r\n\r\nЯ знаю, разделю\r\nС ней славу я Твою;\r\nС ней буду петь я пред Тобой\r\nСпасённою душой.\r\nС ней буду петь я пред Тобой\r\nСпасённою душой.\r\n'),
  (226, 'К Тебе спешат мои мечты', 'К Тебе спешат мои мечты, о, Христос!\r\nК Тебе я поднимаю взор, полный слез.\r\nТебе я сердце приношу – дай покой!\r\nУслышь меня, утешь меня. Боже мой!\r\n\r\nТвою красу не превзойдут все цветы.\r\nИсточник жизни неземной – только\r\nТы! И солнце светит в свете дня пред Тобой!\r\nО, ниспошли любовь огня. Боже мой!\r\n\r\nВезде, всегда следит Твой взор от миров,\r\nТебе всегда поет в псалмах свист ветров,\r\nТы отдал все за нас скорбя, в жизни сей\r\nДолжны всегда хвалить Тебя люди все.\r\n\r\nК Тебе с нуждой я прихожу, мой Христос.\r\nО, дай всегда мне побороть свою плоть,\r\nЧтоб Дух Святой царить лишь мог,\r\nО, Творец, Услышь меня, мой Царь и Бог, мой Отец!\r\n\r\nТы – Пастырь добрый, верный мой, о, Христос!\r\nСам укрепи Своей рукой в мире слез,\r\nЧтоб мне достигнуть славы той, где царишь,\r\nСтраны небесной, неземной, о, услышь!\r\n'),
  (227, 'Когда в море бушует волна', 'Когда в море бушует волна,\r\nВетер рвёт твои паруса,\r\nИ бессмысленной стала борьба,\r\nСпой тогда:\r\n\r\nПрипев:\r\nИисус, Ты моя скала. Иисус, Ты живая вода.\r\nИ Голгофа – завет в крови. Верен Ты!\r\nУпованье моё и покой,я однажды вернусь домой\r\nИ услышу Твои слова: «Ждал Я тебя!»\r\n\r\nВ нашей жизни любая беда,\r\nСловно в море крутая волна,\r\nГубит тех, кто бросил грести,\r\nСвой крест нести.\r\n\r\nУкрепившись любовью Отца,\r\nВерой в жертву Иисуса Христа,\r\nСилой Духа Святого всегда\r\nПой слова:\r\n'),
  (228, 'Кто сотворил весь мир', 'Кто сотворил весь мир,\r\nМоря, озера, лес\r\nИ людям всем открыл\r\nВрата небес?\r\nРаскрасил Кто цветы?:\r\nКто место дал заре\r\nИ движет Кто ручьи\r\nПо всей земле?\r\n\r\nМой Бог, все это - Ты.\r\nХвалу и честь прими!\r\nРожден Тобою я,\r\nТы - жизнь моя.\r\nМой Бог, все это - Ты.\r\nХвалу и честь прими!\r\nТы все предусмотрел -\r\nТы мой удел.\r\n\r\nКто жизни смысл дает?\r\nПомощник Кто в пути?\r\nИ Кто тебя спасет\r\nОт зла и тьмы?\r\nКто посылает свет\r\nИ дождь земле сухой?\r\nКто сохранит от бед\r\nИ даст покой?\r\n'),
  (229, 'Край чудесный нас', 'Край чудесный нас ждет там на небе,\r\nГде окончится труд и печаль!\r\nОт скорбей ястремлюсь к той Отчизне,\r\nГде есть вечная радость и мир.\r\n\r\nПрипев:\r\nКрай чудесный! Край чудесный!\r\nСердце радостно рвется к тебе.\r\nКрай чудесный! Край чудесный!\r\nТам ведь ждет нас Спаситель к Себе.\r\n\r\nКрай чудесный, где Ангелов пенье\r\nРаздается, как шум многих вод,\r\nСлаву Агнцу возносят святые,\r\n\"Аллилуйя!\" - они все поют.\r\n\r\nКрай чудесный, где в свете небесном\r\nБудем видеть мы славу Творца;\r\nВ тени пальм и в долинах чудесных\r\nТам я буду счастлив без конца.\r\n\r\nВ край чудесный мы скоро прибудем\r\nИ увидим Того, Кто нам мил;\r\nТам утихнет скорбящее сердце:\r\nВедь там вечная радость и мир.\r\n'),
  (230, 'Куда бы я идти хотел', 'Припев:\r\n\r\nЯ знаю, что Ты там,\r\nИ в море тёмном, и в вышине —\r\nВезде Ты близок к нам.\r\n\r\nВедь пред Тобой открыто всё,\r\nНам нечего таить,\r\nИ сердца помышления\r\nТы можешь все узреть.\r\n\r\nИ пред моим рождением,\r\nГосподь, Ты знал меня,\r\nТвоя рука пронзённая\r\nК Тебе влечёт меня.\r\n\r\nГосподь, ведь вся вселенная\r\nЕсть дело Твоих рук,\r\nПрославить я хочу Тебя,\r\nМой Бог, как Ты велик.\r\n'),
  (231, 'Любовь Христа безмерно велика', 'Любовь Христа безмерно велика\r\nНачала нет и льется как река,\r\nИ как волна лобзает берега,\r\nИ глубока, светла и широка.\r\n\r\nПрипев:\r\nИ если б не было любви,\r\nЛюбви Христа, мы б не смогли\r\nИметь надежду вечно жить\r\nИ, как Спаситель, всех людей любить.\r\n\r\nПриди скорей, скоре, тебя Он ждет\r\nДавно стоит у каменных ворот,\r\nСтучит к тебе, открой, уже пора,\r\nПрими любовь без злата, серебра.\r\n\r\nО, если б знал живущий на земле\r\nЛюбовь, Христа! Лишь ею дышет все!\r\nИ если б Бог отнял любовь на миг\r\nТо этот мир давно б уже погиб.\r\n\r\nОн возлюбил тебя, меня давно,\r\nОн возлюбил и тех, кто лишь одно\r\nТвердили: \"Нет!\" И поднимали смех,\r\nА Он с любовью призывает всех.\r\n'),
  (232, 'Мы все стремимся в край родной', 'Мы все стремимся в край родной,\r\nГде будет вечной правды свет,\r\nНо в этой жизни суетной\r\nО дай, Господь, исполнить Твой завет.\r\nНо в этой жизни суетной\r\nО дай, Господь, исполнить Твой завет.\r\n\r\nВражда, неверие вокруг,\r\nДля правды Божией - узкий путь,\r\nНо Иисус - наш лучший Друг -\r\nВ конце пути даст в небе отдохнуть.\r\nНо Иисус - наш лучший Друг -\r\nВ конце пути даст в небе отдохнуть.\r\n\r\nНам заповедал Христос\r\nЛюбить и близких, и врагов\r\nИ не роптать в долине слез,\r\nНести евангельскую весть кругом.\r\nИ не роптать в долине слез,\r\nНести евангельскую весть кругом.\r\n\r\nПридите все к Нему скорей,\r\nЗа правой следуйте всегда,\r\nЧтоб не остаться вне дверей,\r\nПокайтесь, люди, близок день суда!\r\nЧтоб не остаться вне дверей,\r\nПокайтесь, люди, близок день суда!\r\n'),
  (233, 'Неужели уйдешь ты', 'Неужели уйдешь ты, не приняв Христа,\r\nЧтоб дальше во мраке блуждать?\r\nПрощеньем Божественным пренебрежешь,\r\nНе приняв Его благодать?\r\n\r\nПрипев:\r\nНеужели уйдешь, неужели уйдешь,\r\nНеужели уйдешь ты, не приняв Христа?\r\nНеужели уйдешь?\r\n\r\nНеужели уйдешь ты, хоть другом своим\r\nТы мог бы Христа приобресть?\r\nОн был бы Хранителем верным твоим,\r\nОценишь ли ты эту честь?\r\n\r\nИисус из любви свою жизнь положил,\r\nЧтоб грешников освободить,\r\nИ чудный источник спасенья открыл,\r\nГде можно грехи все омыть.\r\n\r\nО друг, в этой жизни Христа не отринь,\r\nПокой даст тебе только Он.\r\nВнемли же спасенью, сомненья откинь,\r\nПокайся - и будешь спасен.\r\n'),
  (234, 'Лучшие дни нашей жизни', 'Лучшие дни нашей жизни,\r\nСвежие силы весны молодой\r\nМы посвятим Иисусу\r\nВ дар для Него дорогой!\r\nЧто может быть нам приятней,\r\nКак Иисусу служить,\r\nЖизни простор необъятный,\r\nСердце Христу подарить.\"\r\n\r\nСлышишь, как Он призывает?\r\nЧас посвященья для нас настал!\r\nБольше мы медлить не можем:\r\nОн ведь нам жизнь даровал!\r\nБратья, поднимем же знамя\r\nС именем чудным Христа!\r\nПусть загорается пламя\r\nЖертвенной крови Христа.\r\n\r\nСмело во имя Иисуса,\r\nБратья, вперед на борьбу с грехом!\r\nТысячи гибнут в пороках,\r\nС вестью благой к ним пойдем.\r\nПусть нас постигнут гоненья,\r\nСмерть за Христа не страшна!\r\nС радостным пеньем хваленья\r\nМы одолеем врага!\r\n');
INSERT INTO `SONG` (`ID`, `DESCRIPTION`, `TEXT`) VALUES
  (235, 'Любовь Христова', 'Любовь Христова - она как солнце\r\nЧто светит в небе над всей землёй.\r\nЛюбовь Христова - что поравняться,\r\nМогло бы в мире с любовью той?\r\n\r\nПрипев:\r\nЛюбовь святая, любовь Христа\r\nОна повсюду со мной была\r\nКогда я падал, безчётно, поднимала\r\nИ снова к небу меня вела.\r\n\r\nЛюбовь Христова - там на Голгофе\r\nСтрадал спаситель за грешных нас.\r\nЛюбовь Христова - в оковах тяжких\r\nОт страшной смерти Христос нас спас.\r\n\r\nЛюбовь Христова - она и сегодня\r\nК тебе всё ближе - приди теперь!\r\nПриди к Иисусу, пока не поздно\r\nПока открыта спасенья дверь!\r\n'),
  (236, 'Он шел в жару и в зной', 'Он шел в жару и в зной, В пыли больших дорог,\r\nУчил любить всех и прощать обиды.\r\nОн с мытарями жил, Не знал ни в ком врага,\r\nСклонялся к тем, кто был в слезах и горе.\r\n\r\nПрипев:\r\nУчитель, свой путь пройти Я должен так, как сделал это Ты.\r\nУчитель! Вокруг есть люди, И я должен их любить, как Ты,\r\nУчитель. Ведь не легко нести чужое бремя Так, как Ты,\r\nУчитель я все смогу, но только Будь всегда со мною Ты.\r\n\r\nОн принял грош вдовы И Магдалины плач.\r\nОн знал, что надо их любить, прощать.\r\nИ Никодиму путь во тьме Он указал,\r\nИ в Иордане принял Сам крещенье.\r\n\r\nИ мы пойдем в жару, В пыли больших дорог,\r\nА Он научит нас любви, прощенью,\r\nИ мытаря принять, и зло забыть,\r\nИ людям дать добро и утешенье.\r\n'),
  (237, 'Мы идем тропою узкой', 'Мы идем тропою узкой,\r\nТруден и тернист наш путь,\r\nНо всегда Спаситель с нами,\r\nЭто, друг мой, не забудь.\r\n\r\nХоть идешь ты спотыкаясь,\r\nПадаешь и трудно встать,\r\nНе теряй надежды, друг мой,\r\nНо вставай, иди опять.\r\n\r\nЕсли ты во тьме блуждаешь\r\nИ не знаешь, как идти,\r\nНе смущайся - Бог с тобою,\r\nОн поможет на пути.\r\n\r\nСами мы идти не в силах,\r\nТолько с Ним пройдем свой путь.\r\nСкоро Он введет нас в дом Свой,\r\nЭто, друг мой, не забудь.\r\n'),
  (238, 'Звучит труба, пора нам', 'Звучит труба, пора нам выступать в поход,\r\nГосподь зовет, Он - победитель наш!\r\nВперед, вперед! Врагов не бойся и невзгод,\r\nХвала Ему! Он - победитель наш.\r\n\r\nПрипев:\r\nЕдинодушно на пути\r\nСтремимся к вечной цели мы.\r\nСпасенных войско благослови Ты,\r\nЧтоб ревностью горели мы.\r\n\r\nХоть силен враг, он против нас не устоит,\r\nМы победим его чрез Агнца кровь.\r\nХристос – наш Вождь, живая вера, крепкий щит,\r\nЕдины мы чрез Божию любовь.\r\n\r\nО человек! Доколе будешь ты врагом?!\r\nОставь свой грех, вступи в ряды Христа!\r\nВ смирении вернись скорей в Отцовский дом\r\nДля славного и вечного труда.\r\n'),
  (239, 'Молодым капитанам веры', 'Молодым капитанам веры,\r\nНаправляющим в небо путь,\r\nЯ желаю веры без меры\r\nИ чтоб мужеством крепла грудь!\r\n\r\nНа пути будут ветры гонений,\r\nИ затишье, как мели обман,\r\nИ подводные камни сомнений,\r\nИ неверья гнетущий туман.\r\n\r\nНо для тех, кто Христом научен\r\nСилой веры стихию смирять,\r\nЧерез самые мрачные тучи\r\nБудет солнце победы сиять.\r\n\r\nИ при виде широкого моря\r\nЧеловеческих слез и скорбей,\r\nНе покиньте томящихся в горе\r\nПозабывших о Боге людей.\r\n\r\nКапитаны! Повыше знамя\r\nЛучезарной Божьей любви!\r\nБлаговестия яркое пламя,\r\nНа просторах людских живи!\r\n'),
  (240, 'Мы бодрей на жизненном', 'Мы бодрей на жизненном пути пойдем,\r\nЕсли будем доверять Иисусу.\r\nСилу Божию в работе мы найдем,\r\nЕсли будем доверять Иисусу.\r\n\r\nПрипев:\r\nПросветлеет путь тогда,\r\nРадость будет навсегда,\r\nНе смутит враг никогда,\r\nЕсли будем доверять Иисусу.\r\n\r\nОбещанья Божьи станут нам ясней,\r\nЕсли будем доверять Иисусу.\r\nБлагодать наполнит сердце все полней,\r\nЕсли будем доверять Иисусу.\r\n\r\nВолю Божию научимся творить,\r\nЕсли будем доверять Иисусу,\r\nПостоянно взоры к небу возносить,\r\nЕсли будем доверять Иисусу.\r\n'),
  (241, 'За приятный солнца свет', 'За приятный солнца свет,\r\nЧистый воздух, дар с небес,\r\nЗа охрану с юных лет,\r\nЗа поля, луга и лес –\r\n\r\nПрипев:\r\nГосподу хвала! Господу хвала!\r\nБратьям привет, сестрам привет!\r\nВсем друзьям наш привет,\r\nМолодежный привет!\r\n\r\nЗа друзей в часы нужды,\r\nВоду, хлеб, садов плоды;\r\nЗа работу и досуг,\r\nЖизни цель и мир вокруг –\r\n\r\nЗа сиянье чудных звезд,\r\nТихий сон, уют в домах,\r\nЗа святую книгу книг,\r\nЗа Отчизну в небесах –\r\n\r\nЗа родителей моих,\r\nЗа любовь и верность их;\r\nЗа Спасителя Христа,\r\nЧто открыл небес врата, -\r\n\r\nЗа покоя славный день,\r\nЗа любви и мира сень,\r\nЗа Господень дом святой,\r\nГде мы сходимся семьей, -\r\n'),
  (242, 'Христианин, неси огонь', 'Христианин, неси огонь чудесный свой,\r\nКоторый дал тебе Христос.\r\nОн умер на кресте пожертвовав Собой,\r\nОгонь любви Он всем принёс.\r\n\r\nПрипев:\r\nГори, огонь, всегда гори!\r\nВоспламеняй собой холодные сердца\r\nИ новый мир прекрасный озари,\r\nВ котором радость без конца.\r\n\r\nВот путник, он устал, он изнемог в пути -\r\nСогрей его и ободри,\r\nСкажи, что не покинет его вечный Бог -\r\nЗасветит солнце впереди.\r\n\r\nА если путник вдруг свернет с того пути,\r\nКоторый к радости ведёт,\r\nТо ты в его груди огонь любви зажги,\r\nЧтобы он мог тот путь найти.\r\n\r\nИ если станет очень тяжело тебе -\r\nСуровый мир гасит любовь, -\r\nНе унывай! Ведь любящий Господь с тобой -\r\nОн сохранит светильник твой.\r\n'),
  (243, 'Только Ты охраняешь меня', 'Только Ты охраняешь меня темной ночью, в течение дня.\r\nТолько Ты окрыляешь мечту, чтоб любила она красоту.\r\nДаришь песни Ты мне и стихи каменистые тропы и мхи,\r\nКак я счастлив, с Тобой говоря, когда утром алеет заря.\r\n\r\nПрипев:\r\nТолько Ты, только Ты\r\nВидишь душу мою с высоты.\r\nТолько Ты, только Ты, только Ты,\r\nТолько Ты Мой единственный, Ты. - 2 раза\r\n\r\nКогда звезд загорается рой, Ты опять неизменно со мной.\r\nКогда Ты посылаешь росу, я Тебя в своем сердце несу.\r\nНет при жизни минуты одной, когда б не был Ты нежен со мной.\r\nВ моей жизненной трудной борьбе чем за это воздам я Тебе?\r\n'),
  (244, 'Пока огонь любви чудесной', 'Пока огонь любви чудесной\r\nГорит в груди волной живой,\r\nМы силы юные совместно\r\nУпотребим на труд святой.\r\n\r\nПрипев:\r\nЮность прекрасная, молодость ясная!\r\nСил, здоровья не жалей (для людей),\r\nК Богу духом пламеней (все сильней).\r\n\r\nПока сильны стремленья к небу\r\nИ много сил, здоровья в нас,\r\nДрузья, распределим умело\r\nДля славы Божьей всякий час!\r\n\r\nВедь даже старцы вспоминают\r\nС улыбкой светлой на устах\r\nПорывы юности святые,\r\nО всех друзьях и о трудах.\r\n\r\nСвоих забот у нас немного,\r\nНе будем предаваться им,\r\nНе для себя, а для другого\r\nМы свои силы отдадим.\r\n'),
  (245, 'Мы вам желаем счастья', 'Мы вам желаем счастья\r\nМы вам желаем счастья\r\nМы вам желаем счастья\r\nМы вам желаем счастья, счастья во Христе!\r\n\r\nМы вам желаем мира\r\nМы вам желаем мира\r\nМы вам желаем мира\r\nМы вам желаем мира, мира во Христе!\r\n\r\nМы вам желаем веры\r\nМы вам желаем веры\r\nМы вам желаем веры\r\nМы вам желаем веры, веры во Христе!\r\n\r\nМы вам желаем радость\r\nМы вам желаем радость\r\nМы вам желаем радость\r\nМы вам желаем радость, радость во Христе!\r\n\r\nМы вам Любви желаем\r\nМы вам Любви желаем\r\nМы вам Любви желаем\r\nМы вам Любви, Любви желаем во Христе!\r\n\r\nМы вам желаем счастья\r\nМы вам желаем мира\r\nМы вам желаем веры\r\nМы вам Любви, Любви желаем во Христе!\r\n'),
  (246, 'Книга Богом мне дана', 'Книга Богом мне дана,\r\nХоть поношена она,\r\nВспоминаю дни минувшие давно:\r\nМать читала мне тогда,\r\nНе забуду никогда,\r\nКак нам было с нею мирно, хорошо.\r\n\r\nПрипев:\r\nО святая и живая\r\nКнига Божия, люблю, люблю тебя!\r\nТы дороже с каждым днем\r\nИ ведешь прямым путем\r\nК чудной Родине, где ждет Господь меня.\r\n\r\nПро Спасителя Христа\r\nВсе читала мне она,\r\nКак любил, страдал и умер на кресте.\r\nСо слезами слушал я.\r\n\"Помни, это за тебя,-\r\nГоворила мать.- Храни Его в душе!\"\r\n\r\nЭто все прошло давно,\r\nНо как дорого оно,\r\nИ святая Книга - свет мне на пути.\r\nСлово Божие храню\r\nИ Спасителя люблю,\r\nПо стопам Его всегда хочу идти.\r\n'),
  (247, 'На свете книг не перечесть', 'На свете книг не перечесть\r\nО нашей жизни,нашем быте,\r\nНо лишь в одной Благая весть\r\nЗвучит к несчастным и забытым.\r\n\r\nПрипев:\r\nТа Книга-компас на пути,\r\nТа Книга-наш путеводитель,\r\nКогда нам нужно перейти\r\nС земли в Небесную обитель.\r\n\r\nВ той Книге всё,что нужно нам,\r\nЧтоб быть душе всегда согретой.\r\nОна дорогу к небесам\r\nНам освещает ярким светом.\r\n\r\nОна зовёт на путь святой,\r\nГде благодать рекою льётся.\r\nА как названье Книге той?\r\nТа Книга БИБЛИЕЙ зовётся.\r\n'),
  (248, 'Мы каждый день встречаемся', 'Мы каждый день встречаемся вновь\r\nС прекрасным словом, словом \"любовь\",\r\nМы каждый день за ней летим в мечтах своих,\r\nНо настоящей любви, увы, мы в этом мире не нашли,\r\nНа наш вопрос один ответ: любви без Бога нет.\r\n\r\nПрипев:\r\nЛюбовь Христа верна и чиста, она и ласкова, и нежна,\r\nОна объемлет всех людей и покорит силой своей.\r\nДержитесь этой вы любви, она вас к небу приведет,\r\nГде Иисус спасенных ждет.\r\n\r\nНо настоящая любовь приходит с синих облаков.\r\nЕё дарует вечный Бог, Творец миров.\r\nОна несется и парит, и каждый может получить\r\nБез денег, злата, серебра любовь Христа.\r\n'),
  (249, 'Господь Спаситель мой', 'Господь Спаситель мой, к Тебе взываю я,\r\nУслышь мольбу мою!\r\nУ ног Твоих святых нужду слагаю я\r\nИ всю печаль свою.\r\n\r\nЛюбовь Твоя меня нашла, поставила,\r\nСказала мне: \"Иди!\"\r\nИ к вечной радости спешить заставила,\r\nОгонь зажгла в груди.\r\n\r\nТы нежным голосом сказал душе моей:\r\n\"Не бойся бурь в пути!\r\nТы не один пойдешь, но также буду Я\r\nВсегда с тобой идти.\r\n\r\nИ если ты, скорбя, заплачешь иногда\r\nСреди борьбы и бед,\r\nУтешу Я тебя и укажу тогда\r\nНа Мой кровавый след.\r\n\r\nИль обессилишь ты средь тьмы и суеты,\r\nСклонись пред Мной в мольбе:\r\nЯ путь твой озарю, чтоб ты не пал в бою,\r\nИ силы дам тебе\".\r\n\r\nИ слово истины звучит так сладостно\r\nВ моей больной душе,\r\nИ в сердце мир царит, и дух мой радостно\r\nХвалу поет Тебе.\r\n'),
  (250, 'Мы идем, идем в страну', 'Мы идем, идем в страну, где скорби нет,\r\nГде сияет вечно дивный Божий свет;\r\nДрево жизни там, на нем плоды всегда,\r\nЗла, тревог, вражды не будет никогда!\r\n\r\nПрипев:\r\nПоспеши к Иисусу,\r\nОн тебя спасет\r\nИ твою Он душу\r\nВ рай с Собой введет!\r\n\r\nВ той стране мы встретим всех своих друзей,\r\nЧто страдали здесь под бременем скорбей,\r\nВсе прошло земное: и печаль, и труд;\r\nВ золотых венцах они Христу поют!\r\n\r\nТоржеству спасенных там предела нет.\r\nПажити живые, неба Божий свет...\r\nВ радостном общенье жизнь там без конца,\r\nВ Духе, под покровом Сына и Отца.\r\n'),
  (251, 'Ищите прежде Царства Божия', 'Ищите прежде Царства Божия\r\nИ правды Его,\r\nВсе остальное приложится вам,\r\nАллилуйя, аллилуйя!\r\n\r\nЦарствие Божие – не пища и питье,\r\nА праведность и мир,\r\nИ радость во Святом Духе,\r\nАллилуйя, аллилуйя!\r\n\r\nПросите и дано будет вам,\r\nИщите и найдете,\r\nСтучите и отворят вам,\r\nАллилуйя, аллилуйя!\r\n\r\nДоверте Богу сердце свое,\r\nНе на себя пологайтесь,\r\nБлагодарите за все пути Его\r\nАллилуйя, аллилуйя!\r\n'),
  (252, 'Не стремись земли великие', 'Не стремись земли великие дела творить,\r\nПроходящей славы не ищи,\r\nНо старайся хоть бы в малом ближним послужить,\r\nПусть твой свет сияет пред людьми.\r\n\r\nПрипев:\r\nПусть твой свет сияет пред людьми, (2р.)\r\nВ сумраке блуждающим путь правды укажи,\r\nПусть твой свет сияет пред людьми.\r\n\r\nТучи скорби, что у ближнего над головой,\r\nМожешь ты прогнать лучем любви.\r\nЗвуком песни принеси больной душе покой,\r\nПусть твой свет сияет пред людьми.\r\n\r\nИскру Божию, твой дар тебе, не угашай,\r\nНо во благо ближним примени,\r\nСо смиренным сердцем слово жизни возвещай,\r\nПусть твой свет сияет пред людьми.\r\n'),
  (253, 'Иисус, имя всех выше', 'Иисус, имя всех выше\r\nЧудный Спаситель\r\nСлавный Господь.\r\nЭммануил, Бог вечно с нами,\r\nОсвободитель, вечно живой.\r\n\r\nУста мои милость Твою поют,\r\nВечно в Тебе пребыть жаждет душа.\r\nПокров и щит, мир и любовь Тебе,\r\nДивны дела Твои, Господи сил.\r\n\r\nСпасенья Бог, Собой осветивший нас\r\nПобеду дарующий в жертве Христа.\r\nВеличия, Боже достоин Ты\r\nПревознесенный в Славу Отца.\r\n'),
  (254, 'Ночь укрыла землю тихим', 'Ночь укрыла землю тихим покрывалом сладких снов,\r\nСнова месяц воцарился среди гор, среди холмов,\r\nИ одна звезда вдруг ярко засияла среди тьмы -\r\nК нам на землю Бог явился, человеком став, как мы\r\n\r\nПрипев:\r\nРождество, Рождество! Нас Спаситель посетил,\r\nРождество, Рождество! Жизнь и радость подарил,\r\nБудем петь и ликовать, Иисуса прославлять,\r\nРождество! Рождество!\r\n\r\nО рождении Иисуса Божий Ангел возвестил,\r\nХор небесный с ликованьем пел Христу хвалебный гимн,\r\nИ волхвы пришли с востока, чтоб увидеть то Дитя,\r\nИ склониться пред Мессией и почтить царей Царя!\r\n\r\nМного бурь и испытаний ждет Иисуса впереди,\r\nПуть столь славный и великий предстоит Ему пройти,\r\nА пока семья не знает, что готовит им судьба,\r\nИ Мария обнимает драгоценное Дитя\r\n'),
  (255, 'Пой Аллилуйя Господу! ', 'Пой Аллилуйя Господу! Пой Аллилуйя Господу!\r\nПой Аллилуйя, пой Аллилуйя! Пой Аллилуйя Господу!\r\n\r\nТворец наш дивен и велик! Творец наш дивен и велик!\r\nТворец наш дивен, Творец наш дивен. Творец наш дивен и велик.\r\n\r\nГосподь простил нам все грехи. Господь простил нам все грехи.\r\nГосподь простил нам, Господь простил нам, Господь простил нам все грехи.\r\n\r\nВоздайте славу Господу! Воздайте славу Господу!\r\nВоздайте славу, воздайте славу. Воздайте славу Господу!\r\n'),
  (256, 'Надо мною лист кружится', 'Надо мною лист кружится,\r\nПожелтевший, медленно упал.\r\nОт других он отделился,\r\nБез поры, как цвет травы увял.\r\nОтчего этот лист пожелтел?\r\nМожет влаги в себе не имел?\r\nМожет быть иногда сам не видел себя. –\r\nТак и с нами бывает, друзья?\r\n\r\nПрипев:\r\nДрузья, друзья, забывать нам друг друга нельзя - 2р\r\n\r\nВспомни, как Христос трудился,\r\nПроявлял заботу и любовь.\r\nВспомни, как Отцу молился,\r\nИ за нас пролил святую кровь\r\nВот любовь, вот любовь, вот любовь!\r\nКак сильна ты святая любовь!\r\nМог бы я оценить, хоть частицу любви\r\nНа сестру иль на брата излить?\r\n\r\nЧтоб не стать как лист увядший,\r\nБольше позаботься о друзьях.\r\nЕсли кто, ты видишь, падший, -\r\nПодними и ободри любя.\r\nОтчего этот лист пожелтел?\r\nМожет влаги в себе не имел?\r\nМожет быть иногда сам не видел себя?\r\nТак зачем же с тобою друзья?\r\n'),
  (257, 'Руки Христа - это руки любви', 'Руки Христа - это руки любви,\r\nДобрые, нежные руки.\r\nТы посмотри: эти руки в крови -\r\nЗнак истязаний и муки.\r\nТы посмотри: эти руки в крови -\r\nЗнак истязаний и муки.\r\n\r\nМожет быть, ты изнемог, удручен,\r\nСовесть тебя обличает -\r\nВстань дорогая душа, это Он\r\nРуки к тебе простирает.\r\nВстань дорогая душа, это Он\r\nРуки к тебе простирает.\r\n\r\nРуки Христа извлекают из рва,\r\nСтавят на камне спасенья.\r\nО, это жизнь, а не только слова-\r\nЭто любовь и прощенье.\r\nО, это жизнь, а не только слова-\r\nЭто любовь и прощенье.\r\n\r\nРуки простерты к тебе и ко мне,\r\nПолные ласки, привета,\r\nМожно ли долго стоять в стороне\r\nИ не ответить на это?!\r\nМожно ли долго стоять в стороне\r\nИ не ответить на это?!\r\n\r\nТы посмотри, хорошо посмотри,\r\nНеотвернись равнодушно.\r\nРуки Христа- это руки любви.\r\nДруг мой, вставать тебе нужно!\r\nРуки Христа- это руки любви.\r\nДруг мой, вставать тебе нужно!\r\n'),
  (258, 'Надо мною как птица', 'Надо мною как птица\r\nБелый парус струится,\r\nИ плывет мой кораблик, разрезая волну.\r\nЧерез бури, невзгоды,\r\nЧерез тьму непогоды\r\nпод десницею Бога к отчизне плыву.\r\n\r\nПрипев:\r\nБог— моя помощь! Бог— моя крепость!\r\nВ жизненном море помощник в пути.\r\nС Ним забываю я все невзгоды;\r\nОн помогает вперед мне идти.\r\n\r\nКогда море бушует,\r\nК Богу песнь возношу я\r\nИ прошу, чтоб туман зла не затмил мне звезду.\r\nВедь звезда та Спаситель,\r\nИ в святую обитель\r\nНа нее лишь взирая, корабль свой веду!\r\n\r\nВетер жизни как прежде\r\nДует в парус надежды.\r\nИ плывем мы по морю жизни, бурь и тревог.\r\nНаша лодка — надежна,\r\nВедь она — Слово Божье,\r\nПуть тяжел наш, но с нами Спаситель и Бог!\r\n'),
  (259, 'Иисус, как Ты дорог для меня', 'Иисус, как Ты дорог для меня\r\nИисус, внемлешь Ты мольбе моей.\r\nИисус, поднимешь Ты, коль упаду;\r\nКак Ты дорог, дорог для меня.\r\n\r\nИисус, Тебя люблю я восхвалять.\r\nИисус, Ты без начала без конца.\r\nИисус, Ты умер и простил мой грех;\r\nКак Ты дорог, дорог для меня.\r\n\r\nИисус, Ты обещал прийти сюда.\r\nИисус, мы ждем Тебя, раскрыв сердца.\r\nИисус, услышь Ты наши голоса;\r\nКак Ты дорог, дорог для всех нас.\r\n'),
  (260, 'Не грусти милый друг', 'Не грусти милый друг, не томи свое сердце тоской,\r\nЕсли вдруг над тобой тучи темные небо закроют.\r\nВытри слезы с очей, если взор затуманился твой,\r\nРядом Друг, лучший Друг — Иисус, ведь всегда Он с тобой.\r\n\r\nПрипев:\r\nТвердо верь, ко благу все содействует,\r\nЕсли любишь своего Спасителя.\r\nСолнце даже из-за темных туч придет,\r\nЕсли Бог его дает.\r\n\r\nНе грусти, что порой изменяют земные друзья.\r\nИ когда вместо встреч к тебе снова приходит разлука.\r\nЗнает Он весь твой путь и его охраняет всегда,\r\nИ в ненастье тебе будет солнышко в небе светить.\r\n\r\nНе грусти, милый друг, если сердца мечта не сбылась,\r\nЗначит с этой мечтой в дом опасность сама постучалась.\r\nНо доверься Христу, Это Он охраняет твой путь.\r\nРядом Друг, лучший Друг — Иисус, ведь всегда Он с тобой.\r\n'),
  (261, 'Отрадно общенье с друзьями', 'Отрадно общенье с друзьями, где дышится сердцу легко,\r\nГде гимны рождаются сами, здесь радость, любовь и покой.\r\n\r\nПрипев:\r\nДруг друга совсем мы не знали, не знали Христовой любви.\r\nТеперь же мы близкими стали в Христовой пролитой крови.\r\n\r\nЛюбовью всесильной согреты, приблизим друг другу плечо.\r\nНет большего долга на свете, как ближних любить горячо.\r\n\r\nПусть слабый почувствует силу, холодный согреется вновь\r\nИ явлено будет пусть миру, что с нами Христова любовь.\r\n\r\nПусть тесное с Богом общенье наполнит нас Духом Святым\r\nИ сила Христа вдохновенно влечет к берегам неземным.\r\n'),
  (262, 'Небо надо мной синеет', 'Небо надо мной синеет,\r\nИли в мрачный час ночной\r\nСвет добра в душе лелею,\r\nПотому что Ты со мной!\r\nТы Своей святой рукою\r\nПосылаешь мир, покой.\r\nЯ иду Твоей тропою,\r\nТвердо зная, Ты со мной!\r\n\r\nПрипев:\r\nТы со мной и с Тобою легко мне везде,\r\nТы со мной, рядом в радости, рядом в беде.\r\nТы со мной, Твои милости вижу вокруг,\r\nТы со мной Мой Господь, самый лучший мой Друг.\r\n\r\nИ долина тени смертной\r\nИ холодный мрак ночной,\r\nНе вредят душе бессмертной,\r\nПотому что Ты со мной\r\nУкрепляешь, помогаешь\r\nИ Своей святой рукой,\r\nТы к Отчизне направляешь,\r\nМне легко - ведь Ты со мной.\r\n\r\nИ когда приду к Отчизне,\r\nЗнаю примешь, Пастырь мой.\r\nИ мне скажешь: \"В вечной жизни\r\nБудь во веки ты со Мной!\"\r\nИ паду к ногам пронзенным,\r\nИ услышав голос Твой,\r\nПрошепчу: \"Я всех счастливей,\r\nПотому что Ты со мной!\"\r\n'),
  (263, 'Пишутся прекрасные пейзажи', 'Пишутся прекрасные пейзажи\r\nВечного Художника рукой.\r\nПусть никто о Боге нам не скажет -\r\nВсе творенье говорит о Нем.\r\n\r\nСкажут звезды, скажет буйный ветер,\r\nИ колосьев золото, и лес;\r\nИ закат в прозрачный летний вечер\r\nНам откроет дверь в страну чудес.\r\n\r\nМир безмолвный нам о Боге скажет\r\nСкажет громом, молнией, грозой,\r\nСкажет солнцем и росой, что ляжет\r\nНа траву серебряной слезой.\r\n\r\nПусть никто о Боге нам не скажет,\r\nПусть не слышен голос нам живой -\r\nПишутся прекрасные пейзажи\r\nВечного Художника рукой.\r\n'),
  (264, 'О, Господь, в молитве', 'О, Господь, в молитве пред Тобой склоняюсь\r\nБлагодарность сердца приношу Тебе.\r\nЗа любовь, за путь Твой. Боже умоляю\r\nОсвяти, очисти жизнь мою Себе.\r\n\r\nМой Иисус, Ты очи мне открыл однажды,\r\nЯ себя увидел в страшной нищете.\r\nИ в слезах горячих пал к ногам пронзённым,\r\nВ эту ночь, Иисус мой, я узнал Тебя.\r\n\r\nНежно Ты склонился над моей главою\r\nИ спросил так тихо: «Любишь ли Меня?»\r\nНе упрёком строгим – нежною любовью\r\nТы тогда навеки приобрёл меня.\r\n\r\nВсю земную прелесть суету и мудрость\r\nПроменял на счастье, что мне дал Христос.\r\nНичего не нужно больше в этой жизни,\r\nЕсли Ты, Спаситель, всю её возьмёшь.\r\n'),
  (265, 'Наша надежда - Ты Христос', 'Наша надежда - Ты Христос.\r\nТы наше счастье, наша любовь,\r\nТы наша радость, Ты наша жизнь.\r\nТы наше счастье и мечты.\r\n\r\nПрипев:\r\nНа Тебя мы уповаем, на Тебя жизнь полагаем,\r\nНа Тебя взор устремляем, ввысь, в небеса.\r\nНа Тебя мы уповаем, на Тебя жизнь полагаем,\r\nНа Тебя взор устремляем, ввысь, в небеса.\r\n\r\nТы пострадал за нас, Христос,\r\nТы на кресте пролил Свою Кровь.\r\nТы нам спасенье ныне принёс,\r\nТы нас избавил от горя и слёз.\r\n'),
  (266, 'О молитва, о молитва!', 'О молитва, о молитва! В жизни Богом ты дана.\r\nВ скорбной жизни среди битвы поднимала ты меня.\r\nТемной ночью я не спал, на коленях всё стоял\r\nИ душою с Богом говорил:\r\n\"Ты услышь меня, мой Бог, среди жизненных тревог\r\nПомоги, я выбился из сил!\"\r\n\r\nЗа окном бушует ветер, хлещет снегом ледяным,\r\nИ такой же бурей в сердце в этот вечер я томим.\r\nНо, смирившийся во прах, со слезами на очах\r\nЯ в скорбях Иисуса умолял:\r\n\"О мой Бог! Ты знаешь все, на душе так тяжело,\r\nЯ измучен и почти упал\".\r\n\r\nО молитва, о молитва! Благодарностью горю,\r\nПрославляю Божью силу, благодати глубину.\r\nБоже! Ты в любви святой укреплял дух слабый мой,\r\nКогда в бурю падал я без сил.\r\nК небесам сердечный вздох возносился средь тревог,\r\nЯ в молитве радость получал.\r\n'),
  (267, 'Отче, мы просим, Ты приди', 'Отче, мы просим, Ты приди,\r\nЗемлю исцели.\r\nМы сердцами вместе,\r\nПусть слава Твоя будет в нас,\r\nИ узнает мир, что Иисус — Господь.\r\n\r\nПрипев:\r\nМы в один голос прославим все Тебя.\r\nМы в один голос всем расскажем, что Ты — Царь,\r\nМы в один голос в гармонии любви.\r\nГосподь Иисус, Ты нас объедини.\r\n\r\nВремя настало, чтоб сердца\r\nОбъединить в хвале.\r\nИмя Иисуса прославим над всей землёй,\r\nИ узнает мир, что Иисус — Господь.\r\n'),
  (268, 'Я хочу с Тобой поговорить', 'Я хочу с Тобой поговорить,\r\nМой хороший, самый лучший Друг,\r\nНа меня с небес ты посмотри,\r\nСлышишь сердца частый, частый стук.\r\n\r\nЯ хочу с Тобой поговорить,\r\nОблегчить печали тяжкий груз,\r\nТо, что некому мне здесь открыть,\r\nЯ несу к Тебе, мой Иисус.\r\n\r\nЯ с Тобой поговорить хочу,\r\nПотому, что далеки друзья,\r\nС ними я о горе помолчу,\r\nИм не понесу печали я.\r\n\r\nЯ с Тобой спешу поговорить\r\nВ час, когда темно-темно вокруг,\r\nНа меня с небес Ты посмотри\r\nИ приди, как самый лучший Друг!\r\n'),
  (269, 'Возношу Тебя Господь', 'Возношу Тебя Господь,\r\nПрославляю Tебя в песне.\r\nСчастлив я, что Ты мой Бог,\r\nИ что Ты пришел спасти нас.\r\n\r\nПрипев:\r\nПришел на землю с небес путь показать,\r\nСвой путь продолжил на крест мой грех забрать,\r\nИ с креста в царство тьмы, а оттуда опять -\r\nВ небеса, чтоб нам сиять.\r\n\r\nСлавлю Имя я Твое,\r\nВозношу Тебе хвалу я.\r\nДля меня Ты - жизнь моя,\r\nДля меня Ты - путь спасенья.\r\n'),
  (270, 'Ты – мой Бог Святой', 'Ты – мой Бог Святой, я к Тебе стремлюсь,\r\nИбо знаю: «В Тебе покой».\r\n\r\nПрипев:\r\nУ Твоей груди успокоюсь я,\r\nУспокоится сердце моё.\r\n\r\nЗнаю, что в Тебе – радость вечная,\r\nИ любовь неизменная.\r\n\r\nИ никто уже не лишит меня,\r\nЭтой радости неземной.\r\n\r\nИ когда придёт тот последний час,\r\nВерю, примешь меня к Себе.\r\n'),
  (271, 'Ты пришёл в этот мир', 'Ты пришёл в этот мир, и ты скоро уйдёшь,\r\nБудешь счастлив тогда, когда Бога найдёшь.\r\n\r\nПрипев:\r\nВетер, дождь и туман застилают глаза,\r\nНо ты всё же ищи Иисуса Христа.\r\n\r\nОн один лишь, поверь, может свет тебе дать\r\nИ открыть сердца дверь, чтобы Бога познать.\r\n\r\nБез Него жизни нет, лишь одна суета.\r\nОн даёт мне ответ, Он свершает дела.\r\n'),
  (272, 'Пусть земля вся утверждает', 'Пусть земля вся утверждает,\r\nЧто Тобой я не спасён,\r\nДух мой песню воспевает:\r\n\"Принят раб Твой на Сион!\"\r\n\r\nПрипев:\r\nРадуйтесь со мною люди!\r\nНет сомненья, я спасён.\r\nПусть весь мир твердит иное:\r\nЗнаю я Христом спасён! - 2р.\r\n\r\nЕсли б ангел сам явился\r\nИ сказал: \" не обращен!\"\r\nИ Тогда б я не смутился,\r\nПомня вечный Твой закон.\r\n\r\nЕсли ум мой усомнится,\r\nПризову Тебя душой;\r\nТы придёшь и ум смирится,\r\nИ настанет Твой покой\r\n\r\nЗнаю я в Кого я верю;\r\nДух мой в Нём не сокрушим;\r\nВ жизни всякую потерю\r\nС радостью снесу я с Ним.\r\n\r\nКаждый миг Его я слышу,\r\nКаждый миг с Ним говорю;\r\nОн любовь дарит мне свыше\r\nИ Его огнём горю...\r\n\r\nНету места для сомненья\r\nВ сердце избранных Тобой;\r\nВ час минутного затменья\r\nБудь на страже, Пастырь мой!\r\n'),
  (273, 'Моя ладья в волнах плывет', 'Моя ладья в волнах плывет\r\nТуда, к тем берегам;\r\nСквозь бури рев ее ведет\r\nМой верный капитан.\r\n\r\nО, не страшись, не долог путь,\r\nУж видны берега;\r\nИ слышно, как хвалу поют\r\nДрузья у ног Христа.\r\n\r\nТы знаешь ли тот дивный край,\r\nГде Родина моя,\r\nГде братья ждут, где вечный рай?\r\nАх, встречу ль там тебя?\r\n\r\nМой челн благую весть несет\r\nВсем гибнущим в волнах:\r\nХристос спасает и дает\r\nБлаженство в небесах.\r\n'),
  (274, 'Ты был юношей, Спаситель', 'Ты был юношей, Спаситель,\r\nРадовал сердца,\r\nТы ходил всегда в завете\r\nТвоего Отца.\r\nНаучи и нас смиренно\r\nДни земные проводить,\r\nНеуклонно, неизменно\r\nПо путям Отца ходить.\r\n\r\nМать Свою любил Ты нежно\r\nИ не огорчал,\r\nИ Иосифу прилежно\r\nДелом помогал.\r\nТы любил душой, готовой\r\nК подвигам святым,\r\nВозвещать благое слово\r\nМудрым и простым.\r\n\r\nНаучи и нас трудиться,\r\nБлизким, дальним помогать,\r\nВсех любить, к добру стремиться,\r\nПравду в мире насаждать.\r\nНаучи и нас, Учитель,\r\nСлово жизни возвещать;\r\nДай, чтоб каждый мира житель\r\nМог Твой голос услыхать.\r\n'),
  (275, 'Славною рекою льется Божий мир', 'Славною рекою льется Божий мир,\r\nВсе превозмогая, разливаясь вширь.\r\nХоть уже он полон — все ж течет полней,\r\nГлубже проникает и живит людей.\r\n\r\nПрипев:\r\nВеруя в Иисуса и приняв призыв,\r\nЯ нашел блаженство и навек счастлив.\r\n\r\nВсех сокрытых в мощных Божиих руках\r\nНе прельстит лукавый, не настигнет враг;\r\nНе коснутся сердца: ни печали гнет,\r\nНи соблазн неправды и ни тень забот.\r\n\r\nРадость или горе свыше суждены,\r\nКаждому по силам Господом даны:\r\nЕсли Богу верим, то войдем в покой,\r\nВерен Бог для тех, кто верует душой.\r\n'),
  (276, 'Умей прощать, молись', 'Умей прощать, молись за обижающих,\r\nЗло побеждай святым лучом добра,\r\nИди без колебанья в стан прощающих,\r\nПока горит Голгофская заря.\r\nУчись прощать, когда душа обижена,\r\nИ сердце, словно чаша горьких слез,\r\nИ кажется, что доброта вся выжжена.\r\nТы вспомни, как прощал людей Христос.\r\n\r\nУмей прощать. Прощенье - это сила,\r\nА месть - бессилья признак роковой.\r\nИ камни непрощения в могилу,\r\nМой друг и брат не уноси с собой.\r\nУчись прощать естественно, как дышишь,\r\nКак воду пьешь и ешь насущный хлеб,\r\nВедь Бог тогда твои молитвы слышит,\r\nКогда в тебе горит прощенья свет.\r\n\r\nУмей прощать. Прощать не только словом,\r\nНо всей душой, всей сущностью своей.\r\nПрощение рождается любовью\r\nВ борениях молитвенных ночей.\r\nУчись прощать. В прощенье радость скрыта,\r\nВеликодушие лечит, как бальзам.\r\nКровь на кресте за всех была пролита;\r\nУмей прощать, чтоб ты прощен был сам.\r\n'),
  (277, 'Прости меня, Боже', 'Прости меня, Боже, прости, я молю,\r\nПрости, что так поздно к Тебе прихожу,\r\nПрости, что я раньше Тебя не познал\r\nИ друга иного тогда я избрал.\r\n\r\nПрипев:\r\nПрости, что я раньше Тебя не познал\r\nИ друга иного тогда я избрал.\r\n\r\nПрости, что мой разум с Тобой воевал,\r\nПрости, что Тебя я страдать заставлял,\r\nПрости, что я скорби Тебе причинял\r\nИ дерзко Любовь на кресте распинал.\r\n\r\nНо Ты пробудил меня к жизни иной\r\nИ сердца коснулся Своею рукой,\r\nГлаза мне открыл Ты на пройденный путь,\r\nВ неверье, в грехах мне не дал утонуть.\r\n\r\nИ вот я в молитве стою пред Тобой,\r\nВеди меня, Боже, Своею рукой!\r\nЯ всю свою жизнь посвящаю Тебе,\r\nТы только не дай мне погибнуть в борьбе.\r\n\r\nИ я обещаю Тебя прославлять\r\nИ словом и делом Тебя восхвалять,\r\nЧтоб люди увидели свет Твой во мне\r\nИ, светом влекомые, шли все к Тебе.\r\n'),
  (278, 'Раньше жил я во грехах', 'Раньше жил я во грехах, в горе, болях и скорбях,\r\nНикогда не думал я о вечности.\r\nНо Иисус нашел меня, разорвал все узы зла,\r\nПолон я теперь небесной радости!\r\n\r\nПрипев:\r\nЯ свободен, я свободен от неправды и греха!\r\nИисус нашел меня, разорвал все узы зла.\r\nПолон я теперь небесной радости!\r\n\r\nСлово Божье учит нас: Дух Господень не погас,\r\nСилу даст Он возвещать всем о Христе.\r\nТот Божественный огонь, с неба посланный Отцом,\r\nНаделяет силой слабых на земле.\r\nОн пылает, да пылает, Дух Святой зажег огонь в моей душе.\r\nТот Божественный огонь, с неба посланный Отцом,\r\nНаделяет силой слабых на земле.\r\n\r\nЕсли болен ты, друг мой, обратись к Христу с мольбой,\r\nУдалит Он немощь и печаль твою.\r\nЧто угодно пусть болит.\r\nОн здоровье возвратит.\r\nКровь Христова исцеление дарит.\r\nКровь Христова, Кровь Христова\r\nИ сегодня исцеляет всех больных.\r\nЧто угодно пусть болит.\r\nОн здоровье возвратит,\r\nКровь Христова исцеление дарит.\r\n\r\nВот смоковница цветет, скоро наш Господь придет,\r\nЧтоб забрать свою невесту в небеса.\r\nИ конец придет скорбям, что за радость будет там!\r\nДруг, готов ли встретить Господа Христа?\r\nТы готов ли, ты готов ли?\r\nДруг, готов ли ты покинуть этот мир?\r\nИ конец придет скорбям, что за радость будет там!\r\nДруг, готов ли встретить Господа Христа?\r\n'),
  (279, 'Славьте Бога, славьте', 'Славьте Бога, славьте в песнопенье,\r\nСлавьте, славьте жизнью всей своей,\r\nПусть не смолкнет Господу хваленье\r\nВ дни гонений и среди скорбей!\r\n\r\nПрипев:\r\nСлавьте Бога\r\nСлавьте всей душой!\r\nНас Он спас Собой!\r\nСлавьте Бога\r\nСлава вечная Ему!\r\n\r\nСлавьте Бога каждое мгновенье\r\nВ чувствах ваших, мыслях и делах\r\nЗа Его, Творца, долготерпенье\r\nК миру, гибнущему во грехах!\r\n\r\nСлавьте Бога, славьте всей душою\r\nЗа Его великую любовь,\r\nЧто от смерти спас Он нас Собою,\r\nНа кресте пролил святую Кровь!\r\n\r\nСлавьте Бога, славьте бесконечно!\r\nСкоро Сам во славе Он придет\r\nИ Своей любовью чудной, вечной\r\nВсех спасенных в дом Свой приведет.\r\n'),
  (280, 'Струн души рука Христа касается', 'Струн души рука Христа касается,\r\nИ она восторженно поет,\r\nПеснь еще одна на свет рождается,\r\nПеснь о Том, Кто счастье нам дает.\r\n\r\nО Тебе поют сады весенние\r\nТрелью звонких птичьих голосов.\r\nЯ хочу воспеть Твое спасение,\r\nЖаль, что нет таких на свете слов.\r\n\r\nТвой привет мне льется ароматами\r\nВсех полей цветущих и садов.\r\nНавсегда порвав с мирскими взглядами,\r\nПринял я навек Твою любовь.\r\n\r\nЯ любовь великую, безбрежную\r\nНахожу во всем, что видит взор,\r\nИ люблю любовью самой нежною\r\nЯ Того, Кто снял греха позор.\r\n\r\nСтрун души рука Христа касается,\r\nИ она восторженно поет.\r\nПусть вовеки песня не кончается,\r\nПеснь о Том, Кто счастье нам дает!\r\n'),
  (281, 'Что мне золото бренного мира?', 'Что мне золото бренного мира?\r\nЧто мне царский удел вековой?\r\nЧто мне гимны хвалебные пира,\r\nЕсли нет Иисуса со мной?\r\nЧто мне радости, что мне печали?\r\nЧто волнение или покой?\r\nОтличу день от ночи едва ли,\r\nЕсли нет Иисуса со мной!\r\n\r\nПрипев:\r\nБез Него мир похож на пустыню,\r\nБез Него сердце высушит зной.\r\nУ воды я от жажды погибну,\r\nЕсли нет Иисуса со мной!\r\n\r\nЧто мне чаша владык венценосных?\r\nЧто мне проку от славы земной?\r\nНе найду себе места под солнцем,\r\nЕсли нет Иисуса со мной!\r\nБез Него остановится время,\r\nБез Него я ничто, прах земной.\r\nМне и жизнь – непосильное бремя,\r\nЕсли нет Иисуса со мной!\r\n'),
  (282, 'Сколько есть заманчивых дорог', 'Сколько есть заманчивых дорог, Ты от них\r\nхрани нас, вечный Бог, И веди нас жизнен-\r\nной тропой По стезям колючим за Собой.\r\n\r\nПрипев:\r\nЗа Тобой пойду я сквозь туман. Не беда,\r\nчто кровь течет из ран, Впереди меня Ты Сам\r\nидешь И меня в обители ведешь. Сквозь пургу,\r\nсквозь жизни ураган, За Тобой иду я в Ханаан, В\r\nжизни Ты меня, мой Бог, храни, Силу дай впе-\r\nред всегда идти.\r\n\r\nЕсли Божий Дух в твоей груди, - Встретишь ты\r\nпреграды на пути. Но как счастлив тот, кто проне-\r\nсет Верность Богу сквозь кольцо невзгод.\r\n\r\nТак веди ж меня Своей тропой, Напояй живи-\r\nтельной водой. Дай мне силы лишь Тобою жить,\r\nЖаждущих водой Твоей поить.\r\n'),
  (283, 'Скоро, скоро придет', 'Скоро, скоро придет наш Спаситель\r\nВот уж время Его настаёт.\r\nОн возьмёт нас тогда в ту Обитель,\r\nГде не будет ни слёз ни забот.\r\n\r\nПрипев:\r\nТам за горизонтом\r\nРодина нас ждёт.\r\nТам за горизонтом\r\nВстретимся с Христом.\r\n\r\nТы ведь, здесь - только временный житель\r\nДом твой там, далёко, в небесах.\r\nЗаберёт всех своих наш Спаситель\r\nИ останемся там навсегда.\r\n\r\nБудет там вечной славой наполнен\r\nКаждый взгляд и все наши сердца.\r\nНо в Христову Обитель войдёт тот,\r\nКто был верен Ему до конца.\r\n'),
  (284, 'Скоро я увижу, озаренный', 'Скоро я увижу, озаренный светом\r\nУтра новой жизни мой небесный дом.\r\nО, какое счастье я в Христе имею,\r\nВ небе мне обитель приготовил Он.\r\n\r\nПрипев:\r\nВ вышине небес,\r\nГде живет Творец.\r\nИмею я дом,\r\nИ буду жить в нем,\r\nВ вышине небес.\r\n\r\nИ иду туда я через тьму густую,\r\nЧто мне часто давит душу тяжело.\r\nНо любовь Иисуса нежно душу греет,\r\nХоть печаль глубока, но с Христом светло.\r\n\r\nМного скорби в жизни на пути встречаю,\r\nНо я знаю, что я скоро отдохну.\r\nПосле всех скитаний, скорби и изгнаний\r\nСам Господь отрет последнюю слезу.\r\n'),
  (285, 'Нет никого, как Ты', 'Нет никого, как Ты,\r\nНикто не может так коснуться меня,\r\nЯ могу искать всю жизнь не найти.\r\nНет никого, как Ты.\r\n\r\nТвоя рука на мне,\r\nЯ не боюсь, когда Ты рядом со мной,\r\nТы знаешь сердце и мысли мои,\r\nНет никого, как Ты.\r\n\r\nТы искупил мой грех,\r\nИ благодать Твоей небесной любви,\r\nВ нерукотворном храме наших сердец.\r\nНет никого, как Ты.\r\n'),
  (286, 'Со мной останься', 'Со мной останься, меркнет луч дневной,\r\nТемнеет; о, останься Ты со мной!\r\nДрузья бегут и в пытках тесноты\r\nОстанусь я - Со мной останься Ты.\r\n\r\nПрошу, Господь: останься не на миг,\r\nИ не на день один; ведь Ты сказал,\r\nЧто не покинешь Ты друзей Своих.\r\nОстанься навсегда, как обещал!\r\n\r\nТы в юности явил мне светлый рай;\r\nЯ часто был строптив средь суеты\r\nИ часто оставлял Тебя, но Ты\r\nНе оставлял и впредь не оставляй!\r\n\r\nВ Тебе нуждаюсь каждый час, Господь,\r\nВ Тебе лишь сила, чтоб смирялась плоть.\r\nКто, как не Ты, мне может быть вождем.\r\nПребудь со мной во тьме и светлым днем!\r\n\r\nЯви Свой крест, когда сомкну глаза;\r\nСияй сквозь мглу, когда кругом гроза;\r\nЗажжется день, рассеет ночь скорбей,\r\nСо мною вечно будет Царь царей.\r\n'),
  (287, 'В Христе одном надежда', 'В Христе одном надежда есть, вся моя жизнь и сила в Нём.\r\nОн - Камень мой, скала и песнь – надежный  спутник в страшный шторм\r\n/Как высока любовь Его. Как мир глубок, как с Ним легко.\r\nМой Утешитель – Всё во всём, моя опора только в Нём./\r\n\r\nВ Христе одном, принявшем плоть, открылся всемогущий Бог.\r\nНо дар любви что дал Господь, отверг народ, избрав порок.\r\n/Он на кресте Себя отдал, в смиреньи гнев Отца принял.\r\nМой грех на плечи возложил, чтобы Его я смертью жил./\r\n\r\nВ земле, как все, Он мёртвым был объят был тьмою свет небес.\r\nНо в третий день Он победил, из гроба мрачного воскрес\r\n/Он Бог и победитель всех, уже не правит больше грех.\r\nНавеки Он хозяин мой, я куплен кровию святой./\r\n\r\nНет страха смерти, нет вины, всё только силою Христа.\r\nОт первых дней, до седины, до смерти, в Нём моя судьба.\r\n/Нет никого и ничего, что вырвет из руки Его\r\nОн приведёт меня в Свой дом моя опора только в Нём./\r\n'),
  (288, 'Хорошо мне с Тобою быть', 'Хорошо мне с Тобою быть,\r\nТебе верить, Тебя любить.\r\nХорошо в тихий час ночной,\r\nИисус, говорить с Тобой.\r\n\r\nПрипев:\r\nХорошо мне с Тобой везде,\r\nДаже в горе, в любой беде,\r\nДаже если темно вокруг,\r\nТы – мой самый надежный Друг.\r\n\r\nХорошо мне с Тобой дружить,\r\nВсе заботы свои сложить,\r\nХорошо все вручить Тебе,\r\nВсе, что будет в моей судьбе.\r\n\r\nРазве можно с Тобой скучать?\r\nЯ часами могу молчать!\r\nСознавая, что рядом Ты,\r\nРазделяешь мои мечты.\r\n'),
  (289, 'Ты искупил мир от греха', 'Ты искупил мир от греха,\r\nТы дал ему любовь и свет.\r\nЗажег потухшие сердца\r\nИ дал надежду.\r\nМы пред Тобою предстоим\r\nИ каждый дорог для Тебя,\r\nЗа все, Отец, благодарим\r\nИ превозносим.\r\n\r\nПрипев:\r\nСлава Тебе и величие,\r\nСлава в веках и народах,\r\nСила Твоя безгранична,\r\nМилость Твоя во все роды.\r\n\r\nТы открываешь новый мир\r\nЛюбви, надежд и чистоты.\r\nС Тобой не трудно мне идти,\r\nВедь Ты со мною.\r\nТвоя любовь меня хранит,\r\nДает надежду и покой,\r\nИ потому сердца людей\r\nПолны хвалой.\r\n'),
  (290, 'Там, далеко', 'Там, далеко, (там, далеко),\r\nЗа облаками (за облаками),\r\nВстретит меня Иисус.\r\nНежно обнимет (нежно обнимет)\r\nСвоими руками (Своими руками),\r\nС Ним я навеки сольюсь.\r\n\r\nПрипев:\r\nА пока живу я мире странником\r\nИ с тоской смотрю я в небеса.\r\nС небом связаны мои желания,\r\nС небом связана моя судьба.\r\n\r\nМне распахнут (мне распахнут)\r\nРайские двери (райские двери),\r\nРадостно скажут: \"Входи!\"\r\nВсё это будет (всё это будет),\r\nТвёрдо я верю (твёрдо я верю),\r\nНо это лишь впереди.\r\n\r\nТам навсегда (там навсегда)\r\nСкорби забуду (скорби забуду),\r\nВ небе улыбка царит.\r\nТам с Иисусом (там с Иисусом)\r\nВечно я буду (вечно я буду)\r\nВ сладком общении жить.\r\n\r\nПрипев:\r\nА пока живешь ты в мире странников\r\nИ с тоскою смотришь в небеса.\r\nС чем же связаны твои желания,\r\nС чем же связана твоя судьба?\r\n'),
  (291, 'Я в мире с Богом!', 'Я в мире с Богом! - Двери рая\r\nОн мне отверз, как брату брат;\r\nО, как безмерно я богат,\r\nЕго прощеньем обладая!\r\n\r\nПрипев:\r\nМоя душа нашла покой,\r\nЕго мне дал Спаситель мой.\r\nЯ в мире с Ним!\r\n\r\nНе давит сердце мне тоскою,\r\nИ верю я, что для меня\r\nЕсть место там в чертогах дня,\r\nВ стране блаженной за рекою.\r\n\r\nЯ в мире с Богом! Он со мною\r\nВезде, на всех путях моих,\r\nВ тревогах, горестях земных,\r\nВ борьбе с неправдой и нуждою.\r\n\r\nКак в счастье, так и в тяжкой доле\r\nВсегда твердят мои уста:\r\n\"Пусть воля Господа Христа\r\nМоею вечно будет волей!\"\r\n'),
  (292, 'Спой же песню об Иисусе', 'Спой же песню об Иисусе,\r\nСлавя Его.\r\nОн так благ - ты полагайся\r\nЛишь на Него.\r\n\r\nПрипев:\r\nЛюбит Он нас, как лучший друг.\r\nОн вблизи нас, всегда вокруг,\r\nПоспеши же ему принесть\r\nХвалу и честь.\r\n\r\nСлово правды об Иисусе -\r\nОн свой чужим.\r\nЧтобы все ему служили,\r\nМы все хотим.\r\n\r\nОн во всякое мгновенье\r\nБлизок к сердцам.\r\nЛюди могут обмануть нас -\r\nОн верен нам.\r\n\r\nБудем петь сердечно:\r\nИисус один.\r\nБудет все во всем нам\r\nВечно Вышнего Сын.'),
  (293, 'У дикого гуся могучие крылья', 'У дикого гуся могучие крылья\r\nВ далеком полете широк их размах\r\nНемного старанья, немного усилья\r\nИ гусь высоко , высоко в облаках\r\n\r\nУ ласточки крылья у голубя тоже\r\nА я человек не умею летать\r\nНо крылья молитвы Ты дал мне о Боже\r\nОни меня могут над миром поднять\r\n\r\nКогда человек начинает молиться\r\nУ птиц перелетных растерянный вид\r\nВедь птицам приходиться посторониться\r\nДорогу молитвам – людей уступить\r\n\r\nБесшумные крылья, могучие крылья\r\nМой дух поднимают над грешной землей\r\nНемного старанья, немного усилья\r\nВ полете молитвы для сердца покой.'),
  (294, 'Ты не бойся преткновений', 'Ты не бойся преткновений на пути, В край\r\nдалекий со страданием идти. Но запомни и познай только\r\nодно, Что страдать нам в этом мире суждено.\r\n\r\nПрипев:\r\nСердце страждет и тоскует, Боже мой!\r\nПреткновенья давят душу, и порой Я страдаю и не в силах\r\nкрест нести. О Спаситель, дай мне силы на пути!\r\n\r\nПусть никто лишь твоей веры не смутит, Верой\r\nдолжен ты грехи все победить. Иисуса кровь могуча и\r\nсильна. Благодать Его велика и чиста.\r\n\r\nТы не бойся преткновений на пути. Как устанешь -\r\nбуду Я тебя нести. Я назвал тебя по имени, ты Мой!\r\nПриведу тебя в обитель, где покой.\r\n\r\nУж недолго и немного нам идти, Скоро, скоро не\r\nзамедлю Я прийти. Только слушай. Говорю тебе: \"Молись!\r\nНа Голгофский крест взирая, ты крепись!\"\r\n\r\nТы не бойся преткновений на пути. Помогу тебе\r\nтяжелый крест нести. Сам Христос тебя омоет и простит\r\nИ научит как душою всей любить.\r\n'),
  (295, 'Улетают года, неизбежно', 'Улетают года, неизбежно, как звук,\r\nСколько радостных встреч, сколько горьких разлук.\r\nМного в мире скорбей, много в мире тревог,\r\nМы теряем друзей на развилках дорог.\r\n\r\nВспоминаем мы часто о прежних друзьях,\r\nИ грустим о напрасно потерянных днях.\r\nЧто так много бывало ошибок и зла.\r\nЧто нельзя возвратить и поправить нельзя.\r\n\r\nУлетают года, мы уж жить не спешим,\r\nРеже смотрим вперёд, всё о прошлом грустим.\r\nЧей-то горестный вздох, чей-то ласковый взгляд,\r\nНичего, ничего не воротишь назад.\r\n\r\nМой Господь, пред Тобой я в смиреньи стою.\r\nТы ведь все знаешь Сам, знаешь душу мою.\r\nЗнаешь Ты все печали и слёзы, и стон.\r\nТы - Создатель миров. Ты - Владыка времён.\r\n'),
  (296, 'Только Ты один, так коснулся', 'Только Ты один, так коснулся сердца моего,\r\nТвоя милость, как живая река, в вечности течет.\r\n\r\nТолько Ты один, на Голгофе жизнь отдал за меня!\r\nИстекает драгоценная кровь Сына Божьего!\r\n\r\nТолько Ты один, мне отдал сокровища Свои,\r\nНебеса открыты предо мной, я пришел домой.\r\n'),
  (297, 'Что за друга мы имеем?', 'Что за друга мы имеем? - Нас Он к жизни пробудил,\r\nВ Нём мы счастьем владеем, в Нём источник вечных сил.\r\nО, как часто мы страдали, боль терпя напрасно там,\r\nГде просить мы забывали, чтоб один помог Он нам.\r\n\r\nИскушенье ль нас тревожит, пытки ль тяжки для кого,\r\nКаждый пусть из нас возложит скорбь свою всю на Него.\r\nОн один среди вселенной может свет средь тьмы пролить;\r\nЛишь Христос один мгновенно может горе облегчить.\r\n\r\nИзнываем мы под зноем этой жизни суетной;\r\nСердце лишь Ему откроем, и Он даст душе покой.\r\nЕсли нас друзья забыли, скажем Господу о том,\r\nИ Христос проявит в силе, что Он верный Друг во всем.\r\n'),
  (298, 'Муж Галилеянин тебя не пройдет', 'Муж Галилеянин тебя не пройдет\r\nИ если ты позволишь,\r\nОн Сам придет, любя\r\nИ силою Своею коснется тебя.\r\n\r\nПрипев:\r\nИного пути нет, ты не найдешь, нет, нет.\r\nЛишь только Иисус тебя спасет.\r\nМоя победа, победа в Боге,\r\nПобеда в Имени Иисуса Господа.\r\nИ никогда мои враги не смогут победить меня,\r\nМоя победа, победа в Боге,\r\nПобеда в Имени Иисуса Господа\r\n\r\nМуж Галилеянин меня не прошел\r\nИ я ему позволил –\r\nОн Сам пришел, любя,\r\nИ силою Своею коснулся меня.\r\n'),
  (299, 'Честно, преданно, послушно', 'Честно, преданно, послушно\r\nБудем Господу служить.\r\nНепорочно, свято, дружно\r\nВ грешном мире будем жить.\r\nТьма неверия кругом,\r\nСвет небесный понесем\r\nИ об имени Христовом\r\nЛюдям грешным воспоем.\r\n\r\nПрипев:\r\nСкоро Он придет\r\nИ Своих возьмет;\r\nЧтобы с Ним нам быть,\r\nНужно верить и любить.\r\nСкоро Он придет\r\nИ Своих возьмет;\r\nЧтобы с Ним нам быть,\r\nНужно верить и любить\r\n\r\nСветом истины Христовой\r\nНужно в мире нам сиять\r\nИ о жизни вечной, новой\r\nЛюдям правду рассказать.\r\nВ жизни временной, земной\r\nНе найти душе покой,\r\nТолько в небе с Иисусом -\r\nЧудный отдых и покой.\r\n\r\nФакел Божьего ученья\r\nВ тьму неверья понесем\r\nИ из мрака заблужденья\r\nГрешных к Богу призовем.\r\nОн желает их принять,\r\nГрех простить и радость дать\r\nИ в Своей любви безмерной\r\nИх от гибели спасать.\r\n\r\nТам никто нужды не знает,\r\nСлез никто уже не льет,\r\nСолнце правды там сияет\r\nИ одна любовь живет.\r\nНо чтоб вечность получить,\r\nНужно свято в мире жить\r\nИ, на Господа взирая,\r\nВсем любовию служить.\r\n'),
  (300, 'Что хочет Бог, если стану Его?', 'Что хочет Бог, если стану Его?\r\nЧто мне отдать, чтобы жить для Него?\r\nЕсли пойду, куда Он поведёт,\r\nЧто мне Иисусу отдать?\r\n\r\nПрипев:\r\nВсё Тебе, мой Господь, я отдаю,\r\nСилу всю, дух и плоть, всю жизнь мою.\r\nПосвящаю себя в службу Тебе.\r\nКак Иисус дорог мне! \r\n\r\nКогда я вспомню Господню любовь,\r\nСмерть на кресте и пролитую кровь.\r\nВсё это сделал, меня Он любя.\r\nЧто отдам Господу я?\r\n'),
  (301, 'Я пою под гитару о том', 'Я пою под гитару о том,\r\nЧто расстался со всяким грехом;\r\nПрославляя Христову любовь,\r\nКто пролил на кресте Свою кровь.\r\n\r\nПрипев:\r\nЗвонче, звонче, гитара, звучи,\r\nО могучем Христе не молчи!\r\nЗвонче, звонче, гитара, играй,\r\nИисуса Христа прославляй!\r\n\r\nОн великою жертвой Своей\r\nСпас меня от греховных путей;\r\nОн лишь смог меня к счастью вернуть.\r\nИ направить на праведный путь.\r\n\r\nОн мне сердце мое возродил,\r\nК новой жизни меня пробудил,\r\nИ душа переполнена Им,\r\nПознакомить желает всех с Ним.\r\n\r\nО как счастлив я, зная о том,\r\nЧто на небе я встречусь с Христом\r\nИ войду в Его дом не земной,\r\nПолучу в нем желанный покой!\r\n'),
  (302, 'Я буду на небе', 'Я буду на небе (2р)петь песни победы\r\nКак хочеться верить, что райские двери\r\nОткроються мне.\r\n\r\nПрипев:\r\nКак хочется быть нам вместе,\r\nХвалебные петь там песни,\r\nЗабыть насовсем о старой своей земле.\r\n\r\nКак хочется в небо, в лазурную даль,\r\nПрекрасное небо меня приглашает в заоблачный край.\r\n\r\nПрипев: Мы верим, что есть на свете,\r\nПоверим, как верят дети,\r\nЧто в небе чертог приготовил для нас сам Бог.\r\n\r\nЯ новое небо и новую землю\r\nХотела б увидеть и в город святой вратами войти\r\nВ Новый Иерусалим.\r\n'),
  (303, 'Я постигаю сквозь слезы', 'Я постигаю сквозь слезы и боль\r\nРадость встречи и горечь разлуки.\r\nПью эту чашу, где сладость и соль,\r\nПеремешались надежда и муки.\r\n\r\nПрипев:\r\nТы не печалься, не беспокойся,\r\nЕсли Бог с нами - чего бояться?\r\nТы не печалься, не беспокойся,\r\nЕсли Бог с нами.\r\n\r\nЯ не жалею о прошлом ничуть,\r\nВижу как катится мир к катастрофе.\r\nВерую в крестный мной избранный путь,\r\nВ тайну распятой любви на Голгофе.\r\n\r\nСвободу ищу из железных оков,\r\nТех, что зовутся житейское счастье.\r\nВся моя жизнь - это несколько слов\r\nВ тихой молитве перед причастьем.\r\n'),
  (304, 'Кто бы дал мне слезы', 'Кто бы дал мне слезы сокрушенья,\r\nПлакал бы тогда я день и ночь.\r\nГде найти для сердца утешенье,\r\nВсе сомненья улетели прочь?\r\n\r\nЛьются слезы у царя Давида, -\r\nРазве мало злата, серебра? -\r\nНо я плачу, потому что вижу:\r\nДух Святой уходит от меня.\r\n\r\nКто бы дал мне слезы, как Мария\r\nПлакала у ног Христа,\r\nИ слезами их она омыла,\r\nВ сердце утешение нашла.\r\n\r\nКто бы дал мне слезы раскаянья,\r\nСлезы, что лились из глаз Петра!\r\nВидел Бог ученика рыданье,\r\nИ простил измену у костра.\r\n\r\nБоже, дай молиться с твердой верой,\r\nЧтоб Тобой был услышан я,\r\nЧтоб не плакать мне потом за дверью,\r\nГде слеза уж больше не нужна.\r\n'),
  (305, 'Мы нужны друг другу', 'Мы нужны друг другу\r\nБог нас спас не зря\r\nВ Иисусе стали мы друзья\r\nС радостью святою\r\nС песней на устах\r\nПрославим вместе Господа Христа\r\n\r\nПрипев:\r\nМы - одна семья,мы - одна семья\r\nПролилась за нас Иисуса кровь\r\nМы теперь друзья,Мы с тобой друзья\r\nИ в сердцах у нас Христа любовь\r\n\r\nМы нужны друг другу\r\nБог нас спас не зря\r\nВ Царство Божье мы идем, друзья\r\nТак давайте сохраним здесь веру на земле,\r\nЧтоб царствовать нам с Богом во Хресте.\r\n'),
  (306, 'Я иду земными битвами', 'Я иду земными битвами,\r\nКак в степи идет пастух,\r\nИ сердечными молитвами\r\nУкрепляется мой дух\r\nИ сердечными молитвами. 3 р\r\n\r\nЯ иду в страну далекую,\r\nВ дом Небесного Отца,\r\nГде течет рекой широкою 3p.\r\nСлавословье без конца.\r\n\r\nИ когда войду в селение,\r\nЯ увижу в тот же миг,\r\nСреди ангельского пения 3p.\r\nМилосердный Божий лик.\r\n'),
  (307, 'На далеком холме', 'На далеком холме, средь деревьев и скал,\r\nСквозь седые века старый крест простоял,\r\nМиллионы людей у подножья креста\r\nСвое счастье нашли, Там однажды был я.\r\n\r\nСтарый крест старый крест неброский, но лишь в нем\r\nСила естъ сила есть. Сегодня может он\r\nИсцелять разбитые, больные сердца,\r\nКаждому пришедшему открыть небеса.\r\n\r\nМежду небом святым и греховной землей\r\nПропасть злая лежит, разделяя собой,\r\nХочешь верь, хочешь нет, слушай слово Творца,\r\nКрест — единственный мост от земли к небесам.\r\n\r\nО, Иисус дорогой, у подножья креста\r\nНа далеком холме я увидел Тебя.\r\nНа пронзенных руках не застывшая кровь,\r\nА в Господних глазах — Божья к людям любовь.\r\n'),
  (308, 'Моя заветная мечта', 'Моя заветная мечта,\r\nУвидеть небо над Эдемом\r\nИ там остаться навсегда\r\nВ прекрасной родине Адама\r\n\r\nПрипев:\r\nНебо Эдема-Ты моя песня\r\nНебо Эдема-Родные края\r\nСамая светлая,самая нежная\r\nЖизни моей мечта\r\n\r\nМоя желанная мечта\r\nУ ног Иисуса там склониться\r\nИ там остаться навсегда\r\nГде вечно будет свет струиться\r\n\r\nМолю Тебя, Спаситель мой,\r\nВерни мне небо над Эдемом\r\nХочу я вечно быть с тобой\r\nХочу всегда быть совершенным.\r\n');
INSERT INTO `SONG` (`ID`, `DESCRIPTION`, `TEXT`) VALUES
  (309, 'Мой Господь, в этот час', 'Мой Господь, в этот час я склоняюсь в мольбе,\r\nИ все нужды мои приношу я Тебе.\r\nВ жизни бурь и тревог Ты укроешь меня,\r\nПуть весь знаешь, мой Бог, он не скрыт от Тебя.\r\n\r\nПрипев:\r\nТы, Господь, лишь Ты моя любовь, моя, в Тебе укроюсь я,\r\nВедь Ты моя защита.\r\nТы, Господь, лишь Ты любовь моя, моя, щит мой и Скала,\r\nВедь Ты моя защита и опора.\r\n\r\nНе страшна мне с Тобой даже смертная тень,\r\nОбрету я покой в небе в солнечный день.\r\nЗнаю, слёзы мои Ты с любовью отрёшь,\r\nВсе ошибки в пути Ты простишь и поймёшь.\r\n\r\nА, пока на земле буду славить, Тебя,\r\nБуду сердцем я петь, ожидая любя.\r\nЗнаю, скоро придёшь, скоро встречу Тебя,\r\nСвою Церковь возьмёшь от скорбей и от зла.\r\n'),
  (310, 'Наши годы идут незаметно', 'Наши годы идут незаметно, дни за днями бегут и бегут\r\n//Все, что есть в этой жизни, не вечно,\r\nВсе имеет лишь краткий свой путь.//\r\n\r\nОтшумит и пора золотая, годы жизни так быстро пройдут\r\n//Мы друзей на земле здесь теряем, потеряет и нас кто- нибудь.//\r\n\r\nВсе имеет конец и начало, наша жизнь пролетает, как миг.\r\n//Если с Богом ты не повстречался, проживаешь бессмысленно дни.//\r\n\r\nЕсли хочешь ты жить бесконечно, ни минуты теперь не теряй:\r\n//Во Христе открывается вечность, свое сердце Ему ты отдай!//\r\n'),
  (311, 'Следуй за Мной, когда', 'Следуй за Мной, когда море бушует,\r\nСледуй за Мной, когда тьма торжествует,\r\nСледуй за Мной через терний шипы.\r\nСледуй, о, следуй за Мной!\r\n\r\nСледуй за Мной, когда в жизни страдаешь,\r\nСледуй за Мной, когда силы теряешь,\r\nСледуй за Мной – помогу Я тебе.\r\nСледуй, о, следуй за Мной!\r\n\r\nСледуй за Мной, когда враг искушает,\r\nСледуй за Мной, когда друг изменяет,\r\nСледуй за Мной – не оставлю тебя.\r\nСледуй, о, следуй за Мной!\r\n\r\nСледуй за Мной, когда дали темнеют,\r\nСледуй за Мной, когда очи тускнеют,\r\nСледуй за Мной – в вечный Дом ты войдёшь.\r\nСледуй, о, следуй за Мной!\r\n'),
  (312, 'Начиная новый день', 'Начиная новый день, слышу голос Твой, голос Твой.\r\nЯ смотрю на небеса, знаю - Ты со мной, Ты со мной.\r\nПусть, Иисус, Твоя рука поведёт меня, поведёт меня.\r\nЯ люблю Тебя, Господь, Ты храни меня, храни меня!\r\n\r\nПрипев:\r\nТолько Ты радость даёшь, каждый день за Собою ведёшь.\r\nТы омыл душу мою, для Тебя живу и пою.\r\nТолько Ты радость даёшь, каждый день за Собою ведёшь.\r\nТы омыл душу мою, для Тебя живу и пою.\r\n\r\nЯ Тебя благодарю за любовь и мир, за любовь и мир.\r\nТы от злобы и греха нас освободил, освободил\r\nВ город света и любви нам открыта дверь, открыта дверь.\r\nСлава, всемогущий Бог, мы с Тобой теперь, с Тобой теперь!\r\n\r\nЯ хочу, чтоб сегодня горели сердца, горели сердца,\r\nПусть сияет в них свет от великого Бога - Отца, а-а-а\r\nПусть ещё миллионы заблудших людей, заблудших людей\r\nНаконец то поймут, кто для них всех нужней, всех нужней!\r\n'),
  (313, 'Велик наш небесный Отец', 'Велик наш небесный Отец\r\nТворит Он чудеса.\r\nЕго могущества везде\r\nМы слышим голоса.\r\n\r\nПрепев:\r\nЧудесно звезды поместил\r\nВ кругу пустом,\r\nЧудно Землю\r\nОн повесил ни на чем!\r\nНо чтоб меня спасти\r\nИ в Свой дом ввести -\r\nЯвил он чудо вечной милости.\r\n\r\nПо всей земле, во всех делах\r\nБог виден нам во всем!\r\nНо полноту Его любви\r\nЛишь в вечности поймем.\r\n\r\nО Нем Писанье говорит,\r\nЕму хвала звучит\r\nИ в пенье птичек, и в цветах,\r\nи в вечных небесах.\r\n'),
  (314, 'Поднимаю взоры к небесам', 'Поднимаю взоры к небесам,\r\nАх какая радость ждёт нас там,\r\nВ радостном общении,\r\nВ трепетном волнении,\r\nСо Христом мы встретимся. А там…\r\n\r\nЖдёт награда светлые дворцы,\r\nПальмовые ветви и венцы.\r\nПосле слёз и горя на стеклянном море,\r\nПеснь поют победы первенцы.\r\n\r\nНас ко древу жизни поведут,\r\nАрфы золотые нам дадут.\r\nАрфы не стареют, струны не ржавеют,\r\nЗвуки неземные издают.\r\n\r\nТам страна нетленной красоты,\r\nНикогда не вянут там цветы.\r\nТам настолько чудно, что представить трудно,\r\nВсюду там сиянье красоты.\r\n\r\nРадость скоротечна в мире сём,\r\nГоре и ненастье ждут потом.\r\nРадость бесконечна, Там пребудем вечно,\r\nЕсли мы в союзе со Христом.\r\n'),
  (315, 'Господи! Я взываю к Тебе!', 'Господи! Я взываю к Тебе!\r\nГосподи! Ты один лишь помощник в борьбе!\r\nПребудь же со мной, о Боже благой,\r\nМеня охраняй на пути Своею могучей рукой.\r\nПребудь же со мной, о Боже благой,\r\nМеня охраняй на пути Своею могучей рукой.\r\n\r\nГосподи! Сколько горя вокруг!\r\nГосподи! Ты один лишь надежный мой Друг!\r\nВ сердечной мольбе взываю к Тебе\r\nИ тайную сердца тоску Тебе одному приношу.\r\nВ сердечной мольбе взываю к Тебе\r\nИ тайную сердца тоску Тебе одному приношу.\r\n\r\nГосподи! Ты - надежда моя!\r\nГосподи! На Тебя полагаюся я!\r\nИ всею душой стремлюсь быть с Тобой\r\nВ той вечной стране,\r\nгде всегда царит мир и дивный покой.\r\nИ всею душой стремлюсь быть с Тобой\r\nВ той вечной стране,\r\nгде всегда царит мир и дивный покой.\r\n'),
  (316, 'Мы у Бога о многом просим', 'Мы у Бога о многом просим,\r\nА просить об одном лишь надо -\r\nО любви, что все переносит,\r\nИ чужому счастью рада.\r\nО любви, что все переносит,\r\nИ чужому счастью рада.\r\n\r\nКогда любишь - легко прощаешь.\r\nКогда любишь - не мыслишь злого.\r\nНа обиды не отвечаешь\r\nЗлобным взглядом и дерзким словом.\r\nНа обиды не отвечаешь\r\nЗлобным взглядом и дерзким словом.\r\n\r\nЕсли любишь - совсем не важно,\r\nЧто не все тебя понимают.\r\nТа любовь, что зажглась однажды,\r\nНегасимым светом сияет.\r\nТа любовь, что зажглась однажды,\r\nНегасимым светом сияет.\r\n\r\nИ огонь ее ярко светит,\r\nИ о нем говорить не нужно.\r\nЕго всякий и так заметит.\r\nТам, где он, - там любовь и дружба.\r\nЕго всякий и так заметит.\r\nТам, где он, - там любовь и дружба.\r\n\r\nЕсли ты ее проявлений\r\nВ своей жизни не замечаешь,\r\nЗначит, ты любви не имеешь,\r\nНо о ней понаслышке знаешь.\r\nЗначит, ты любви не имеешь,\r\nНо о ней понаслышке знаешь.\r\n\r\nМы у Бога о многом просим,\r\nНо, по сути, одно лишь надо -\r\nО любви просить лишь у Бога,\r\nО других не заботясь нуждах.\r\nО любви просить лишь у Бога,\r\nО других не заботясь нуждах.\r\n'),
  (317, 'Господи! Тобой душа согрета', 'Господи! Тобой душа согрета,\r\nТы - велик, а я - так слаб и мал!\r\nДай мне помнить все мои обеты,\r\nЧто не раз в молитве я давал.\r\n\r\nЧтобы я любым своим деяньем\r\nРадовал Тебя в своих путях,\r\nЧтобы все мои благодеянья\r\nНе были лишь только на словах.\r\n\r\nЧтоб молитва искренней, горячей\r\nВ каждое мгновение была,\r\nЧтоб во всех печалях и удачах\r\nНе смолкала Господу хвала.\r\n\r\nМного лет я изнывал в пустыне,\r\nТы - свершил в душе переворот.\r\nГосподи! Веди меня к вершине\r\nИз пучины скорби и забот.\r\n\r\nДай мне с трудной справиться задачей:\r\nПобедить земную суету!\r\nТолько полной радостной отдачей\r\nЯ Тебя, Господь, приобрету.\r\n'),
  (318, 'Ты объемлешь меня', 'Ты объемлешь меня, позади впереди,\r\nПолагаешь на мне руку,\r\nКуда б я не шёл, лик Твой будет со мной,\r\nНе укроюсь я от Духа.\r\n\r\nПрипев:\r\nВзойду ли я на небо, Ты там Господь.\r\nВойду ли в преисподню, Ты там Господь.\r\nВознесусь ли я на крыльях зари.\r\nДаже там ведом Тобою. Любящей Твоей рукою.\r\n'),
  (319, 'Иисуса приход ожидаем', 'Иисуса приход ожидаем, В последнее время живем.\r\nВсех грешных к Нему призываем, Все к вечности ближе\r\nидем. Вокруг все в неверии гибнет И канет в волнах\r\nнавсегда. И зреет вопрос в нашем сердце, Вопрос в одном\r\nслове: \"Когда?\"\r\n\r\nПрипев:\r\nКогда? Когда? Господь, о скажи же, когда?\r\nКогда в новом теле мы сможем Увидеть, услышать Тебя\r\nВдали от греховного шума? Скажи же, Господь наш, когда?\r\n\r\nВ груди нашей сердце тоскует, Тоскует и рвется\r\nдомой, В лазурные, чистые дали Стремится и рвется оно.\r\nТвоя лишь, Спаситель наш сильный, Твоя лишь нам\r\nпомощь нужна. И снова вопрос в нашем сердце, Вопрос в\r\nодном слове: \"Когда?\"\r\n\r\nНет, не исчисленья годами Его тревожат в пути. Но\r\nтихая боль и желанье Пребыть у Христовой груди. Лишь\r\nчистое сердце сумеет Спасителя видеть Христа.\r\nК лукавству Его не причастно Томленье в вопросе: \"Когда?\"\r\n'),
  (320, 'Весь мир поёт Богу', 'Весь мир поёт Богу Творцу «Слава!»\r\nЗа всю любовь, за красоту «Слава!»\r\nСлава Христу! Слава Христу! Осанна!\r\nЗа всю доброту воспойте все люди земли!\r\n\r\nПрипев:\r\nКак же прекрасно вокруг!\r\nЭто Христос – лучший Друг!\r\nПоле взрастил, нас накормил,\r\nНебо для нас открыл!\r\nСлавы достоин отвсех,\r\nСмыл Своей кровью наш грех,\r\nПойте Ему, пойте хвалу,\r\nРадуйтесь без конца!\r\n\r\nНа всех цветах Его любовь вижу,\r\nСо всех сторон Ему псалом слышу,\r\nСлава Христу! Слава Христу! Осанна!\r\nЗа всю доброту воспойте все люди земли!\r\n'),
  (321, 'Где б ни шёл я жизненной', 'Где б ни шёл я жизненной тропою,\r\nСколько бы тревог не встретил я,\r\nЧувствовал я, что всегда со мною,\r\nДобрая Спасителя рука!\r\nИ даже, если на пути уставал\r\nИ силы не было идти,\r\nТы нежно так, меня всегда поднимал\r\nИ вновь надежду дарил!\r\n\r\nПрипев:\r\nТы всегда со мной, Ты всегда со мной!\r\nЗнаю твёрдо, что всегда, со мной Твоя рука!\r\nТы всегда со мной (со мной),\r\nПусть вокруг темно, но на душе легко,\r\nПотому, что знаю я, верю, Ты всегда со мной!\r\n\r\nЕсли мрак сгущался надо мною\r\nИ оставили меня друзья.\r\nБыло мне не страшно, ведь с Тобою,\r\nШёл по этой бурной жизни я!\r\nИ чтобы ни случилось, в жизни со мной,\r\nГотов не плакать я, а петь!\r\nМне хорошо уже от мысли одной,\r\nТы можешь сердце согреть!\r\n'),
  (322, 'Поклонюсь Тебе, Иисус', 'Поклонюсь Тебе, Иисус,\r\nЯ всей душой и телом.\r\nВоспою Тебе, Иисус,\r\nЯ духом сердца своего.\r\nВеликий, _ Всемогущий,\r\nБог Великий, _ Всемогущий.\r\n\r\nПрипев:\r\nВ своём сердце\r\nХраню Завет Твоей Крови.\r\nВ новом сердце\r\nГорит огонь Твоей любви.\r\nВ своём сердце\r\nХраню Завет Твоей Крови.\r\nВ новом сердце\r\nГорит огонь Твоей любви.\r\n'),
  (323, 'С бурей жизни, я сражаюсь', 'С бурей жизни, я сражаюсь,\r\nВоин я Христа,\r\nНе страшусь, не отступаю\r\nС Ним я никогда.\r\nВ Его армии спасенных\r\nПравда лишь царит,\r\nИ врагов хоть миллионы -\r\nОн меня хранит.\r\n\r\nБог мой сильный, Вождь, Спаситель,\r\nТы - защитник мой,\r\nТы - мой добрый Повелитель\r\nЯ храним Тобой.\r\nК тишине родного края\r\nТы ведешь меня,\r\nМне открыты двери рая,\r\nЯ люблю Тебя.\r\n\r\nДруг мой робкий, маловерный,\r\nСкорби не страшись,\r\nБог мой - в слове неизменный,\r\nТы Ему молись.\r\nСилу бури успокоит\r\nВластной Он рукой,\r\nНебо для тебя откроет,\r\nТы войдешь в покой.\r\n'),
  (324, 'Ныне Божья любовь обнимает', 'Ныне Божья любовь обнимает\r\nВсех живущих людей на земле,\r\nНо, во мраке неверья блуждая,\r\nГрешный мир погибает во зле.\r\nБог зажег яркий свет искупленья\r\nНа погибельном темном пути\r\nИ велит благодать и спасенье\r\nПогибающим людям нести,\r\nИ велит благодать и спасенье\r\nПогибающим людям нести.\r\n\r\nНа тернистой дороге страданья\r\nВсюду видим мы крови следы -\r\nЭто верные шли христиане\r\nСовершать чудный подвиг любви.\r\nВ них зажженное Господом пламя\r\nБури зла потушить не смогли,\r\nИ до нас сквозь огонь испытанья\r\nЕго старцы седые несли,\r\nИ до нас сквозь огонь испытанья\r\nЕго старцы седые несли.\r\n\r\nДонесли, но служителей многих\r\nПотеряли за годы борьбы,\r\nНас зовут они, юных и сильных,\r\nВ поредевшие стать их ряды.\r\nЗа великое дело служенья\r\nЕсли жизнь мы готовы отдать,\r\nНас в ряды добровольцев спасенья\r\nИисус призывает вступать,\r\nНас в ряды добровольцев спасенья\r\nИисус призывает вступать.\r\n\r\nМы знамена Христова ученья\r\nИз слабеющих рук их возьмем\r\nИ навстречу векам, поколеньям\r\nСветоч правды во мрак понесем.\r\nПусть в пути нашем будут невзгоды,\r\nНо за гранью земных непогод\r\nСолнце правды, желанной свободы\r\nДля искупленных там не зайдет,\r\nСолнце правды, желанной свободы\r\nДля искупленных там не зайдет.\r\n'),
  (325, 'Скажи, ну что тебя', 'Скажи, ну что тебя сюда ведет?\r\nОтветь, ну что тебя сюда влечет?\r\nИ от чего твои глаза горят,\r\nТакой счастливый и открытый взгляд?\r\n\r\nПрипев:\r\nЭто любовь, Божья любовь,\r\nОна зальет все, заполнит все,\r\nТолько любовь, Божья любовь,\r\nВсе в мире пройдет, кроме нее!\r\n\r\nЯ возвожу к Иисусу сердцем взгляд,\r\nИ знаю: мой Господь мне очень рад.\r\nВ ответ с престола льется на меня\r\nВода живая, свет Его огня.\r\n\r\nСкажи, откуда этот блеск в глазах?\r\nОтветь, ну что дает нам Бог в сердца?\r\nОн помогает в этом мире жить,\r\nДает нам сил и верить, и любить!\r\n'),
  (326, 'Там, в вышине бесконечной', 'Там, в вышине бесконечной\r\nМежду планет, город стоит, город вечный.\r\nКак я хочу там отдохнуть\r\nИ дышать, дышать, дышать\r\nИ с восторгом наслаждаться\r\nПрославлять Христа, прославлять.\r\n\r\nВсе предо мною прекрасно,\r\nКажется мне, чувство мое не угасло.\r\nГород родной неповторим\r\nОн живет, растет, цветет.\r\nГород чудный, город славный\r\nКрасотой к себе влечет.\r\n\r\nЗдесь постоянно усталый,\r\nТрудно дышать в мире греховном мне стало.\r\nТолько в одном, мне со Христом\r\nВсе вперед, вперед, вперед,\r\nОн в небесную Отчизну\r\nНепременно доведет.\r\n '),
  (327, 'Берегите любовь к Иисусу', 'Берегите любовь к Иисусу друзья,\r\nДруг ко другу любовь берегите,\r\nБез любви жизнь прожить, просто будет нельзя,\r\nИ не тратьте её, но цените.\r\n\r\nБерегите надежду на Бога, Христа,\r\nОна верным всем рай обещает.\r\nВерой Божьей увидите неба врата,\r\nОна козни врага побеждает.\r\n\r\nВерь, надейся, люби – вот девиз христиан.\r\nПусть вас в жизни ничто не смущает,\r\nЖизнь загадок полна, как морской океан,\r\nПусть Господь ваш челнок направляет.\r\n'),
  (328, 'Утром, когда встает рассвет', 'Утром, когда встает рассвет,\r\nБоже, Тебе пою\r\nЗа благодать Твою ко мне\r\nИ за любовь Твою.\r\n\r\nПрипев:\r\nСлаву Тебе пою\r\nЗа благодать Твою,\r\nСилы дай мне всегда\r\nПрославлять Тебя.\r\n\r\nВ радости дай воспеть Тебе,\r\nВ скорби - не унывать\r\nИ о Твоей святой любви\r\nЛюдям всем рассказать.\r\n\r\nВ жизни моей Тебя хвалить -\r\nЦель будет пусть моя,\r\nЧтоб чудеса Ты мог творить\r\nВ сердце моем всегда.\r\n\r\nКогда приходит день к концу,\r\nЗвезды с небес блестят,\r\nК Богу с молитвой прихожу,\r\nЧтобы опять сказать:\r\n\r\nМилости полон Ты, Милости и любви!\r\nИ хочу я всегда Прославлять Тебя.\r\nМилости полон Ты, Милости и любви!\r\nИ хочу я всегда Прославлять Тебя.\r\n'),
  (329, 'Христос мой как о Тебе', 'Христос мой как о Тебе молчать\r\nКак тебя не благодарить\r\nВедь Ты дал право Тебя узнать\r\nИ Твой свет всем людям дарить\r\n\r\nПрипев:\r\nНе покидай меня я быть хочу с Тобой\r\nКаждый день и час и миг земной\r\nРуку подай любя в вечность веди меня\r\nВ мир счастья чудесного дня\r\n\r\nТворец мой Ты для людей лишь миф\r\nДля меня Спаситель Христос\r\nСоздатель неба, планет и звезд\r\nУтешитель от горя и слез\r\n\r\nВсевышний Ты нас благослови\r\nДля труда во Имя Христа\r\nПошли нам больше любви добра\r\nЧтоб горели наши сердца\r\n'),
  (330, 'Скоро день тот придет', 'Скоро день тот придет, и не будет всех забот,\r\nНи печали невзгод, ни нужды, ни тревог.\r\nМир царит в стране святой, тьмы не будет никакой.\r\nЧто за день, радостный день будет там!\r\n\r\nПрипев:\r\nЧто за день будет там, когда встречу я Христа!\r\nОн возьмет меня с Собой в край небесный, не земной.\r\nРуку даст Он мне Свою, и я с Ним туда пойду.\r\nЧто за день, радостный день будет там!\r\n\r\nТам не будет забот, Мир и радость не пройдет.\r\nНи болезнь, ни слеза, Радость будет без конца.\r\nБуду вечно я тогда Прославлять Царя Христа.\r\nЧто за день, радостный день Будет там!\r\n\r\nСкоро день тот придет, и не будет всех забот,\r\nНи печали невзгод, Ни нужды, ни тревог.\r\nМир царит в стране святой, Тьмы не будет никакой.\r\nЧто за день, радостный день Будет там!\r\n'),
  (331, 'Твои небеса и Твоя земля', 'Твои небеса и Твоя земля,\r\nИ что наполняет ее.\r\nТы видишь пути и знаешь дела,\r\nИ все помышленья мои.\r\n\r\nИду ли я, отдыхаю ли,\r\nИ Ты окружаешь меня,\r\nИ все пути мои\r\nИзвестны лишь Тебе.\r\n\r\nИ в тайне сердечной Ты знаешь меня.\r\nЖелания знаешь мои.\r\nНичто от Тебя не сокрыто,\r\nМой Бог, Ты - сила и слава моя!\r\n\r\nЗа все остаюсь благодарен Тебе,\r\nЗа милость Твою, за любовь!\r\nИ дальше храни ты меня,\r\nМой Господь - с Тобою хочу я идти.\r\n'),
  (332, 'Я на жизнь рассчитывать', 'Я на жизнь рассчитывать не вправе –\r\nСмерть меня не раз к себе звала.\r\nКак же мне Тебя, Господь, прославить\r\nЗа Твои чудесные дела?\r\n\r\nПрипев:\r\nЧем воздать мне за Твои даянья?\r\nЯ всегда перед Тобой в долгу.\r\nПрихожу к Тебе я с покаяньем –\r\nЭто всё, что сделать я могу.\r\n\r\nТы провёл через огни и годы,\r\nИ хотя я истины не знал,\r\nУкрывал меня от непогоды,\r\nОт врагов меня оберегал.\r\n\r\nЯ с тобою сиротою не был,\r\nЯ с тобою не был одинок.\r\nПод твоим голубоглазым небом\r\nТы мне дашь спокойный уголок.\r\n'),
  (333, 'Я песнь пою тебе', 'Я песнь пою тебе, Иерусалим,\r\nМолюсь за мир тебе, Иерусалим.\r\nХрани Господь тебя , Иерусалим!\r\nДа будет мир тебе, Иерусалим!\r\n\r\nПрипев:\r\nШалом, шалом, Иерусалим\r\nШалом, шалом – да будет мир тебе, Иерусалим!\r\nХрани Господи тебя Иеркусалим\r\n\r\nПрошу мир тебе, Иерусалим,\r\nПусть мир пребудет среди стен твоих,\r\nЛюбви и песен радостный родник!\r\nДа будет мир тебе, Иерусалим!\r\n\r\nНу как забыть тебя, Иерусалим,\r\nНельзя забыть тебя,Иерусалим.\r\nЯ помню о тебе, Иерусалим!\r\nХвалу тебе плю, Иерусалим!\r\n'),
  (334, 'Я поднимаю взор', 'Я поднимаю взор к далеким звездам,\r\nИ манят меня дальние миры,\r\nИ рвется так душа к просторам звездным,\r\nТуда, где обитаешь вечно Ты.\r\n\r\nПрипев:\r\nЗвезды, звезды,\r\nСветлые, прекрасные миры,\r\nВсе мои направлены туда\r\nЛучшие, счастливые мечты.\r\n\r\nС надеждой я смотрю на звезды эти,\r\nТам родина далекая моя,\r\nИ нет мечты прекраснее на свете,\r\nКогда же я вернусь в ее края?\r\n\r\nЯ жду, придет на землю мой Спаситель\r\nИ звездный мир откроет для меня,\r\nИ оставляя грешную обитель,\r\nЯ устремлюсь в небесные края.\r\n'),
  (335, 'Через всю жизнь', 'Через всю жизнь, из года в год,\r\nПри встрече и в разлуке,\r\nНас всех ведут, ведут вперед\r\nНевидимые руки.\r\n\r\nОни заботились о нас,\r\nНесли, вели, держали,\r\nМы их присутствие не раз\r\nРеально ощущали.\r\n\r\nОни порой ласкали нас,\r\nПорою бичевали,\r\nИ не давали нам упасть\r\nВ день скорби и печали...\r\n\r\nИ нам казалось иногда,\r\nЧто мы их потеряли.\r\nО, как мы плакали тогда!\r\nКак их найти желали!\r\n\r\nНо в час, когда иссякнет путь,\r\nУмолкнут речи, звуки,\r\nНас в Отчий Дом перенесут\r\nНевидимые руки.\r\n\r\nВ объятья примет нас Отец\r\nДля жизни без разлуки.\r\nИ там увидим, наконец,\r\nНевидимые руки.\r\n'),
  (336, 'Когда Иисус мой лучший Друг', 'Когда Иисус мой лучший Друг,\r\nТогда счастлив я.\r\nПусть даже гром гремит вокруг -\r\nПоет душа моя.\r\nПусть гаснут звезды и луна,\r\nВсе покроет ночь,\r\nДуша надеждою полна:\r\nОн силен мне помочь.\r\n\r\nОн мой лучший Друг, верю я Ему.\r\nС Ним не страшно мне, Он со мной везде.\r\nПусть гаснут звезды и луна,\r\nВсе покроет ночь,\r\nДуша надеждою полна:\r\nОн силен мне помочь.\r\n\r\nКогда Иисус мой - жизнь и свет,\r\nСердце песнь поет,\r\nЧто там на небе горя нет,\r\nГде мой Господь живет.\r\nЯ знаю: скоро Он придет,\r\nПридет Божий Сын,\r\nИ в Свой чертог меня возьмет\r\nНебесный Властелин.\r\n'),
  (337, 'Красота Иисуса, светись во мне!', 'Красота Иисуса, светись во мне!\r\nЧистота и любовь, проявись вполне!\r\nДух любви и огня (и огня),\r\nОживи и меня (и меня),\r\nЧтобы образ Иисуса сиял во мне.\r\n\r\nПусть увидят во мне красоту Христа,\r\nВсю Его чистоту и любовь всегда.\r\nО Дух Божий, приди (приди),\r\nВсе во мне измени (измени),\r\nПусть увидят во мне красоту Христа.\r\n\r\nДух Святой, Утешитель, живи во мне,\r\nБлагодать и обитель Твоя - во мне,\r\nЧтоб я плод приносил (приносил),\r\nИ Тебя я любил (я любил),\r\nИ вечерю со мною Ты разделил.\r\n\r\nПусть душа с торжеством гимн хвалы поет,\r\nСлавя имя Христа, Им одним живет,\r\nЧтобы имя Христа (Христа)\r\nПрославляли уста (уста),\r\nЧтобы пела душа гимн хвалы всегда.\r\n'),
  (338, 'На твоём и моём столе', 'На твоём и моём столе, золотится горбушка хлеба.\r\nА в далекой чужой стране косит голод людей свирепо.\r\nВ нашей памяти есть провал, о вражде вспоминать нелепо.\r\nМы лишь помним свои права за гроши наедаться хлебом.\r\n\r\nПрипев:\r\nИисус говорит, Я хлеб, значит, жизни без Бога нету\r\nИ душа без Него как склеп, и мертва без Него планета.\r\nИисус говорит, Я хлеб, значит, жизни без Бога нету\r\nИ душа без Него как склеп, и мертва без Него планета.\r\n\r\nДля голодного есть резон за горбушку отдать именье.\r\nА у нас иногда как сор, выметает горбушку веник.\r\nПоле спелой отборной ржи в лето позднее колосится.\r\nДобрым делом брат докажи, что для Господа стал ты пшеницей.\r\n\r\nЕсли жизнь для тебя Иисус, если Им ты дорогу меришь.\r\nЕсли горя познаешь вкус, то откроешь уставшему двери.\r\nА у нас впереди итог, так помыслим хотя бы немножко\r\nЕсли в доме хозяин Бог, мы не бросим на пол ни крошки.\r\n'),
  (339, 'Я нуждаюсь в тебе мой спаситель', 'Я нуждаюсь в тебе мой спаситель\r\nЯ желаю всегда быть с Тобой,\r\nТы - Господь мой, любимый Учитель,\r\nУпованье мое и покой.\r\n\r\nОчищенья душа моя жаждет.\r\nПлоть желает противного ей.\r\nВсе борьба и борьба, о, когда же\r\nЯ достигну небесных дверей?\r\n\r\nПрипев:\r\nЯ жду Тебя, люблю Тебя.\r\nМне трудно на пути.\r\nБез Пастыря, Хранителя\r\nЯ не могу дойти.\r\n\r\nМне трудно на пути,\r\nМне трудно на пути\r\nБез Пастыря, Хранителя,\r\nЯ не смогу дойти\r\n\r\nИ когда в этой жизни унылой\r\nОт борьбы утомится душа,\r\nПодойди же ко мне, мой Пастырь любимый,\r\nВедь с Тобою так жизнь хороша.\r\n\r\nПоселись Ты во мне, мой Спаситель,\r\nИ даруй чувствованья Твои,\r\nЧтобы верным я был. Твой служитель,\r\nО, приди же ко мне и живи.\r\n'),
  (340, 'Тихий вечер, закат', 'Тихий вечер, закат нежно-розовый.\r\nЗапах травок весенний в степи.\r\nЯ хочу, воспевая о Господе,\r\nТак всё время идти и идти.\r\nЗдесь легко этой свежестью дышится,\r\nСкоро ночь, на востоке - луна.\r\nВ сердце тихая радость колышится,\r\nИ душа моя миром полна.\r\n\r\nПой же, пой ты, душа, Богу нашему,\r\nПеснь хвалы громогласней, сильней.\r\nТы Христовой любовью украшена -\r\nНа тебе помазанья елей.\r\nЯ пою во весь голос, несдержанно.\r\nНе велика та песня, мала.\r\nПусть хоть буду всем миром отвержен я,\r\nЛишь бы Богу не смолкла хвала.\r\n\r\nЧтобы свет Твой, как факел пылающий,\r\nЯ пронёс среди мрака сильней.\r\nИ чтоб в Духе Святом пребывающем,\r\nБыть в числе голубицы Твоей.\r\nПой же пой ты душа Богу нашему,\r\nПеснь хвалы громогласней сильней,\r\nТы Христовой любовью украшена,\r\nНа тебе помазанья елей.\r\n\r\nЛишь бы верным во всех испытаниях,\r\nОказался Тебе я, Творец.\r\nЛишь во всех бы скорбях и скитаниях,\r\nТвердо верить в желанный конец.\r\nНе нужны Тебе рифмы сложенные.\r\nКрасноречие слов языка.\r\nСердце, сердце возьми на служение.\r\nМой Спаситель Христос, на века.\r\n'),
  (341, 'Разливайся, песня', 'Разливайся, песня, радостным потоком -\r\nО любви небесной близким и далеким.\r\n\r\nПрипев:\r\nКто Иисуса любит и за Ним идет,\r\nТот на небе будет, где Господь живет,\r\n:,:Где Господь живет.:,:\r\n\r\nВ плане Иисуса: грешникам спасенье,\r\nСтрашная Голгофа, радость воскресенья.\r\n\r\nЖить Спаситель будет в возрожденном сердце,\r\nСлавьте Бога, люди, Он нас спас от смерти.\r\n'),
  (342, 'Учись у солнца жизнь дарить', 'Учись у солнца жизнь дарить и свет, И каждому\r\nс любовью помогать, Но на любовь не ждать любви в\r\nответ: Кто может солнцу что-нибудь отдать?\r\n\r\nУчись у солнца путь всем освещать, А повстречавших\r\nгоре - ободрять. Учись у солнца радостно сиять, На небе\r\nжить, а землю согревать!\r\n\r\nУчись не обижаться, не грустить. Но радовать, как\r\nрадует оно. Живи, как солнце, чтобы с Богом жить.\r\nСтремись к добру, отвергнув жизни зло.\r\n\r\nГде б ни был я и ты, мой милый друг, Да светит свет\r\nХристов от нас вокруг. Чтоб люди, видя добрые дела,\r\nПрославили Небесного Отца.\r\n\r\nУчись, мой друг, пока оно горит И ласковый привет\r\nнам с неба шлет, И словно тихо сердцу говорит.\r\nЧто Солнце правды скоро уж взойдет!\r\n'),
  (343, 'Часто тебя волнует вопрос', 'Часто тебя волнует вопрос,\r\nДля чего живёт человек на свете?\r\nИ если ответ ты мой примешь всерьёз,\r\nЯ тебе на вопрос отвечу.\r\n\r\nПрипев:\r\nЖизнь так коротка, пролетит, как миг,\r\nПрошедшие годы окажутся днями.\r\nНо важно понять, что жизнь - экзамен.\r\nЭкзамен длиною в жизнь.\r\n\r\nПопробуй, мой друг, усвоить урок,\r\nЧто после смерти приходит вечность.\r\nЖизнь - испытание, смерти порог,\r\nА за порогом бесконечность.\r\n\r\nПрими сегодня добрый совет,\r\nПойди по правильной дороге.\r\nВозьми один счастливый билет,\r\nС единой темой: \"Вера в Бога\".\r\n\r\nБог создал бессмертной душу твою,\r\nНо дал тебе земное тело.\r\nЕсли ты хочешь быть с Ним в раю,\r\nЖиви для Бога и правду делай.\r\n'),
  (344, 'Мой Иисус, возьми меня', 'Мой Иисус, возьми меня в Свои ладони,\r\nСогрей теплом и нежностью согрей.\r\nЯ как былинка средь большого поля,\r\nЖиву под милостью и добротой Твоей.\r\n\r\nПрипев:\r\nНе отпускай меня, когда я буду рваться\r\nВ большие выси неосознанной мечты.\r\nИ если не успею разобраться,\r\nНе отпускай меня, всегда будь рядом Ты.\r\n\r\nТы наделил меня таким же нежным светом,\r\nТы наделил меня такой же красотой.\r\nМы обвенчались, и в одежде этой\r\nПовсюду следовать я буду за Тобой.\r\n'),
  (345, 'Ты, Господь, ценнее серебра', 'Ты, Господь, ценнее серебра,\r\nТы, Господь, дороже золота.\r\nТы, Господь, прекрасней жемчугов.\r\nТы, Господь, – один лишь мой Бог.\r\n\r\nТолько Ты, Господь, Спаситель мой.\r\nТолько Ты – упование моё.\r\nТолько Ты, Господь, Создатель мой.\r\nТолько Ты – Бог мой.\r\n'),
  (346, 'Скрой меня Своим крылом', 'Скрой меня Своим крылом\r\nСохрани под сильною рукой\r\n\r\nПрипев:\r\nПусть бушует шторм и гром гремит\r\nНад бурей буду я с Тобой парить\r\nМой Отец, Ты Царь над всей землей\r\nЯ не боюсь – мой Бог со мной\r\n\r\nНайду покой в Тебе, Иисус\r\nВерю я в могущество Твоё\r\n'),
  (347, 'Хочет всех людей Господь', 'Хочет всех людей Господь благословить,\r\nВесть благую в сердце каждому вложить.\r\nДверь открой, широко сердце распахни,\r\nИисуса ты впусти!\r\n\r\nПрипев:\r\nИисуса ты впусти, Иисуса ты впусти,\r\nДверь открой, широко сердце распахни,\r\nИисуса ты впусти!\r\n\r\nОн пришёл за грех наш на кресте страдать,\r\nВоскресением Своим победу дать,\r\nДверь открой, широко сердце распахни,\r\nИисуса ты впусти!\r\n\r\nБродишь ли в пустыне, хочешь путь найти,\r\nЗнай, таких Иисус пришёл взыскать, спасти.\r\nДверь открой, широко сердце распахни,\r\nИисуса ты впусти!\r\n\r\nСчастлив, кто Спасителю себя отдаст,\r\nКто Его возлюбит, тот хвалу воздаст.\r\nДверь открой, широко сердце распахни,\r\nИисуса ты впусти!\r\n'),
  (348, 'Христос - надежда тех сердец', 'Христос - надежда тех сердец\r\nСредь этой жизни на земле,\r\nКоторых спас святой Отец\r\nОт уз греха и жизни в зле.\r\nОн жизнь Свою за них отдал\r\nИ мир небесный в сердце дал.\r\n\r\nПрипев:\r\nХристос - надежда христиан\r\nВсех континентов и всех стран.\r\nДве тысячи уж лет ведет\r\nОн Свой искупленный народ.\r\n\r\nИ кто познал любовь Христа\r\nЛюбить стремиться, как Христос:\r\nЛюбить без меры, без конца,\r\nЛюбить, хоть в жизни много слез,\r\nЛюбить гонителей, врагов,\r\nЗакон Иисуса ведь таков.\r\n\r\nХоть мир и хвалится собой,\r\nСвоим умом гордится он,\r\nНо что весь мир перед Тобой?\r\nВ своих глазах хоть силен он\r\nХвалиться буду я Творцом,\r\nМоим Спасителем Христом.\r\n\r\nИ нет здесь силы, чтоб могла\r\nНас отлучить от благ Христа:\r\nНи широта, ни долгота,\r\nНи высота, ни глубина;\r\nВсех христиан Господь хранит,\r\nДля них Он - всемогущий щит.\r\n\r\nНе надо строить башен нам,\r\nНебес просторы покорять,\r\nЧтобы подняться в небеса\r\nИ там планеты созерцать.\r\nХристос-Творец восхитит нас,\r\nВ небесный дом возьмет в свой час.\r\n'),
  (349, 'Жаратқан Ием – Бағушым', 'Жаратқан Ием – Бағушым, Мен еш нәрседен тарықпаймын.\r\nМені жайлауға жаяды, Тұнық суға апарады.\r\n\r\nҚайырмасы:\r\nО, Иса – Құтқарушым, О, Иса – менің Бағушым,\r\nО, Иса – Жұбатушым, О, Иса – менің Ұстазым.\r\n\r\nЖаныма Ол күш береді, Жетектеп түзу жолменен.\r\nҚараңғы жерде жүрсем де, Қауіп-қатерден қорықпаймын.\r\n\r\nСебебі Ием қасымда, Маған сүйеніш болады.\r\nДұшпандарымның алдында, Маған дастархан жаяды.'),
  (350, 'Я колени склоню и услышит', 'Я колени склоню и услышит Всевышний\r\nБлагодарность моя и печали мои\r\n// Я колени склоню и душою услышу\r\nТихий голос Христа – голос вечной любви //\r\n\r\nЯ колени склоню и раскроется небо\r\nДивный Господа край где не гаснет заря\r\n// И согреется сердце живительным светом\r\nИ исполнится сил новой верой горя //\r\n\r\nЯ колени склоню и увижу так ясно\r\nЗаблуждения свои и ошибки в пути\r\n// Я пойму, что Христос был мне светом в ненастье\r\nНес меня на руках и без гнева простил //\r\n\r\nЯ колени склоню перед Богом Всевышним\r\nСвое сердце как дар принесу к алтарю\r\n// Тихий голос Христа над собою услышу\r\nЯ колени склоню, я колени склоню //\r\n'),
  (351, 'Когда Бог близок', 'Когда Бог близок, на душе покой\r\nСвет благодати льётся неземной\r\nЯ поднимаю к небесам глаза\r\nИ прославляю Небесного Отца\r\n\r\nПрипев:\r\nТам в небесах ангелов слышно пенье\r\nБог мне скала, Бог моё утешенье\r\nСлавит душа те сладкие мгновенья\r\nКогда сойдёт с чудных небес вдохновенье\r\n\r\nСколько волнений на земном пути\r\nСколько сомнений, сколько суеты\r\nТолько с Иисусом мне легко идти\r\nВ Нём моё счастье, надежды и мечты.\r\n'),
  (352, 'Что этот свет', 'Что этот свет, он созданье Всевышнего,\r\nВоля и разум Творца,\r\nЭто ль ни есть выражение Божьей любви?\r\nГоры, равнины и дали лесистые.\r\nСкалы, овраги и травы душистые,\r\nУтра румянец и ночи мерцание –\r\nБога создание, Бога дыхание.\r\n\r\nПрипев:\r\nЗвезд дивные алмазы на черный бархат неба,\r\nТы поместил Своей рукой, поместил своей рукой.\r\nНет, я ещё ни разу столь к Тебе близок не был,\r\nТы подарил душе покой, подарил душе покой.\r\n\r\nЧто этот мрак, силы вражьей сгущение,\r\nВечно скрывает свой лик?\r\nСлаву, Отец, что не ею дано нам спастись!\r\nСолнце взойдет, тучи черные скроются,\r\nЗемли водой дождевою омоются,\r\nПтицы небесные песню Тебе поют,\r\nИмя Твоё облекая в гармонию.\r\n\r\nЧто эта жизнь – Божий дар и служение,\r\nРадость и мука души?\r\nГде ты найдёшь совокупность столь разных в одном.\r\nПусть из страданий и счастие строится,\r\nЛишь после смерти награда откроется.\r\nЧаша скорбей производит терпение,\r\nВся наша жизнь на земле лишь мгновение!\r\n'),
  (353, 'Жаратқан жерді де', 'Жаратқан жерді де, аспанды да, Шын сүйіп, бас ием Құдайыма.\r\nАдамзат күнәдан арылсын деп, Жіберді Өз Ұлы Исаны да.\r\n\r\nҚайырмасы:\r\nЖетеле мені, жетеле, Ғажайып жұмақ төріне,\r\nРуханды берсең кеудеме, Мәңгілік құлмын Өзіңе.\r\n//Өзіңе ғана бағынам, Өзіңе ғана табынам,\r\nӨзіңнен артық панам жоқ, Құдайым, Маған сиынам//\r\n\r\nАдасып көп жылдар шаршадым мен,\r\nСонда да тыныштық таппадым мен.\r\nСол сәтте Құдайды ойға алып ем,\r\nБақытқа кенеліп, шаттандым мен.\r\n\r\nОқыдым Киелі кітабыңды,\r\nЖүрегім азаптан құтқарылды.\r\nҰмыттым қайғы мен азабымды,\r\nӨйткені таныдым Құдайымды.'),
  (354, 'Я хочу быть таким', 'Я хочу быть таким, как Ты, Иисус.\r\nЯ хочу быть таким, как Ты.\r\nМой сосуд в Твоих руках.\r\nЯ хочу быть таким, как Ты.\r\n\r\nЯ хочу всех любить, как Ты, Иисус.\r\nЯ хочу всех любить, как Ты.\r\nМой сосуд в Твоих руках.\r\nЯ хочу всех любить, как Ты.\r\n\r\nЯ хочу всем служить, как Ты, Иисус.\r\nЯ хочу всем служить, как Ты.\r\nМой сосуд в Твоих руках.\r\nЯ хочу всем служить, как Ты.\r\n\r\nЯ хочу всех прощать, как Ты, Иисус.\r\nЯ хочу всех прощать, как Ты.\r\nМой сосуд в Твоих руках.\r\nЯ хочу всех прощать, как Ты.\r\n'),
  (355, 'Библия знакомит нас', 'Библия знакомит нас с страной, её нет на карте ни одной,\r\nНо для Божьих чад Евангелие лучше всяких карт здесь на земле.\r\nЧудная страна! Как хотел бы я в тебе всегда пребыть.\r\nГде живёт Христос, далеко от слёз хотел бы быть.\r\n\r\nТам из жемчуга ворота все и из золота дороги те.\r\nСветит свет Господнего лица, как прекрасно будет нам тогда.\r\nВоспоём тогда Господа Христа, когда домой придём.\r\nВ доме золотом будем петь о том, как любит Он.\r\n\r\nДруг, не медли, о, скорей реши. Есть перед тобою два пути -\r\nИзбери, каким тебе идти. Наш совет: тернистым ты пойди.\r\nЧудная страна! Как хотел бы я в тебе всегда пребыть.\r\nГде живёт Христос, далеко от слёз хотел бы быть.\r\n\r\nО, реши же и иди, мой друг, за Христом. Вот вскоре наш Иисус\r\nСам придёт во славе неземной, чтобы верных взять к Себе домой.\r\nВоспоём тогда Господа Христа, когда домой придём.\r\nВ доме золотом будем петь о том, как любит Он.\r\n'),
  (356, 'О, Иисус, Ты простил меня', 'О, Иисус, Ты простил меня,\r\nТы омыл все грехи мои\r\nИ теперь знаю точно я,\r\nЧто прощён, я Тобой прощён!\r\n\r\nПрипев:\r\nБудь прославлен, Божий Сын!\r\nБудь прославлен, Божий Сын!\r\nТы простил меня и я Тобой спасён!\r\nБудь прославлен, Божий Сын!\r\nБудь прославлен, Божий Сын!\r\nТы простил меня и я спасён!\r\n\r\nО, Иисус, я люблю Тебя\r\nИ я славлю Твоё имя!\r\nИ теперь знаю точно я,\r\nЧто спасён, я Тобой спасён!\r\n'),
  (357, 'Последнее время уже наступает', 'Последнее время уже наступает,\r\nДрожит вся земля.\r\nПридет наш Спаситель,\r\nУже не учитель, Он - грозный судья!\r\nМой друг, ободрись же,\r\nМой друг, приготовься,\r\nЧтоб встретить Царя.\r\nПоследнее время в эпоху растленья\r\nВстречает земля.\r\n\r\nПрипев:\r\nПоследнее время уже наступает.\r\nДрожит вся земля, в смятении она.\r\nПоследнее время тебя вопрошает:\r\n«Как встретишь Христа?»\r\n\r\nПоследнее время летит незаметно\r\nСо скоростью сна.\r\nВ заботах житейских почти не заметив,\r\nКак осень пришла.\r\nНо так же, как прежде\r\nМы верим с надеждой в Иисуса слова\r\nПоследнее время в эпоху растленья\r\nВстречает земля.\r\n\r\nВ последнее время так много работы\r\nДля Божьих детей.\r\nВедь в мире коварном навеки погибнет\r\nТак много людей.\r\nМой друг, собери же последние силы,\r\nОтдай для Христа.\r\nПоследнее время в эпоху растленья\r\nВстречает земля.\r\n'),
  (358, 'Кто сравнится с Тобою, Отец?', 'Кто сравнится с Тобою, Отец? Во вселенной равных нет Тебе!\r\n//Воспоем же хвалу Богу Всевышнему, Богу Всевышнему!//\r\n\r\nКто сравнится с Тобою, Иисус, Агнец Божий, закланный за всех?\r\n//Ты прославлен вовек! Имя Твое выше всех! Имя Твое- выше всех.//\r\n\r\nКто сравнится с ТОбою, Дух Святой- Утешитель милостивый, благой?\r\n//Будь же с нами в пути; в царство Твое нас веди! В царство Твое нас веди!//\r\n'),
  (359, 'В Тебе укроюсь я', 'В Тебе укроюсь я,\r\nТы слышишь голос мой,\r\nКогда я в смятении,\r\nК Тебе я прибегаю.\r\nУповая на Тебя,\r\nУповая  на Тебя,\r\nСлабый скажет:\r\n«Я силен в Господе моем»!\r\n'),
  (360, 'Из глубин души моей', 'Из глубин души моей льется песня о Тебе.\r\nТы – Господь, кто всех милей в моей судьбе.\r\nВек проходит, время мчится; в сердце – Божья благодать.\r\nНу, а песню, словно птицу, невозможно удержать.\r\n\r\nПрипев:\r\nТы с нами, Бог! Ты с нами, Бог!\r\nСлавим имя Христа, Нашей веры исток.\r\nТы с нами, Бог! Ты с нами, Бог!\r\nВ слабом сердце наша вера прорастет, как колосок.\r\nТы с нами, Бог!\r\n\r\nВоздух песнею наполнят отовсюду голоса,\r\nПусть ее нам подпоют поля, леса.\r\nЭта песня о спасении, о любви на всей земле,\r\nО великом искуплении, о тебе и обо мне!\r\n\r\nВ нашем сердце, полном песни, и в росинке и в слезе\r\nСчастье, горе с нами вместе в солнце и грозе.\r\nПойте реки, пойте горы и равнины и моря.\r\nИмя Господа и веру снова прославляю я!\r\n'),
  (361, 'Жизнь предлагает', 'Жизнь предлагает нам много путей,\r\nНо один лишь ведет нас к Тебе,\r\nБоже Праведный, мы выбираем его.\r\nЧто бы быть нам рожденными вновь,\r\nПотому, что пролив Свою кровь,\r\nБоже Праведный, Ты дал нам жизнь, пойте:\r\n\r\nПрипев ( 2 раза):\r\nАллилуйя – и услышит Господь\r\nИ Он примет хвалу нашу,\r\nАллилуйя – нашу жизнь освятит\r\nИ за всё нас простит, Боже (2й раз наш Господь)\r\n\r\nТы нас прости своих грешных детей\r\nИ позволь до конца наших дней,\r\nПеть хвалу Тебе, Пастырь наш любящий.\r\nВсё, чем мы дышим и чем мы живем,\r\nЛишь надеждой, что в царстве Твоём\r\nБоже Праведный, мы пребывать будем.\r\n'),
  (362, 'Если в темноте ты идешь один', 'Если в темноте ты идешь один, долог и уныл твой путь,\r\nЕсли ты иссох без веры и любви, ищешь, к тебе найти\r\n\r\nПрипев:\r\n//Реки воды, реки воды, реки воды живой.//\r\n\r\nСделай только шаг, шаг над пропастью и тогда увидешь длань Того,\r\nКто победил, Кто кроток и смирен, Кто в дар с небес тебе дает.\r\n\r\nВновь сияет свет и ты в него войдешь и твердо станешь на скале,\r\nОткроет Сам Господь источники тебе, и вечность реки потекут.\r\n'),
  (363, 'В жизни много есть разных вопросов', 'В жизни много есть разных вопросов\r\nОт вопросов томится душа.\r\nНо один самый лучший и важный\r\nЕщё раз прозвучал для тебя\r\nОн один не даёт лишь покоя\r\nНо ответ нужно дать на него\r\nПочему ты живёшь без Иисуса?\r\nПочему ты живёшь без Него?\r\n\r\nПрипев:\r\nПочему ты стоишь в стороне?\r\nПочему не идёшь к Иисусу?\r\nОн поможет утешит в беде\r\nОн твой друг самый верный и лучший\r\nО прийди о прийди друг скорей\r\nВот свободны, распахнуты двери\r\nОт ненастья, вражды и скорбей\r\nОн зовёт тебя, Он зовёт тебя\r\nОн зовёт тебя в светлое небо\r\n\r\nДруг подумай сегодня серьёзно\r\nБудет поздно ведь думать тогда\r\nКогда вдруг перед Богом предстанешь\r\nЧтоб ответить за жизни года\r\nКогда встретишся ты с Иисусом\r\nКогда встретятся ваши глаза\r\n'),
  (364, 'Воспою любовь Христа!', 'Воспою любовь Христа!\r\nУмер Он от мук креста\r\nЗа тебя и за меня,\r\nВсех нас, грешных, возлюбя.\r\n\r\nПрипев:\r\nВоспою любовь Христа,\r\nВознесут хвалу уста.\r\nУмер Он, чтоб я мог жить,\r\nПо Его путям ходить.\r\n\r\nПрежде слез моих за грех,\r\nПлакал Он тогда за всех;\r\nПрежде чем молился я -\r\nОн молился за меня.\r\n\r\nГлубину Его любви\r\nНе могу вполне понять:\r\nКак Он снял грехи мои\r\nИ дарует благодать?\r\n\r\nЧем же я Ему воздам?\r\nВедь за грех мой Он страдал!\r\nСердце все, Господь, возьми\r\nНа все жизненные дни.\r\n'),
  (365, 'Господи! Милость Твоя', 'Господи! Милость Твоя обновляется каждое утро...\r\nГосподи, милость Твоя лучше нежели жизнь.\r\nДухом Твоим наполняешь Ты небо и землю,\r\nУхо Твоё наклоняешь к молитвам святым.\r\n\r\nВеличаю Тебя, прославляю Тебя чудный Бог и Творец!\r\nТы Единый достоин хвалы, поклоненья.\r\nВсё живое да славит Тебя, мой небесный Отец.\r\nПред Тобою, о Бог мой, в смирении склоняю колени.\r\n\r\nГосподи! Всё прекрасно устроила мудрость Твоя во вселенной!\r\nИ она есть художница дел Твоих чудных, Господь.\r\nТы противишься гордым и благословляешь смиренных,\r\nКто подобен Тебе, о великий и дивный Господь!\r\n'),
  (366, 'Жатва есть кончина века', 'Жатва есть кончина века,\r\n- так Господь сказал.\r\nИ всегда у человека\r\nв жизни есть финал.\r\n\r\nПрипев:\r\nНебо для меня открыто,\r\nЯ навек спасён.\r\nИисус моя защита,\r\nЯ Им утверждён.\r\n\r\nЕсли ты сегодня с Богом\r\n- славен твой конец.\r\nНесмотря на все невзгоды,\r\nЖдёт тебя венец.\r\n\r\nСейте доброе, святое,\r\nБудет время жать.\r\nИисус пошлёт обильно\r\nс неба благодать.\r\n\r\nРадостно поднимем руки,\r\nБлизится конец.\r\nНе страшны нам смерть и муки,\r\nС нами Бог Отец.\r\n'),
  (367, 'Как цветок головку к солнцу', 'Как цветок головку к солнцу гнёт и как жаждет лань воды,\r\nТак душа моя к Иисусу вопиет\r\nТвой мир я чувствую вблизи, в нём сокрыт я как дитя\r\nПринадлежит тебе вся жизнь моя.\r\n\r\nПрипев:\r\nВсе во всём Ты создал для меня\r\nТы мир даришь, богат в Тебе лишь я\r\nИисус Ты жизнь даришь сполна\r\nТы -Друг мой и не устрашусь я зла.\r\n\r\nНикогда я не смогу понять, за что любим Тобой ,\r\nЯ часто не послушен так Тебе\r\nНо я знаю, что грехи прощать Ты готов, что б всё забыть\r\nКак дитя в мольбе взываю я к Тебе.\r\n'),
  (368, 'Есть счастье святое', 'Есть счастье святое, есть дивная радость,\r\nПодобной нигде не найти:\r\nМолитва к Иисусу, любовь и отрада,\r\nИ сила, чтоб крест свой нести.\r\n\r\nПрипев:\r\nВ Тебе моя радость, в Тебе моя сила,\r\nВ Тебе, мой Иисус дорогой.\r\nК Тебе я взываю, Христос мой любимый,\r\nТебя я люблю всей душой.\r\n\r\nСредь тяжких мучений, борьбы и тревоги,\r\nВо мраке разбитых сердец\r\nЕсть счастье святое беседовать с Богом\r\nВ сиянии светлых надежд.\r\n\r\nЕсть счастье святое, я радуюсь в Боге:\r\nМеня Он в виссон Свой одел\r\nИ место навек обещает в чертоге,\r\nГде будет наш вечный удел.\r\n\r\nЕсть пламя молитвы, простертые руки\r\nК Тому, Кто страдал и воскрес.\r\nЕсть луч утешенья средь скорби и муки,\r\nДарованный Богом чудес.\r\n'),
  (369, 'Хочу я петь о Господе моём', 'Хочу я петь о Господе моём, —\r\nОн душу спас, ведёт меня в свой дом,\r\nОн посылает силу мне Свою, —\r\nВот потому о Нём всегда пою.\r\n\r\nНе умолкай моя душа, а пой,\r\nХоть трудно петь тебе средь бури злой,\r\nТы слышишь песнь мою, — не унывай, —\r\nОн Сам ведёт тебя в небесный край.\r\n\r\nЛукавый шепчет: «Для кого поёшь?\r\nТы согрешил, за это ты умрёшь.»\r\nДа я умру, но с песней на устах, —\r\nПрощён мой грех, я буду в небесах.\r\n\r\nДрузья давайте вместе воспоём\r\nХвалебный Богу нашему псалом, —\r\nЗа жертву Сына Божьего Христа,\r\nЗа кровь пролитую за нас с Креста.\r\n'),
  (370, 'Трудная дорога впереди лежит', 'Трудная дорога впереди лежит,\r\nУповай на Бога сердце говорит.\r\nХочется смириться, хочется взывать,\r\nПомоги молиться и не унывать.\r\nХочется смириться, хочется взывать,\r\nПомоги молиться и не унывать.\r\n\r\nДай мне помнить свято, что путем скорбей\r\nШел Христос когда-то возлюбя людей.\r\nИ когда сгустится зло вокруг опять,\r\nПомоги молиться и не унывать.\r\nИ когда сгустится зло вокруг опять,\r\nПомоги молиться и не унывать.\r\n\r\nМне бы так хотелось, чтобы Дух Святой\r\nОвладел всецело сердцем и душой,\r\nЧтоб не усомниться, чтобы устоять,\r\nЧтоб всегда молиться и не унывать.\r\nЧтоб не усомниться, чтобы устоять,\r\nЧтоб всегда молиться и не унывать.\r\n'),
  (371, 'Мне Господь Иисус дороже клада', 'Мне Господь Иисус дороже клада,\r\nБольше нет отрады, чем быть с Ним.\r\nОн со мной, мне ничего не надо -\r\nМир в душе ничем не заменить.\r\n\r\nПрипев:\r\nОн - источник всех благословений,\r\nОн - родник живительной воды,\r\nОн излечивает от сомнений\r\nИ хранит меня Он от беды.\r\n\r\nИ хоть и страшно завывает буря,\r\nИ клубятся низко облака,\r\nНо я знаю: выше туч, в лазури -\r\nЖизни нескончаемой река.\r\n\r\nИ когда иду опять к распятью,\r\nС каждым днем короче путь земной.\r\nМиг один - и над небесной гладью\r\nВстретится Спаситель мой со мной!\r\n'),
  (372, 'Ах Земля, чужая сторона', 'Ах Земля, чужая сторона,\r\nчуть обжитый уголок Вселенной!\r\nО другом Отечестве узнав.\r\nМы идем к нему без сожаленья!\r\n\r\nТы, Земля, обиды не таи.\r\nНе суди придирчиво и строго.\r\nНынешние жители Земли\r\nЗавтра, может быть, увидят Бога!\r\n\r\nА пока что в хрупкости цветка\r\nИ в тревожном ожиданьи почек\r\nЗнаешь, как приятно узнавать\r\nБожией любви знакомый почерк!\r\n\r\nНо растут грехи, как сорняки,\r\nИ порою даже в птичьих песнях\r\nСлышно столько горестной тоски\r\nО нетленной красоте небесной!\r\n\r\nСкоро скажем мы тебе \"Прощай!\"\r\nИ свершится вмиг переселенье.\r\nАх, Земля, наш временный причал,\r\nЧуть обжитый уголок Вселенной!\r\n'),
  (373, 'Будем славить Бога', 'Будем славить Бога в радости и счастье,\r\nБудем славить Бога в бедности, в трудах\r\nВ полном изобильи или с малой частью,\r\nБудем славить Бога мы в своих мольбах.\r\n\r\nПрипев:\r\nБлагословенье в этом, в том, что будем вместе,\r\nЧто придём мы в вечность к Богу своему.\r\n/:Славьте, славьте Бога, потому что есть Он\r\nИ земной дорогой мы идём к Нему:/.\r\n\r\nБудем славить Бога на дорогах трудных,\r\nНа морях коварных, в ледяных снегах.\r\nСтанет жизнь пустыней или садом чудным,\r\nБудем славить Бога также, как сейчас.\r\n'),
  (374, 'Молитва, это крик души', 'Молитва, это крик души\r\nЧем искренней, тем тише\r\nИ  так легко, когда в тиши\r\nЕё Господь услышит\r\nОна, как самый громкий плач\r\nНе пред людьми, пред Богом\r\nВ надежде, что Небесный Врач\r\nПоможет вам во многом\r\n\r\nПрипев:\r\nМолитва, это Божий дар\r\nОдно из средств, не многих\r\nЧерез неё и стар и мал\r\nВедут беседу с Богом\r\n\r\nОна, глоток живой воды\r\nЗалог успешной битвы\r\nОна, защита от беды\r\nПостигшим суть молитвы\r\nОна, орудие труда\r\nОна, души блаженство\r\nЧерез неё везде всегда\r\nПриходят к совершенству\r\n\r\nНо жаль, молитва не для всех\r\nУбежище от бедствий\r\nТворивший постоянно грех\r\nТеряет это средство\r\nКак пустоцвет неверных чувств\r\nОна тогда бесплодна\r\nЛукавства лесть из грешных уст\r\nДля Бога неугодны.\r\n'),
  (375, 'Сколько звуков вокруг!', 'Сколько звуков вокруг!\r\nДаже ночью не смолкнут машины,\r\nИ глядит удивленно на шумную землю луна,\r\nПо дорогам шуршат в быстром беге колесные шины.\r\nГде же ты, тишина?\r\n\r\nМы куда-то спешим,\r\nМы о чем-то заботимся вечно,\r\nНаша жизнь суеты и пустого движенья полна.\r\nСмотрит вниз с высоты ночью темной далекий Путь Млечный\r\nУ него – тишина.\r\n\r\nЯ все время тружусь,\r\nСтолько сделать и выполнить надо.\r\nЯ все время с людьми и так редко бываю одна,\r\nНо душе моей больно, и шум ей – смертельнее ада,\r\nЕй мила тишина.\r\n\r\nТолько поздно в ночи,\r\nУтомясь от дневной канители,\r\nЯ сажусь, наконец,- мне не нужно глубокого сна-\r\nМне не нужно удобной и теплой и мягкой постели, –\r\nМне нужна тишина.\r\n\r\nИ душа отдохнет,\r\nСловно лань у воды в полдень знойный,\r\nИ в молитве пред Богом забудет о шуме она.\r\nИ сама замолчит, чтобы слышать как с неба спокойно\r\nГоворит тишина.\r\n\r\nЧто сравнится с тобой,\r\nТишина, нисходящая с неба?\r\nТы – елей для души, ты покоя и мира полна,\r\nБез тебя не вкусил бы наш дух благодатного Хлеба,\r\nДар небес – тишина!\r\n'),
  (376, 'В небе над Вифлеемом', 'В небе над Вифлеемом зажглась звезда Божьего Сына...\r\nСветом, светом небесным озарила в яслях Христа.\r\n\r\nПрипев:\r\nСлава! Слава! за святое Рождество!\r\n\r\nВ яслях, в бедных яслях на соломе лежал Христос.\r\nЦарь царей, Спаситель мира, как человек в наш мир был рожден.\r\n\r\nСлава! Слава, хваленье Божьему Сыну вечно звучит.\r\n\"Слава Эммануилу!\"- каждый пусть скажет в день Рождества.\r\n'),
  (377, 'Нам в чужом скитаться', 'Нам в чужом скитаться суждено краю,\r\nЖизнь проходит, но ответа не найду.\r\nТолько Ты, мой Бог, всё знаешь наперёд,\r\nТа звезда, что ярко светит к нам взойдёт.\r\n\r\nВсё, что прожито и пройдено давно\r\nВозвратиться, и увидишь, как в кино,\r\nЖизнь минувшую, где ты один стоял,\r\nТолько то, что ты нашёл и потерял.\r\n\r\nПрипев:\r\nТолько Ты, мой Бог, Лекарь всех тревог.\r\nЗнаю скоро я к Тебе приду.\r\nЯ хочу к Тебе, вспомни обо мне,\r\nЗабери к Себе и отведи беду.\r\n\r\nДолго странствовал я по большой земле,\r\nОбгоняя время, вновь я шёл к Тебе.\r\nОтзовись и я услышу голос Твой,\r\nГолос истины, что греет неземной.\r\n'),
  (378, 'Иисус Твоя любовь', 'Иисус Твоя любовь безмерна и чиста!\r\nМне не хватает слов благодарить Тебя.\r\n\r\nПрипев:\r\nМеня к Себе принял\r\nТы просто, без упрёков.\r\nТы полюбил меня,\r\nКогда я был далёко.\r\n\r\nНа жизненном пути\r\nТернистою тропою\r\nМне не легко идти,\r\nНо Ты теперь со мною.\r\n\r\nТебе я жизнь свою\r\nОхотно посвящаю!\r\nХвалу Тебе пою!\r\nТебя я прославляю!\r\n'),
  (379, 'Жизни река бежит', 'Жизни река бежит,\r\nВ вечность она ведёт.\r\nКто грех свой победит,\r\nВ вечную жизнь войдёт.\r\nНужно Иисуса искать,\r\nНужно его найти,\r\nВечную благодать\r\nВерою обрести.\r\nНужно Иисуса искать,\r\nНужно его найти,\r\nВечную благодать\r\nВерою обрести.\r\n\r\nВера - нелёгкий путь,\r\nУзок он и тернист.\r\nГлавное - не свернуть,\r\nГлавное - прямо ввысь.\r\nВера - нелёгкий наш путь,\r\nНо нет путей иных,\r\nЧтобы к Христу прильнуть\r\nВ царстве его святых.\r\nВера - нелёгкий наш путь,\r\nНо нет путей иных,\r\nЧтобы к Христу прильнуть\r\nВ царстве его святых.\r\n\r\nВерю, господь Иисус,\r\nВерю, ты мой творец.\r\nВерю, отрёшь слезу,\r\nДашь славы мне венец.\r\nВерю, что день тот придёт,\r\nПримешь меня, Иисус,\r\nВерю, ты мой господь,\r\nВерю, не постыжусь.\r\nВерю, что день тот придёт,\r\nПримешь меня, Иисус,\r\nВерю, ты мой господь,\r\nВерю, не постыжусь.\r\n'),
  (380, 'Кроткий дух и смиренное', 'Кроткий дух и смиренное сердце,\r\nБоже, Ты во мне сотвори!\r\nПред Тобой снова стану младенцем,\r\nТолько правде меня научи.\r\n\r\nПусть Твой Дух мое сердце расширит,\r\nЧтоб я мог моих ближних вмещать,\r\nТвоим светом и солью быть в мире,\r\nЗлую гордость уметь побеждать.\r\n\r\nТем, кто горд Ты противишся, знаю,\r\nА смиренным даешь благодать.\r\nБоже мой, я Тебя умоляю,\r\nДай мне кроткой душой обладать.\r\n\r\nЯ желаю Твоих повелений,\r\nБыть с Тобою в общеньи стремлюсь,\r\nИ в смиреньи склоняю колени,\r\nС чистым сердцем к Тебе обращусь.\r\n'),
  (381, 'Благодарю, Господь', 'Благодарю, Господь, за хлеб насущный.\r\nЗа лунный свет в ночи, за раннюю зарю,\r\nЗа день прошедший и за день грядущий,\r\nТебя, Господь благодарю!\r\n\r\nБлагодарю Тебя за Кровь святую,\r\nЗа то, что Дух Святой во мне теперь живет.\r\nДаешь без меры воду мне живую,\r\nХвала Тебе, хвала, Господь!\r\n\r\nПрипев:\r\nАллилуйя, аллилуйя\r\nСлавлю имя я, что всех имён светлей\r\nПред Тобой склоняюсь я\r\nТы один достоин быть Царём царей\r\n\r\nБлагодарю за радость обновления,\r\nЗа то, что я с Тобой в молитве говорю.\r\nЩит веры Ты мне дал и шлем спасенья.\r\nТебя, Господь, благодарю!\r\n\r\nИ в дивную небесную обитель\r\nМеня Твоя любовь по жизни поведет,\r\nПрославлен, будь навек Ты, мой Спаситель,\r\nХвала Тебе, хвала, Господь!\r\n'),
  (382, 'Пою хвалу Тебе, Отец', 'Пою хвалу Тебе, Отец\r\nИ прославляю Твое Имя! 2р\r\n\r\nТы Любовь во всем,\r\nТы Любовь везде,\r\nТы Любовь  - это Твое Имя!  - 2р\r\n\r\nПою хвалу Тебе, Иисус\r\nИ прославляю Твое Имя! 2р\r\n');
INSERT INTO `SONG` (`ID`, `DESCRIPTION`, `TEXT`) VALUES
  (383, 'Жизнь пройдёт, жизнь пройдёт', 'Жизнь пройдёт, жизнь пройдёт, —\r\nНе заметишь как.\r\nПодойдёт, подойдёт, как коварный враг, страх:\r\nЧто будет потом?\r\nИли тьма, или свет, или пустота…\r\nКак же можно узнать, не узнав Творца?\r\nКак можно узнать?\r\n\r\nНа извечный вопрос дан один ответ,\r\nТретьего не дано:\r\nИли «да», или «нет» – ответ один.\r\nЕсли хочешь быть с Богом – ты будешь с Ним,\r\nПодойди, и Он простит,\r\nОн поймет, исцелит.\r\n\r\nПрипев:\r\nПрекрасней нету любви,\r\nТы это сердцем пойми\r\nИ Бога в жизнь пригласи.\r\nТебя Он хочет понять,\r\nТебя Он сможет простить,\r\nИ жизнь в любви подарить.\r\nНе надо говорить, что Бога нет,\r\nНе надо искать в себе ответ.\r\nИ надеясь на себя, ты никогда не поймёшь,\r\nЧто есть Иисус, который страдал,\r\nЧто жизнь Свою за нас Он отдал,\r\nЧтобы ты никогда не страдал.\r\n\r\nНе давай, не давай в жизнь придти беде,\r\nУповай, уповай на Него везде ночью и днём.\r\nОн с Тобой, Он с Тобой хочет рядом быть,\r\nОн тебе, Он тебе хочет дверь открыть\r\nВ жизнь вечной любви.\r\n\r\nНо проходят года, оставляя след,\r\nИ бывали те дни, когда рядом нет\r\nнаших друзей.\r\nНо пойми, но пойми, что нельзя уйти,\r\nУслыхав, услыхав о Его любви.\r\nДавай, Бога прими!\r\n'),
  (384, 'В день великой скорби на земле', 'В день великой скорби на земле\r\nНе забудет Бог мой обо мне.\r\nЯ знаю уповающего на Иисуса\r\nЖдёт судьба великая вовек.\r\n\r\nПрипев:\r\nМы Господа благодарим\r\nза жертву Его и за кровь.\r\nМы Господа благодарим\r\nза Его к нам любовь.\r\nВедь Он из руин нас поднял,\r\nнам в жизни уверенность дал.\r\nИ нет человека, который не мог бы спастись\r\nчерез Жертву Христа.\r\n\r\nЗнаю никогда нам не понять,\r\nПочему Господь так любит нас.\r\nИ на кресте Он в страшных муках нас простил,\r\nЧто бы жили вместе с Ним вовек.\r\n'),
  (385, 'Не грусти ,не рыдай', 'Не грусти ,не рыдай и не плачь ты о том\r\nЧто невзгоды постигли тебя\r\nЧто весну долго ждать, но придёт она\r\nБудь спокоен взирай на Христа.\r\n\r\nПрипев:\r\n//А весна вновь прекрасная\r\nС ароматом цветов к нам идёт.\r\nИ хотелось бы знать какая она\r\nТа весна что искупленных ждёт.// 2 р.\r\n\r\nТот, кто юные годы свои посвятил\r\nНа служение Богу Творцу\r\nТот и в старости дней будет помнить весну\r\nКогда искренно Богу служил.\r\n\r\nПомоги нам достойными, верными быть,\r\nСвято жизнь проводить на земле.\r\nЧтобы холод греха нас не смог победить.\r\nМы стремимся к той вечной весне.\r\n'),
  (386, 'О, если б ты узнал', 'О, если б ты узнал,\r\nХотя бы в этот день,\r\nЧто в слепоте избрал\r\nТы вместо света - тень!\r\n\r\nПрипев:\r\nО, если б ты узнал,\r\nХотя бы в этот день,\r\nЧто служит к миру твоему,\r\nЧто служит к миру твоему!\r\n\r\nО, если б ты узнал,\r\nЧто ты идешь тропой,\r\nВедущей среди скал\r\nК кончине роковой!\r\n\r\nО, если б ты узнал,\r\nКто, полный дивных слов,\r\nТебя к спасенью звал\r\nИ Чей отверг ты зов!\r\n\r\nО, если б ты узнал,\r\nКто плакал над тобой,\r\nКогда ты грех свершал\r\nПрельщенною душой!\r\n\r\nО, если б ты узнал,\r\nКто возлюбил тебя,\r\nКто за тебя отдал\r\nНа смертный крест Себя!\r\n\r\nО, если б ты узнал:\r\nОн Царь души твоей;\r\nВ Нем Бог любви сиял\r\nДля мира, для людей!\r\n\r\nО, если б ты узнал:\r\nОн ныне пред тобой;\r\nО, разве б ты не пал\r\nК Его стопам с мольбой?!\r\n'),
  (387, 'Вот идёт Царь царей', 'Вот идёт Царь царей,\r\nЗа невестой Своей,\r\nС высоты лучезарных небес.\r\nЧтобы взять от Земли\r\nВсех, кто верит и ждёт,\r\nЗа кого пострадал Он и воскрес.\r\n\r\nПрипев:\r\nИ на белых облаках,\r\nПозабыв печаль и страх,\r\nБудем новую песню мы петь.\r\nТам, где грусти больше нет,\r\nГде сияет вечный свет,\r\nГде на каждый вопрос найдём мы ответ.\r\n\r\nВот идёт Царь царей,\r\nЧтобы стало теплей.\r\nНа Земле, где морозы царят.\r\nВсякий жаждущий пить,\r\nПравду жизни святой,\r\nВремя в ногу идти, друг наш, не стой.\r\n'),
  (388, 'Нам кажется, завтра', 'Нам кажется, завтра мы будем прилежней\r\nИ лучше, полезней, добрей.\r\nСегодня мы грубы, а завтра мы нежны,\r\nИ завтра - мы будем мудрей.\r\nМы завтра проведаем старого друга\r\nИ завтра поможем родным,\r\nИ завтра окажем кому-то услугу,\r\nНе только своим, но чужим.\r\n\r\nПрипев:\r\nА в планах на завтра,\r\nЧто скрыто в тумане,\r\nЗа годом проносится год.\r\nА, что, если завтра возьмёт и обманет?\r\nИ завтра совсем не придёт?\r\n\r\nМы завтра раскаемся в жизни бесплодной\r\nВ последнем предсмертном бреду.\r\nОденем раздетых, накормим голодных,\r\nРазделим чужую беду.\r\nМы завтра поймём, что такое спасенье,\r\nИ завтра пойдём за Христом.\r\nИ завтра преклоним пред Богом колени,\r\nНе ныне, а завтра потом.\r\n'),
  (389, 'Я Есмь Лоза', 'Я Есмь Лоза, а вы Мои ветви,\r\nЯ вас очищаю, чтоб плод вы принесли.\r\nТрудно бывает, но каждый из вас знает,\r\nЖивительные соки текут из Лозы.\r\n\r\nЯ ваш Отец, а вы Мои дети,\r\nЯ о вас забочусь, Я о вас пекусь.\r\nВ жизни бывает, мать дитя бросает,\r\nЯ вас не покину, не оставлю вас.\r\n\r\nЯ Дух Утешитель, Я вас утешаю,\r\nЯ открою истину, Я вас поддержу\r\nНе унывайте , кого Я обличаю,\r\nЯ того наказываю, кого Я люблю.\r\n'),
  (390, 'Еще недолго, да', 'Еще недолго, да, совсем недолго -\r\nИ наш Спаситель нас к Себе возьмет.\r\nХоть и тревог встречаем в жизни много,\r\nНо в небесах покой душа найдет.\r\n\r\nПрипев:\r\nЕще недолго, да, совсем недолго,\r\nИ я узрю Того, Кого люблю.\r\nЕще недолго, да, совсем недолго,\r\nИ в небесах Христу я воспою.\r\n\r\nМой путь тернистый тягостен порою,\r\nИ не растут цветы на нем нигде.\r\nЯ падаю, измученный душою,\r\nНо, вновь поднявшись, я иду к Тебе.\r\n\r\nЯ пилигрим усталый, изнуренный.\r\nО, будь со мной и помогай в борьбе!\r\nКогда Ты рядом - я тогда спокоен\r\nИ в горе, и в страданье, и в нужде.\r\n\r\nЯ радуюсь, что Ты меня так любишь,\r\nЧто среди ночи помогаешь мне!\r\nЯ верю: Ты меня не позабудешь.\r\nО, будь со мной всегда, во всем, везде!\r\n'),
  (391, 'Приходи, Иисус ко мне', 'Приходи, Иисус ко мне,\r\nПриходи и в ночной тишине,\r\nПриходи, когда в сердце грусть,\r\nПриходи, когда я молюсь,\r\nПриходи, когда я молюсь.\r\n\r\nИ когда Ты придёшь ко мне,\r\nСтанет светлым всё на земле.\r\nИ страданий горькую чашу я\r\nС радостью приму за Тебя,\r\nПотому, что со мною Ты.\r\nПотому, что со мною Ты.\r\n\r\nВ этом мире, в суровой тьме\r\nОчень нужен, Иисус, Ты мне.\r\nДай мне силы, Создатель, здесь так прожить,\r\nЧтоб обители вечной я мог достичь,\r\nЧтобы вовеки мне быть с Тобой.\r\nЧтобы вовеки мне быть с Тобой.\r\n'),
  (392, 'Хорошо, когда вместе в общенье', 'Хорошо, когда вместе в общенье\r\nМы сольемся единой хвалой;\r\nХорошо, когда скорби в терпенье\r\nПереносим с надеждой живой.\r\n\r\nПрипев:\r\nХорошо, если сердце свободно,\r\nХорошо, если нету в нем зла!\r\nХорошо, хорошо и спокойно\r\nВ той душе, где всегда тишина.\r\n\r\nХорошо, когда душу и тело\r\nМы вполне для Христа отдаем.\r\nХорошо, когда в битве мы смелы,\r\nКогда действуем так, как поем.\r\n\r\nХорошо, когда бури, невзгоды\r\nНашу душу порой всколыхнут,\r\nКогда чувствуем тяжесть дороги,\r\nКогда труден наш жизненный путь.\r\n\r\nХорошо с Иисусом повсюду,\r\nС Ним легко, ведь Он бремя несет.\r\nХорошо нам открыть сердце Другу.\r\nА наш Друг – Иисус – все поймет.\r\n\r\nХорошо! Как мы счастливы с Богом,\r\nНе покинет Он нас никогда!\r\nПо тернистым житейским дорогам\r\nХорошо с Иисусом всегда!\r\n'),
  (393, 'Сила моя в Тебе, Господь', 'Сила моя в Тебе, Господь,\r\nТы исцеляешь мою плоть,\r\nМой Всемогущий Бог!\r\n\r\nГде бы я ни был, Ты со мной,\r\nСлышу повсюду голос Твой,\r\nМой Вездесущий Бог!\r\n\r\nИисус, Агнец Божий,\r\nДостоин имени Своего!\r\nИисус, Агнец Божий,\r\nДостоин имени Своего!\r\n\r\nВзял мой позор, мой грех на крест,\r\nБлагословлю имя Твоё,\r\nТы для меня всё!\r\n\r\nЯ упал вниз - Ты поднял вверх,\r\nЖаждал я, Ты мне дал воды,\r\nТы для меня - всё.\r\n'),
  (394, 'Если ты хочешь со Мною', 'Если ты хочешь со Мною идти, не отвлекайся на этом пути,\r\nСердцем прими ты Божественный дар, любовь настоящую Я тебе дам...\r\nПойдем, за мной!\r\nВзор устремляй всегда только вперед, кто обернется, наврядли дойдет,\r\nТы откажись от всего своего, навек подарю тебе все что Мое!\r\nПойдем, за Мной!\r\n\r\nПрипев:\r\nЗа Христом пойдем! Все что нужно лишь в Нем обретем,\r\nНичего в этой жизни не может для нас быть важней!\r\nЗа Христом пойдем! Это лучшее что мы найдем,\r\nСлышишь Он тебя к Себе завет! Он ждет!\r\n\r\nЯ покажу тебе столько чулес, все от морей и полей, до небес.\r\nЛюбовью Своею тебя окружу, силой и верою Я снаряжу.\r\nПойдем за Мной!\r\n\r\nВ час испытаний тебя поддержу, если придется чуть- чуть накажу,\r\nЯ ни на миг не оставлю тебя, буду защитой и помощью Я.\r\nПойдем, за Мной!\r\n'),
  (395, 'Не угасай же, посвети', 'Не угасай же, посвети немного,\r\nСветить не просто в беспросветной тьме,\r\nНо ты свети мне вера ради Бога,\r\nНе долгий путь уже остался мне.\r\n\r\nЯ в небесах увижу счастье полдня,\r\nИ в семицветье радостно вольюсь,\r\nВсе это будет завтра, а сегодня,\r\nЯ без Тебя никак не обойдусь.\r\n\r\nУже конец пути, уже преддверье,\r\nУже я слышу вечности шаги,\r\nЯ плохо вижу, посвети мне вера,\r\nВойти в чертог небесный помоги.\r\n'),
  (396, 'Если ты устал в пути', 'Если ты устал в пути и не знаешь, как идти,\r\nИ сомнения в груди изнурили,\r\nНа коленях боль души Иисусу расскажи,\r\nСлышишь, голос у двери: Отвори!\r\n\r\nПрипев:\r\nИди к Христу!\r\nСвоей милости Он не жалел никогда, никому!\r\nИди к Христу!\r\nВсе равно все дороги людские приводят к Нему!\r\n\r\nЕсли жизнь прожить ты смог без обид и без тревог,\r\nНо если беды твой порог обходили,\r\nНа коленях счастье ты с Иисусом раздели,\r\nСлышишь голос у двери: Отвори!\r\n\r\nИ когда к Нему придешь и любовь Его поймешь,\r\nИ в Спасителе найдешь радость жизни,\r\nНа коленях ты тогда прославляй любовь Христа!\r\nПусть поют твоя душа и уста!\r\n'),
  (397, 'Когда Дух Господень', 'Когда Дух Господень наполняет меня,\r\nПою я, как пел Давид.\r\nПою я, пою я, пою я, как пел Давид.\r\n\r\nКогда Дух Господень наполняет меня,\r\nМолюсь я, как Давид.\r\nМолюсь я, молюсь я, молюсь я, как Давид.\r\n\r\nКогда Дух Господень наполняет меня,\r\nТо плачу я, как Давид.\r\nЯ плачу, я плачу, то плачу я, как Давид.\r\n\r\nКогда Дух Господень наполняет меня,\r\nПою я, как пел Давид.\r\nКогда Дух Господень наполняет меня,\r\nМолюсь я, как Давид.\r\nПою я, молюсь я, и плачю я, как Давид.\r\n'),
  (398, 'Одиноко смотрят звёзды', 'Одиноко смотрят звёзды,\r\nОдиноко плачет небо,\r\nОдиноко ветер носит\r\nЛистья за окном.\r\n\r\nВ сердце, раненом обидой,\r\nНавсегда уходит совесть.\r\nГде же радость, где же правда,\r\nГде же та любовь?\r\n\r\nВидел в жизни ты так много,\r\nПерепробовал немало,\r\nЗнаешь, что с волками жить—\r\nТак по-волчьи вой.\r\nТы не веришь даже тем,\r\nКому (ты) доверял так много\r\nИ не веришь, что ты можешь\r\nЖить другой судьбой.\r\n\r\nПрипев:\r\nМожешь жить другой судьбой,\r\nПотому что Бог нас любит.\r\nМожешь, потому что Он Святой,\r\nОн не разлюбит.\r\nТы посмотри на Кровь Христа,\r\nНа Его страданья,\r\nОн не оставит никогда\r\n(Он не оставит тебя).\r\n\r\nВсё проносится так быстро\r\nИ над временем нет власти.\r\nКак бы ты хотел вернуть\r\nПрошлое назад!\r\nИ ты мечешься по жизни,\r\nХочешь вырвать куш свободы;\r\nНо я знаю—бесполезно,\r\nЕсли нет Христа.\r\n'),
  (399, 'В час, когда труба', 'В час, когда труба Господня над землею прозвучит\r\nИ настанет вечно светлая заря,\r\nИмена Он всех спасенных в перекличке повторит,\r\nТам по милости Господней буду я.\r\n\r\nПрипев:\r\nНа небесной перекличке (буду я),\r\nНа небесной перекличке (буду я),\r\nНа небесной перекличке,\r\nТам по милости Господней буду я.\r\n\r\nВ день блаженный воскресенья всех умерших во Христе,\r\nВодворенья их в обители Царя,\r\nВсех святых, омытых Кровию, пролитой на кресте,\r\nСозовет Он, и на зов отвечу я.\r\n\r\nДай трудиться для Тебя, Господь, любовью всем служить\r\nС утра раннего и до заката дня.\r\nА когда велишь работу мне земную завершить,\r\nНа небесный клич тогда предстану я.\r\n'),
  (400, 'Небеса ожидают меня', 'Небеса ожидают меня,\r\nРадость ждёт неземная меня,\r\nБудет встреча с Иисусом Христом,\r\nСвятым Духом, Небесным Отцом.\r\n\r\nПрипев:\r\nИ венец, ну конечно, венец, тот, что мне приготовил Отец!\r\nИ венец, ну конечно, венец, тот, что мне приготовил Отец!\r\nАллилуйя! Аллилуйя! Аллилуйя! Аллилуйя!\r\nАллилуйя! Аллилуйя! Аллилуйя! Аллилуйя!\r\n\r\nГород ждёт неземной красоты.\r\nВсюду ангелы, всюду цветы,\r\nЛица милых родных мне друзей,\r\nИ Спаситель стоит у дверей.\r\n\r\nБудет встреча с Иоанном, Петром,\r\nИ святых без числа целый сонм.\r\nПодойдут и обнимут меня,\r\nРадость будет моя без конца.'),
  (401, 'Пусть угасло небо', 'Пусть угасло небо, пусть померк рассвет, \r\nПусть умолкли люди, пусть молчат в ответ\r\nНа любовь Твою. \r\n\r\nПрипев: \r\nНо шелестом дождя, песнею ручья\r\nДля Тебя, Господь,\r\nИз сердца в небеса льется песнь моя,\r\nПесня без конца. \r\n\r\n2)Да, кругом раздоры, Да и Ты забыт, \r\nЛюди прочь уходят в путь, \r\nчто тьмой покрыт, Где Ты забыт. \r\n\r\n3)Ты придёшь и навсегда Умолкнет грех.\r\nТам, где темнота была, \r\nСияет свет, вечный свет.'),
  (402, 'Великий Бог! Когда на мир', 'Великий Бог! Когда на мир смотрю я,\r\nНа все, что Ты создал рукой Творца,\r\nНа всех существ, кого, Свой свет даруя,\r\nПитаешь Ты любовию Отца, –\r\n\r\nA D A\r\nТогда поет мой дух, Господь к Тебе:\r\nE A E\r\nКак Ты велик, как Ты велик!\r\n\r\nКогда весной природа расцветает,\r\nИ слышно в дальней роще соловья,\r\nИ аромат долины грудь вдыхает,\r\nИ слух ласкает звонкий шум ручья, –\r\n\r\nКогда смотрю я к небу, к звездам млечным,\r\nГде дивно светлые миры текут,\r\nГде солнце и луна в эфире вечном,\r\nКак в океане корабли, плывут, –\r\n\r\nКогда Господь меня Сам призывает,\r\nИ светит луч сияния Его,\r\nТогда мой дух в смирении смолкает,\r\nПризнав величье Бога Своего'),
  (404, 'Когда смолкнет все, в тиши', 'Когда смолкнет все, в тиши утонет звук.\r\nA Я к Тебе приду \r\nПринести хочу из сердца моего \r\nA Нежную хвалу\r\n\r\nТебе я песню спою, \r\nНо не просто, чтоб спеть,\r\nВедь Ты не этого ждешь,\r\nТы видишь мысли мои,\r\nНичего мне не скрыть,\r\nA Ты смотришь в сердце мое \r\n\r\nПрипев:\r\nЯ возвращаюсь сердцем к поклонению\r\nК поклонению Тебе, только Тебе, Иисус.\r\nПрости, что я допустил сомненье,\r\nНо теперь для Тебя, все для Тебя, Иисус\r\n\r\n[2.] Драгоценный Царь не выразить того,\r\nЧего достоин Ты. Беден я и нищ и все, \r\nчто есть - Твое, Даже просто вздох'),
  (405, 'Ты Царь в сердце моем', 'Ты Царь в сердце моем Ты скала, \r\nкуда бегу Источник, где я пью \r\nО, Ты песнь моя! Ты Царь в сердце моем \r\nТень, где скроюсь я \r\nИскупление за меня, \r\nО, Ты песнь моя! \r\n\r\nПРИПЕВ:\r\nТы так благ, благ о-о \r\nТы так благ, благ о-о \r\n\r\nТы Царь в сердце моем\r\nБудешь ветром в парусах, Якорем в волнах\r\nО, Ты песнь моя! \r\nТы Царь в сердце моем Будешь пламенем внутри, \r\nЭхом дней моих \r\nО, Ты песнь моя! \r\n\r\nБРИДЖ: \r\nИ Ты не подведешь, \r\nТы не подведешь меня'),
  (406, 'Бесконечный Бог!', 'Бесконечный Бог! \r\nБезграничный Царь! \r\nВсепрощающий! \r\nВечнолюбящий! \r\n\r\nМой Господь, я к Тебе стремлюсь \r\nТы открыл мне Любовь Свою \r\nНаполняешь Собою Ты \r\nМои мысли и жизнь \r\nЯрче солнца сияешь Ты \r\nНас, спасая из темноты \r\nС восхищением в этот день \r\nБуду петь о Тебе: \r\n\r\nБесконечный Бог! \r\nБезграничный Царь! \r\nВсепрощающий! \r\nВечнолюбящий! \r\n\r\nМой Господь, благо петь Тебе: \r\nТы велик, свят и праведен, \r\nСозерцать вечно я могу \r\nВсю Твою красоту \r\nПред величием пред Твоим, \r\nИз живущих, кто устоит? \r\nС хором ангелов в этот день \r\nБуду петь о Тебе: \r\n\r\nБесконечный Бог! \r\nБезграничный Царь! \r\nВсепрощающий! \r\nВечнолюбящий! \r\n\r\nСлавный! Великий! Прекрасный! \r\nНаполняющий все во всем! \r\n\r\nБесконечный Бог! \r\nБезграничный Царь! \r\nВсепрощающий! \r\nВечнолюбящий!'),
  (407, 'В глубины вод меня призвал Ты', 'В глубины вод меня призвал Ты\r\nМне не пройти их без Тебя\r\nНо вера крепнет в океанах\r\nТам явишь мне, Господь, Себя\r\n\r\nПрипев:\r\nЯ имя, Бог, Твоё зову\r\nИ взгляд я с волн на Тебя смещу\r\nДуше моей Ты дашь покой во время бурь\r\nВедь я в Тебе, а Ты во мне\r\n\r\n2 куплет:\r\nВ пучинах больше благодати\r\nРукой Своей меня спасешь\r\nГде страх объял и нет опоры\r\nТы не подводил и не подведешь\r\n\r\nБридж:\r\nБезгранично доверяюсь Духу Бога\r\nПусть ведёт меня по водам туда, куда призвал Он\r\nИ в глубинах тех, что не преодолеть мне\r\nСтанет вера моя крепче когда рядом мой Спаситель\r\n\r\nБезгранично доверяюсь Духу Бога\r\nПусть ведёт меня по водам туда, куда призвал Он\r\nИ в глубинах тех, что не преодолеть мне\r\nСтанет вера моя крепче когда рядом мой Спаситель\r\n\r\nБезгранично доверяюсь Духу Бога\r\nПусть ведёт меня по водам туда, куда призвал Он\r\nИ в глубинах тех, что не преодолеть мне\r\nСтанет вера моя крепче когда рядом мой Спаситель'),
  (408, 'Верую, Господи, истинно верую', '1 куплет:\r\n Am                                                             Dm                        E\r\nВерую, Господи, истинно верую в Слово святое Твое.\r\n  Am                                                           Dm                            G\r\nВремя отмерено каждому верному определенье свое.\r\n    C                                                                    A\r\nВсе, что назначено, будет исполнено сильной рукою Твоей.\r\nDm                                                              E\r\nБожье провиденье не омрачайте, не убегайте скорбей.\r\n\r\nПрипев:\r\n Am            Dm                         G                     C\r\nНебо, небо правду знает, небо верных принимает:\r\n Am                   Dm                  E  \r\nСтефан, Павел, Магдалина, Иоанн, Филипп, Мария,\r\n Am                    Dm                    G               C\r\nТит, Лука, Андрей, Варнава - Имена, и с ними слава.\r\nAm                   Dm                  E   \r\nО, дай Бог свое услышать имя между ними свыше.\r\n\r\n2 куплет:\r\nВсе, что построили, будет проверено посланным с неба огнем.\r\nБог так устроил все, будьте уверены, может быть, после поймем,\r\nГде было золото, сено и дерево, чистое серебро.\r\nКаждый увидит, насколько он верен был в том, что от Неба дано.\r\n\r\n3 куплет:\r\nБоже, помилуй нас, из рук не выпусти и для Себя сохрани.\r\nГде бы не были мы, ехали, плыли бы, милости не отними.\r\nПеред Тобою никто не осмелится чтобы неправду сказать.\r\nЧистое небо святыми заселится, грешникам там не бывать.'),
  (409, 'Одно тело и один дух', 'Одно тело и один дух \r\nВ братолюбии пребудем, друг, \r\nСохраним единство мысли между собой. – 2р. \r\n\r\nПрипев: \r\nОдин Господь, одна вера, \r\nОдно крещение для всех. – 2р.\r\n\r\nОдин Бог и Господь Один, \r\nПребывает на века Един, \r\nПодаёт пример единства для нас с Тобой. – 2р.');
